      }

      if (conditions.length > 0) {
        query = query.where(and(...conditions));
      }
    }

    const leadsData = await query.orderBy(desc(leads.createdAt));

    return leadsData.map(l => ({
      ...l.leads,
      assignedUser: l.users
    }));
  }

