  // File Upload
  attachmentPath: text("attachment_path"), // Path to uploaded document
  attachmentName: text("attachment_name"), // Original filename

  // Additional Info
  notes: text("notes"),
  reviewedBy: uuid("reviewed_by").references(() => users.id),
