                    <Icon className="h-5 w-5" />
                    <div>
                      <div className="font-light">{item.label}</div>
                      <div className={`text-xs ${
                        isActive ? 'text-primary-foreground/80' : 'text-muted-foreground'
                      }`}>
                        {item.description}
