                  <p className="text-muted-foreground mb-4">
                    Get started by adding your first bank account.
                  </p>
                  <Button onClick={() => setIsCreateAccountOpen(true)} data-testid="button-add-first-account">
                    <Plus className="h-4 w-4 mr-2" />
                    Add Bank Account
                  </Button>
