import type { Express, Request, Response, NextFunction } from "express";
import { z } from "zod";
import { storage } from "./storage";
import {
  insertLeadSchema, updateLeadSchema, insertFieldVisitSchema, insertMarketingTaskSchema, insertMarketingAttendanceSchema,
  updateLeadStatusSchema, updateFieldVisitStatusSchema, updateMarketingTaskStatusSchema,
  fieldVisitCheckInSchema, fieldVisitCheckOutSchema, convertLeadSchema,
  leadFilterSchema, fieldVisitFilterSchema, marketingTaskFilterSchema, attendancePhotoUploadSchema
} from "@shared/schema";
import { ObjectStorageService } from "./objectStorage";

// Object storage service for photo uploads
const objectStorage = new ObjectStorageService();

// Authentication and Authorization types
interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    role: string;
    username: string;
  };
}

// Marketing Route Handlers - Extracted from routes.ts

// ==========================================
// LEADS HANDLERS
// ==========================================

export const getLeads = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    const filters = leadFilterSchema.parse(req.query);

    // Convert query parameters to filter object
    const filterObject: any = {};

    if (filters.status && filters.status !== 'all') {
      filterObject.status = filters.status;
    }

    if (filters.source && filters.source !== 'all') {
      filterObject.source = filters.source;
    }

    if (filters.priority && filters.priority !== 'all') {
      filterObject.priority = filters.priority;
    }

    if (filters.assignedTo && filters.assignedTo !== 'all') {
      filterObject.assignedTo = filters.assignedTo;
    }

    if (filters.search && filters.search.trim().length > 0) {
      filterObject.search = filters.search.trim();
    }

    // SECURITY: Apply user-based scoping based on role
    if (req.user!.role === 'admin' || req.user!.role === 'manager') {
      // Admins and managers can see all leads
    } else {
      // Regular employees can only see leads they created or are assigned to
      filterObject.userScope = {
        userId: req.user!.id,
        showOnlyUserLeads: true
      };
    }

    // Apply filters and get leads
    const leads = await storage.getLeads(Object.keys(filterObject).length > 0 ? filterObject : undefined);

    res.json(leads);
  } catch (error) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ error: "Invalid filter parameters", details: error.errors });
      return;
    }
    res.status(500).json({ error: "Failed to fetch leads" });
  }
};

export const getLead = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(req.params.id)) {
      res.status(400).json({ error: "Invalid lead ID format" });
      return;
    }

    const lead = await storage.getLead(req.params.id);
    if (!lead) {
      res.status(404).json({ error: "Lead not found" });
      return;
    }

    // SECURITY: Check if user has permission to view this lead
    if (req.user!.role !== 'admin' && req.user!.role !== 'manager') {
      if (lead.createdBy !== req.user!.id && lead.assignedTo !== req.user!.id) {
        res.status(403).json({ error: "You do not have permission to view this lead" });
        return;
      }
    }

    res.json(lead);
  } catch (error) {
    res.status(500).json({ error: "Failed to fetch lead" });
  }
};

export const createLead = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    const leadData = insertLeadSchema.parse(req.body);

    // Set creator and assignment fields
    leadData.createdBy = req.user!.id;
    leadData.assignedBy = req.user!.id;
    // If no assignedTo is specified, assign to the creator
    if (!leadData.assignedTo) {
      leadData.assignedTo = req.user!.id;
    }

    const lead = await storage.createLead(leadData);
    await storage.createActivity({
      userId: req.user!.id,
      action: "CREATE_LEAD",
      entityType: "lead",
      entityId: lead.id,
      details: `Created lead: ${lead.firstName} ${lead.lastName}`,
    });
    res.status(201).json(lead);
  } catch (error) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ error: "Invalid lead data", details: error.errors });
      return;
    }
    res.status(500).json({ error: "Failed to create lead" });
  }
};

export const updateLead = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(req.params.id)) {
      res.status(400).json({ error: "Invalid lead ID format" });
      return;
    }

    const existingLead = await storage.getLead(req.params.id);
    if (!existingLead) {
      res.status(404).json({ error: "Lead not found" });
      return;
    }

    // SECURITY: Check if user has permission to update this lead
    if (req.user!.role !== 'admin' && req.user!.role !== 'manager') {
      if (existingLead.createdBy !== req.user!.id && existingLead.assignedTo !== req.user!.id) {
        res.status(403).json({ error: "You do not have permission to update this lead" });
        return;
      }
    }

    // SECURITY: Use secure update schema that omits ownership fields
    const leadData = updateLeadSchema.parse(req.body);
    const lead = await storage.updateLead(req.params.id, leadData);
    await storage.createActivity({
      userId: req.user!.id,
      action: "UPDATE_LEAD",
      entityType: "lead",
      entityId: lead.id,
      details: `Updated lead: ${lead.firstName} ${lead.lastName}`,
    });
    res.json(lead);
  } catch (error) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ error: "Invalid lead data", details: error.errors });
      return;
    }
    res.status(500).json({ error: "Failed to update lead" });
  }
};

export const deleteLead = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(req.params.id)) {
      res.status(400).json({ error: "Invalid lead ID format" });
      return;
    }

    const existingLead = await storage.getLead(req.params.id);
    if (!existingLead) {
      res.status(404).json({ error: "Lead not found" });
      return;
    }

    // SECURITY: Check if user has permission to delete this lead
    if (req.user!.role !== 'admin' && req.user!.role !== 'manager') {
      if (existingLead.createdBy !== req.user!.id && existingLead.assignedTo !== req.user!.id) {
        res.status(403).json({ error: "You do not have permission to delete this lead" });
        return;
      }
    }

    await storage.deleteLead(req.params.id);
    await storage.createActivity({
      userId: req.user!.id,
      action: "DELETE_LEAD",
      entityType: "lead",
      entityId: req.params.id,
      details: `Deleted lead: ${existingLead.firstName} ${existingLead.lastName}`,
    });
    res.status(204).send();
  } catch (error) {
    res.status(500).json({ error: "Failed to delete lead" });
  }
};

export const updateLeadStatus = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(req.params.id)) {
      res.status(400).json({ error: "Invalid lead ID format" });
      return;
    }

    const existingLead = await storage.getLead(req.params.id);
    if (!existingLead) {
      res.status(404).json({ error: "Lead not found" });
      return;
    }

    // SECURITY: Check if user has permission to update this lead status
    if (req.user!.role !== 'admin' && req.user!.role !== 'manager') {
      if (existingLead.createdBy !== req.user!.id && existingLead.assignedTo !== req.user!.id) {
        res.status(403).json({ error: "You do not have permission to update this lead status" });
        return;
      }
    }

    const { status, notes } = updateLeadStatusSchema.parse(req.body);

    const lead = await storage.updateLead(req.params.id, { status });
    await storage.createActivity({
      userId: req.user!.id,
      action: "UPDATE_LEAD_STATUS",
      entityType: "lead",
      entityId: lead.id,
      details: `Updated lead status to '${status}': ${lead.firstName} ${lead.lastName}${notes ? ` - ${notes}` : ''}`,
    });
    res.json(lead);
  } catch (error) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ error: "Invalid status update data", details: error.errors });
      return;
    }
    res.status(500).json({ error: "Failed to update lead status" });
  }
};

export const convertLead = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    if (!/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(req.params.id)) {
      res.status(400).json({ error: "Invalid lead ID format" });
      return;
    }

    const existingLead = await storage.getLead(req.params.id);
    if (!existingLead) {
      res.status(404).json({ error: "Lead not found" });
      return;
    }

    // SECURITY: Check if user has permission to convert this lead
    if (req.user!.role !== 'admin' && req.user!.role !== 'manager') {
      if (existingLead.createdBy !== req.user!.id && existingLead.assignedTo !== req.user!.id) {
        res.status(403).json({ error: "You do not have permission to convert this lead" });
        return;
      }
    }

    const conversionData = convertLeadSchema.parse(req.body);

    const customer = await storage.convertLeadToCustomer(req.params.id);
    await storage.createActivity({
      userId: req.user!.id,
      action: "CONVERT_LEAD",
      entityType: "customer",
      entityId: customer.id,
      details: `Converted lead to customer: ${customer.name}`,
    });
    res.json({ customer, message: "Lead successfully converted to customer" });
  } catch (error) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ error: "Invalid conversion data", details: error.errors });
      return;
    }
    res.status(500).json({ error: "Failed to convert lead to customer" });
  }
};

export const getLeadMetrics = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    // SECURITY: Apply user-based scoping for metrics
    const metricsOptions: any = {};

    if (req.user!.role === 'admin' || req.user!.role === 'manager') {
      // Admins and managers can see all lead metrics
    } else {
      // Regular employees can only see metrics for their own leads
      metricsOptions.userScope = {
        userId: req.user!.id,
        showOnlyUserLeads: true
      };
    }

    const metrics = await storage.getLeadsConversionMetrics(Object.keys(metricsOptions).length > 0 ? metricsOptions : undefined);
    res.json(metrics);
  } catch (error) {
    res.status(500).json({ error: "Failed to fetch leads metrics" });
  }
};

export const searchLeads = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    const query = req.query.q as string;
    if (!query || query.trim().length === 0) {
      res.status(400).json({ error: "Search query is required and cannot be empty" });
      return;
    }
    if (query.length < 2) {
      res.status(400).json({ error: "Search query must be at least 2 characters long" });
      return;
    }

    // SECURITY: Apply user-based scoping for search results
    const searchOptions: any = { query: query.trim() };

    if (req.user!.role === 'admin' || req.user!.role === 'manager') {
      // Admins and managers can search all leads
    } else {
      // Regular employees can only search leads they created or are assigned to
      searchOptions.userScope = {
        userId: req.user!.id,
        showOnlyUserLeads: true
      };
    }

    const leads = await storage.searchLeads(searchOptions);
    res.json(leads);
  } catch (error) {
    res.status(500).json({ error: "Failed to search leads" });
  }
};

// ==========================================
// FIELD VISITS HANDLERS
// ==========================================

export const getFieldVisits = async (req: AuthenticatedRequest, res: Response): Promise<void> => {
  try {
    const filters = fieldVisitFilterSchema.parse(req.query);

    // Convert query parameters to filter object
    const filterObject: any = {};

    if (filters.status && filters.status !== 'all') {
      filterObject.status = filters.status;
    }

    if (filters.assignedTo && filters.assignedTo !== 'all') {
      filterObject.assignedTo = filters.assignedTo;
    }

    if (filters.leadId) {
      filterObject.leadId = filters.leadId;
    }

    if (filters.startDate && filters.endDate) {
      filterObject.startDate = filters.startDate;
      filterObject.endDate = filters.endDate;
    }

    // SECURITY: Apply user-based scoping based on role
    if (req.user!.role === 'admin' || req.user!.role === 'manager') {
      // Admins and managers can see all field visits
    } else {
      // Regular employees can only see field visits they created or are assigned to
      filterObject.userScope = {
        userId: req.user!.id,
        showOnlyUserVisits: true
      };
    }

    // Apply filters and get field visits
    const visits = await storage.getFieldVisits(Object.keys(filterObject).length > 0 ? filterObject : undefined);

    res.json(visits);
  } catch (error) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ error: "Invalid filter parameters", details: error.errors });
      return;
    }
    res.status(500).json({ error: "Failed to fetch field visits" });
  }
