    const checkOutData = fieldVisitCheckOutSchema.parse(req.body);

    if (existingVisit.actualEndTime) {
      res.status(400).json({ error: "Already checked out of this field visit" });
      return;
    }

    if (!existingVisit.actualStartTime) {
