} from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

// Import marketing pages
import MarketingDashboard from "@/pages/MarketingDashboard";
import Leads from "./Leads";
import FieldVisits from "./FieldVisits";
import MarketingTasks from "./MarketingTasks";
import Reports from "./Reports";
import MarketingAttendance from "./MarketingAttendance";

const sidebarItems = [
  {
    id: 'dashboard',
    label: 'Dashboard',
    icon: BarChart3,
    path: '/marketing',
    description: 'Overview and marketing metrics'
  },
  {
    id: 'leads',
    label: 'Leads',
    icon: Users,
    path: '/marketing/leads',
    description: 'Lead management and status workflow'
  },
  {
    id: 'field-visits',
    label: 'Field Visits',
    icon: MapPin,
    path: '/api/field-visits',
    description: 'Scheduling and geo-tracking'
  },
  {
    id: 'tasks',
    label: 'Marketing Tasks',
    icon: ClipboardList,
    path: '/marketing/tasks',
    description: 'Employee task assignment'
  },
  {
    id: 'reports',
    label: 'Reports',
    icon: FileText,
    path: '/marketing/reports',
    description: 'Conversion rates and analytics'
  },
  {
    id: 'attendance',
    label: 'Marketing Attendance',
    icon: Clock,
    path: '/marketing/attendance',
    description: 'Team attendance and leave tracking'
  }
];

export default function MarketingLayout() {
  const [location] = useLocation();

  const getActiveSidebarItem = () => {
    if (location === '/marketing') return 'dashboard';
    if (location.includes('/leads')) return 'leads';
    if (location.includes('/field-visits')) return 'field-visits';
    if (location.includes('/tasks')) return 'tasks';
    if (location.includes('/reports')) return 'reports';
    if (location.includes('/attendance')) return 'attendance';
    return 'dashboard';
  };

  return (
    <div className="flex min-h-screen bg-background">
      {/* Sidebar */}
      <div className="w-80 bg-card border-r border-border p-6">
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-foreground mb-2">Marketing Dashboard</h1>
          <p className="text-sm text-muted-foreground">
            Comprehensive marketing management system
          </p>
        </div>

        <div className="space-y-2">
          {sidebarItems.map((item) => {
            const Icon = item.icon;
            const isActive = getActiveSidebarItem() === item.id;

            return (
              <Link
                key={item.id}
                href={item.path}
                data-testid={`sidebar-${item.id}`}
              >
                <div
                  className={`p-4 rounded-[0.3rem] transition-colors cursor-pointer ${
                    isActive
                      ? 'bg-primary text-primary-foreground'
                      : 'hover:bg-muted/50'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    <Icon className="h-5 w-5" />
                    <div>
                      <div className="font-light">{item.label}</div>
                      <div className={`text-xs ${
                        isActive ? 'text-primary-foreground/80' : 'text-muted-foreground'
                      }`}>
                        {item.description}
                      </div>
                    </div>
                  </div>
                </div>
              </Link>
            );
          })}
        </div>

        {/* Quick Stats Card */}
        <div className="mt-8">
          <Card className="bg-muted/30">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-light flex items-center space-x-2">
                <Target className="h-4 w-4" />
                <span>Marketing Overview</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-xs text-muted-foreground">Active Leads</span>
                <div className="flex items-center space-x-1">
                  <Users className="h-3 w-3 text-blue-500" />
                  <span className="text-xs font-light">127</span>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-xs text-muted-foreground">Field Visits Today</span>
                <div className="flex items-center space-x-1">
                  <Calendar className="h-3 w-3 text-green-500" />
                  <span className="text-xs font-light">8</span>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-xs text-muted-foreground">Conversion Rate</span>
                <div className="flex items-center space-x-1">
                  <TrendingUp className="h-3 w-3 text-emerald-500" />
                  <span className="text-xs font-light">24%</span>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-xs text-muted-foreground">Pending Tasks</span>
                <span className="text-xs font-light">12</span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1">
        <Switch>
          <Route path="/marketing">
            <MarketingDashboard />
          </Route>
          <Route path="/marketing/leads">
            <Leads />
          </Route>
          <Route path="/api/field-visits">
            <FieldVisits />
          </Route>
          <Route path="/marketing/tasks">
            <MarketingTasks />
          </Route>
          <Route path="/marketing/reports">
            <Reports />
          </Route>
          <Route path="/marketing/attendance">
            <MarketingAttendance />
          </Route>
          <Route>
            <div className="p-8">
              <Card>
                <CardContent className="pt-6">
                  <h2 className="text-lg font-semibold mb-2">Page Not Found</h2>
                  <p className="text-muted-foreground">
                    The requested marketing page could not be found.
                  </p>
                </CardContent>
              </Card>
            </div>
          </Route>
        </Switch>
      </div>
    </div>
  );
}

client/src/components/marketing/LeadForm.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { X, Calendar, User as UserIcon, MapPin, Phone, Mail, Building, FileText, Tag, DollarSign } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar as CalendarComponent } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";

import type { LeadFormData, LeadSource, LeadPriority, User } from "@/types";

const leadFormSchema = z.object({
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  companyName: z.string().optional(),
  email: z.string().email("Invalid email format").optional().or(z.literal("")),
  phone: z.string().min(10, "Phone number must be at least 10 digits").optional().or(z.literal("")),
  alternatePhone: z.string().optional(),
  address: z.string().optional(),
  city: z.string().optional(),
  state: z.string().optional(),
  zipCode: z.string().optional(),
  country: z.string().min(1, "Country is required").default("India"),
  source: z.enum(['website', 'referral', 'advertisement', 'social_media', 'trade_show', 'cold_call', 'email_campaign', 'other']),
  sourceDetails: z.string().optional(),
  referredBy: z.string().optional(),
  requirementDescription: z.string().optional(),
  estimatedBudget: z.string().optional(),
  budgetRange: z.string().optional(),
  priority: z.enum(['low', 'medium', 'high', 'urgent']).default('medium'),
  assignedTo: z.string().optional(),
  followUpDate: z.string().optional(),
  expectedClosingDate: z.string().optional(),
  notes: z.string().optional(),
  tags: z.array(z.string()).optional()
});

interface LeadFormProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  leadId?: string;
  defaultValues?: Partial<LeadFormData>;
}

export default function LeadForm({ open, onOpenChange, leadId, defaultValues }: LeadFormProps) {
  const [activeTab, setActiveTab] = useState("basic");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch users for assignment dropdown
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['/api/users'],
    enabled: open
  });

  // Fetch existing lead data if editing
  const { data: existingLead } = useQuery({
    queryKey: ['/api/marketing/leads ', leadId],
    enabled: !!leadId && open
  });

  const form = useForm<LeadFormData>({
    resolver: zodResolver(leadFormSchema),
    defaultValues: {
      country: "India",
      priority: "medium",
      source: "other",
      ...defaultValues
    }
  });

  useEffect(() => {
    if (existingLead) {
      const leadData = existingLead as any; // Type assertion for nested properties
      form.reset({
        ...existingLead,
        followUpDate: leadData.followUpDate ? new Date(leadData.followUpDate).toISOString().split('T')[0] : "",
        expectedClosingDate: leadData.expectedClosingDate ? new Date(leadData.expectedClosingDate).toISOString().split('T')[0] : "",
        estimatedBudget: leadData.estimatedBudget?.toString() || ""
      });
    }
  }, [existingLead, form]);

  const createMutation = useMutation({
    mutationFn: (data: LeadFormData) => apiRequest('/api/marketing/leads ', { method: 'POST', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing/leads '] });
      toast({ title: "Lead created successfully!" });
      onOpenChange(false);
      form.reset();
    },
    onError: (error: any) => {
      toast({ 
        title: "Error creating lead", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const updateMutation = useMutation({
    mutationFn: (data: LeadFormData) => apiRequest(`/api/marketing/leads /${leadId}`, { method: 'PUT', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing/leads '] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing/leads ', leadId] });
      toast({ title: "Lead updated successfully!" });
      onOpenChange(false);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating lead", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const onSubmit = (data: LeadFormData) => {
    // Prepare submit data with proper types
    const submitData = {
      ...data,
      estimatedBudget: data.estimatedBudget || undefined,
      tags: data.tags || []
    };

    if (leadId) {
      updateMutation.mutate(submitData);
    } else {
      createMutation.mutate(submitData);
    }
  };

  const isPending = createMutation.isPending || updateMutation.isPending;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <UserIcon className="h-5 w-5" />
            <span>{leadId ? 'Edit Lead' : 'Add New Lead'}</span>
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <Tabs value={activeTab} onValueChange={setActiveTab}>
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="basic" data-testid="tab-basic">Basic Info</TabsTrigger>
                <TabsTrigger value="contact" data-testid="tab-contact">Contact</TabsTrigger>
                <TabsTrigger value="requirements" data-testid="tab-requirements">Requirements</TabsTrigger>
                <TabsTrigger value="management" data-testid="tab-management">Management</TabsTrigger>
              </TabsList>

              <TabsContent value="basic" className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
