import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { CalendarIcon, MapPin, User as UserIcon, Clock, Target, FileText, AlertCircle } from "lucide-react";
import { format } from "date-fns";

import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";

import type { FieldVisit, InsertFieldVisit, User, Lead } from "@shared/schema";

const visitFormSchema = z.object({
  leadId: z.string().min(1, "Lead is required"),
  plannedDate: z.date({ required_error: "Visit date is required" }),
  plannedStartTime: z.string().optional(),
  plannedEndTime: z.string().optional(),
  assignedTo: z.string().min(1, "Assigned employee is required"),
  visitAddress: z.string().min(1, "Visit address is required"),
  visitCity: z.string().optional(),
  visitState: z.string().optional(),
  latitude: z.number().optional(),
  longitude: z.number().optional(),
  purpose: z.enum(['initial_meeting', 'demo', 'follow_up', 'quotation_discussion', 'negotiation', 'closing', 'support', 'other']),
  preVisitNotes: z.string().optional(),
  travelExpense: z.string().optional(),
});

type VisitFormData = z.infer<typeof visitFormSchema>;

interface VisitWithDetails extends FieldVisit {
  lead?: {
    id: string;
    firstName: string;
    lastName: string;
    companyName?: string;
  };
}

interface VisitFormProps {
  visit?: VisitWithDetails | null;
  leads: Lead[];
  users: User[];
  onSubmit: (data: InsertFieldVisit) => void;
  onCancel: () => void;
  isLoading?: boolean;
}

export default function VisitForm({ visit, leads, users, onSubmit, onCancel, isLoading = false }: VisitFormProps) {
  const [activeTab, setActiveTab] = useState("basic");
  const [selectedLead, setSelectedLead] = useState<Lead | null>(null);

  const form = useForm<VisitFormData>({
    resolver: zodResolver(visitFormSchema),
    defaultValues: {
      purpose: "initial_meeting",
      visitCity: "",
      visitState: "",
      preVisitNotes: "",
      travelExpense: "",
    }
  });

  // Update form when visit data changes
  useEffect(() => {
    if (visit) {
      const visitData = {
        leadId: visit.leadId,
        plannedDate: visit.plannedDate ? new Date(visit.plannedDate) : new Date(),
        plannedStartTime: visit.plannedStartTime ? 
          format(new Date(visit.plannedStartTime), 'HH:mm') : '',
        plannedEndTime: visit.plannedEndTime ? 
          format(new Date(visit.plannedEndTime), 'HH:mm') : '',
        assignedTo: visit.assignedTo,
        visitAddress: visit.visitAddress,
        visitCity: visit.visitCity || '',
        visitState: visit.visitState || '',
        latitude: visit.latitude ? parseFloat(visit.latitude) : undefined,
        longitude: visit.longitude ? parseFloat(visit.longitude) : undefined,
        purpose: visit.purpose,
        preVisitNotes: visit.preVisitNotes || '',
        travelExpense: visit.travelExpense || '',
      };

      form.reset(visitData);

      // Find and set the selected lead
      const lead = leads.find(l => l.id === visit.leadId);
      if (lead) {
        setSelectedLead(lead);
      }
    } else {
      form.reset();
      setSelectedLead(null);
    }
  }, [visit, leads, form]);

  // Handle lead selection
  const handleLeadChange = (leadId: string) => {
    const lead = leads.find(l => l.id === leadId);
    if (lead) {
      setSelectedLead(lead);

      // Auto-fill address if available
      if (lead.address) {
        form.setValue('visitAddress', lead.address);
        form.setValue('visitCity', lead.city || '');
        form.setValue('visitState', lead.state || '');
      }
    }
  };

  // Get user's current location for GPS coordinates
  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          form.setValue('latitude', position.coords.latitude);
          form.setValue('longitude', position.coords.longitude);
        },
        (error) => {
          console.error('Error getting location:', error);
        },
        { enableHighAccuracy: true, timeout: 10000 }
      );
    }
  };

  // Handle form submission
  const handleSubmit = (data: VisitFormData) => {
    const submitData: InsertFieldVisit = {
      leadId: data.leadId,
      plannedDate: data.plannedDate,
      plannedStartTime: data.plannedStartTime ? 
        new Date(`${format(data.plannedDate, 'yyyy-MM-dd')}T${data.plannedStartTime}`) : 
        undefined,
      plannedEndTime: data.plannedEndTime ? 
        new Date(`${format(data.plannedDate, 'yyyy-MM-dd')}T${data.plannedEndTime}`) : 
        undefined,
      assignedTo: data.assignedTo,
      visitAddress: data.visitAddress,
      visitCity: data.visitCity,
      visitState: data.visitState,
      latitude: data.latitude?.toString(),
      longitude: data.longitude?.toString(),
      purpose: data.purpose,
      preVisitNotes: data.preVisitNotes,
      travelExpense: data.travelExpense,
      status: 'scheduled',
    };

    onSubmit(submitData);
  };

  // Generate visit number (for display purposes)
  const generateVisitNumber = () => {
    const date = new Date();
    const year = date.getFullYear().toString().slice(-2);
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    return `FV${year}${month}${day}${random}`;
  };

  // Filter active users for assignment
  const activeUsers = users.filter(user => user.isActive);

  // Filter leads that are not converted or dropped
  const availableLeads = leads.filter(lead => 
    ['new', 'contacted', 'in_progress'].includes(lead.status || 'new')
  );

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
        <Tabs value={activeTab} onValueChange={setActiveTab}>
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="basic" data-testid="tab-basic-info">Basic Info</TabsTrigger>
            <TabsTrigger value="location" data-testid="tab-location-info">Location</TabsTrigger>
            <TabsTrigger value="details" data-testid="tab-visit-details">Details</TabsTrigger>
          </TabsList>

          <TabsContent value="basic" className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="leadId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center space-x-2">
                      <UserIcon className="h-4 w-4" />
                      <span>Customer/Lead *</span>
                    </FormLabel>
                    <Select 
                      onValueChange={(value) => {
                        field.onChange(value);
                        handleLeadChange(value);
                      }} 
                      defaultValue={field.value}
                    >
                      <FormControl>
                        <SelectTrigger data-testid="select-lead">
                          <SelectValue placeholder="Select a lead" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {availableLeads.map(lead => (
                          <SelectItem key={lead.id} value={lead.id}>
                            {lead.firstName} {lead.lastName}
                            {lead.companyName && ` - ${lead.companyName}`}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="assignedTo"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center space-x-2">
                      <UserIcon className="h-4 w-4" />
                      <span>Assigned To *</span>
                    </FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-assigned-to">
                          <SelectValue placeholder="Select an employee" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {activeUsers.map(user => (
                          <SelectItem key={user.id} value={user.id}>
                            {user.firstName} {user.lastName} - {user.department || 'Marketing'}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <FormField
                control={form.control}
                name="plannedDate"
                render={({ field }) => (
                  <FormItem className="flex flex-col">
                    <FormLabel className="flex items-center space-x-2">
                      <CalendarIcon className="h-4 w-4" />
                      <span>Visit Date *</span>
                    </FormLabel>
                    <Popover>
                      <PopoverTrigger asChild>
                        <FormControl>
                          <Button
                            variant="outline"
                            className={`w-full pl-3 text-left font-normal ${
                              !field.value && "text-muted-foreground"
                            }`}
                            data-testid="button-select-date"
                          >
                            {field.value ? (
                              format(field.value, "PPP")
                            ) : (
                              <span>Pick a date</span>
                            )}
                            <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                          </Button>
                        </FormControl>
                      </PopoverTrigger>
                      <PopoverContent className="w-auto p-0" align="start">
                        <Calendar
                          mode="single"
                          selected={field.value}
                          onSelect={field.onChange}
                          disabled={(date) =>
                            date < new Date(new Date().setHours(0, 0, 0, 0))
                          }
                          initialFocus
                        />
                      </PopoverContent>
                    </Popover>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="plannedStartTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center space-x-2">
                      <Clock className="h-4 w-4" />
                      <span>Start Time</span>
                    </FormLabel>
                    <FormControl>
                      <Input 
                        type="time" 
                        {...field}
                        data-testid="input-start-time"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="plannedEndTime"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center space-x-2">
                      <Clock className="h-4 w-4" />
                      <span>End Time</span>
                    </FormLabel>
                    <FormControl>
                      <Input 
                        type="time" 
                        {...field}
                        data-testid="input-end-time"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="purpose"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center space-x-2">
                    <Target className="h-4 w-4" />
                    <span>Visit Purpose *</span>
                  </FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-purpose">
                        <SelectValue placeholder="Select visit purpose" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="initial_meeting">Initial Meeting</SelectItem>
                      <SelectItem value="demo">Product Demo</SelectItem>
                      <SelectItem value="follow_up">Follow Up</SelectItem>
                      <SelectItem value="quotation_discussion">Quotation Discussion</SelectItem>
                      <SelectItem value="negotiation">Negotiation</SelectItem>
                      <SelectItem value="closing">Closing</SelectItem>
                      <SelectItem value="support">Support</SelectItem>
                      <SelectItem value="other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Lead Information Card */}
            {selectedLead && (
              <Card className="bg-muted/30">
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm">Customer Information</CardTitle>
                </CardHeader>
                <CardContent className="space-y-2">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-muted-foreground">Name:</span>
                      <p className="font-light">
                        {selectedLead.firstName} {selectedLead.lastName}
                      </p>
                    </div>
                    {selectedLead.companyName && (
                      <div>
                        <span className="text-muted-foreground">Company:</span>
                        <p className="font-light">{selectedLead.companyName}</p>
                      </div>
                    )}
                    {selectedLead.phone && (
                      <div>
                        <span className="text-muted-foreground">Phone:</span>
                        <p className="font-light">{selectedLead.phone}</p>
                      </div>
                    )}
                    {selectedLead.email && (
                      <div>
                        <span className="text-muted-foreground">Email:</span>
                        <p className="font-light">{selectedLead.email}</p>
                      </div>
                    )}
                  </div>
                  {selectedLead.requirementDescription && (
                    <div>
                      <span className="text-muted-foreground">Requirements:</span>
                      <p className="text-sm mt-1">{selectedLead.requirementDescription}</p>
                    </div>
                  )}
                </CardContent>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="location" className="space-y-4">
            <FormField
              control={form.control}
              name="visitAddress"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center space-x-2">
                    <MapPin className="h-4 w-4" />
                    <span>Visit Address *</span>
                  </FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Enter the complete visit address"
                      {...field}
                      data-testid="input-visit-address"
                      rows={3}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="visitCity"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>City</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="City"
                        {...field}
                        data-testid="input-visit-city"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="visitState"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>State</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="State"
                        {...field}
                        data-testid="input-visit-state"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <FormField
                control={form.control}
                name="latitude"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Latitude</FormLabel>
                    <FormControl>
                      <Input 
                        type="number"
                        step="any"
                        placeholder="e.g. 12.9716"
                        {...field}
                        onChange={(e) => field.onChange(e.target.value ? parseFloat(e.target.value) : undefined)}
                        value={field.value || ''}
                        data-testid="input-latitude"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="longitude"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Longitude</FormLabel>
                    <FormControl>
                      <Input 
                        type="number"
                        step="any"
                        placeholder="e.g. 77.5946"
                        {...field}
                        onChange={(e) => field.onChange(e.target.value ? parseFloat(e.target.value) : undefined)}
                        value={field.value || ''}
                        data-testid="input-longitude"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="flex items-end">
                <Button
                  type="button"
                  variant="outline"
                  onClick={getCurrentLocation}
                  className="w-full"
                  data-testid="button-get-location"
                >
                  <MapPin className="h-4 w-4 mr-2" />
                  Get GPS
                </Button>
              </div>
            </div>

            <Card className="bg-blue-50 dark:bg-blue-950/30">
              <CardContent className="pt-4">
                <div className="flex items-start space-x-3">
                  <AlertCircle className="h-5 w-5 text-blue-500 mt-0.5" />
                  <div className="text-sm">
                    <p className="font-light text-blue-900 dark:text-blue-100">GPS Coordinates</p>
                    <p className="text-blue-700 dark:text-blue-300 mt-1">
                      GPS coordinates help verify the actual visit location during check-in. 
                      You can get your current location or enter coordinates manually.
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="details" className="space-y-4">
            <FormField
              control={form.control}
              name="preVisitNotes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="flex items-center space-x-2">
                    <FileText className="h-4 w-4" />
                    <span>Pre-Visit Notes</span>
                  </FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Add any preparation notes, talking points, or special instructions for this visit..."
                      {...field}
                      data-testid="textarea-pre-visit-notes"
                      rows={4}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="travelExpense"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Estimated Travel Expense</FormLabel>
                  <FormControl>
                    <Input 
                      type="number"
                      step="0.01"
                      placeholder="Enter amount (e.g. 500.00)"
                      {...field}
                      data-testid="input-travel-expense"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Visit Summary */}
            <Card className="bg-muted/30">
              <CardHeader className="pb-2">
                <CardTitle className="text-sm">Visit Summary</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-muted-foreground">Visit Number:</span>
                    <p className="font-light">{visit?.visitNumber || generateVisitNumber()}</p>
                  </div>
                  <div>
                    <span className="text-muted-foreground">Status:</span>
                    <p className="font-light">Scheduled</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        <Separator />

        <div className="flex justify-end space-x-3">
          <Button
            type="button"
            variant="outline"
            onClick={onCancel}
            disabled={isLoading}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
          <Button
            type="submit"
            disabled={isLoading}
            data-testid="button-submit"
          >
            {isLoading ? 'Saving...' : (visit ? 'Update Visit' : 'Schedule Visit')}
          </Button>
        </div>
      </form>
    </Form>
  );
}

client/src/components/marketing/VisitMap.content:
import { useState, useEffect, useRef } from "react";
import { MapPin, Navigation, Timer, CheckCircle, AlertCircle, Zap, Route, Target } from "lucide-react";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Alert, AlertDescription } from "@/components/ui/alert";

import type { FieldVisit } from "@shared/schema";

interface VisitWithDetails extends FieldVisit {
  lead?: {
    id: string;
    firstName: string;
    lastName: string;
    companyName?: string;
  };
  assignedToUser?: {
    id: string;
    firstName: string;
    lastName: string;
  };
}

interface VisitMapProps {
  visits: VisitWithDetails[];
  isLoading: boolean;
  onVisitSelect: (visit: VisitWithDetails) => void;
  onCheckIn: (visit: VisitWithDetails) => void;
  onCheckOut: (visit: VisitWithDetails) => void;
}

interface MapMarker {
  id: string;
  latitude: number;
  longitude: number;
  visit: VisitWithDetails;
  status: string;
}

export default function VisitMap({ visits, isLoading, onVisitSelect, onCheckIn, onCheckOut }: VisitMapProps) {
  const [currentLocation, setCurrentLocation] = useState<{ latitude: number; longitude: number } | null>(null);
  const [selectedVisit, setSelectedVisit] = useState<VisitWithDetails | null>(null);
  const [mapError, setMapError] = useState<string>('');
  const mapContainerRef = useRef<HTMLDivElement>(null);

  // Filter visits with valid coordinates
  const visitsWithCoordinates = visits.filter(visit => 
    visit.latitude && visit.longitude
  );

  // Create markers from visits
  const markers: MapMarker[] = visitsWithCoordinates.map(visit => ({
    id: visit.id,
    latitude: parseFloat(visit.latitude!),
    longitude: parseFloat(visit.longitude!),
    visit,
    status: visit.status
  }));

  // Get current location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setCurrentLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
        },
        (error) => {
          console.warn('Could not get current location:', error);
        },
        { enableHighAccuracy: true, timeout: 10000 }
      );
    }
  }, []);

  // Get status color for markers
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled':
        return '#3b82f6'; // Blue
      case 'in_progress':
        return '#f59e0b'; // Orange
      case 'completed':
        return '#10b981'; // Green
      case 'cancelled':
        return '#ef4444'; // Red
      default:
        return '#6b7280'; // Gray
    }
  };

  // Get status icon
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'scheduled':
        return Timer;
      case 'in_progress':
        return Navigation;
      case 'completed':
        return CheckCircle;
      case 'cancelled':
        return AlertCircle;
      default:
        return MapPin;
    }
  };

  // Calculate distance between two coordinates
  const calculateDistance = (lat1: number, lon1: number, lat2: number, lon2: number): number => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Open location in external map app
  const openInMaps = (visit: VisitWithDetails) => {
    if (visit.latitude && visit.longitude) {
      const url = `https://www.google.com/maps/dir/?api=1&destination=${visit.latitude},${visit.longitude}`;
      window.open(url, '_blank');
    }
  };

  // Get nearby visits
  const getNearbyVisits = (centerVisit: VisitWithDetails) => {
    if (!centerVisit.latitude || !centerVisit.longitude) return [];

    return visitsWithCoordinates
      .filter(visit => visit.id !== centerVisit.id)
      .map(visit => ({
        visit,
        distance: calculateDistance(
          parseFloat(centerVisit.latitude!),
          parseFloat(centerVisit.longitude!),
          parseFloat(visit.latitude!),
          parseFloat(visit.longitude!)
        )
      }))
      .filter(item => item.distance <= 10) // Within 10km
      .sort((a, b) => a.distance - b.distance)
      .slice(0, 3);
  };

  // Simple map implementation using CSS and positioning
  const renderSimpleMap = () => {
    if (markers.length === 0) {
      return (
        <div className="h-96 bg-muted rounded-lg flex items-center justify-center">
          <div className="text-center">
            <MapPin className="h-12 w-12 text-muted-foreground mx-auto mb-2" />
            <p className="text-muted-foreground">No visits with GPS coordinates</p>
          </div>
        </div>
      );
    }

    // Calculate bounds
    const latitudes = markers.map(m => m.latitude);
    const longitudes = markers.map(m => m.longitude);
    const minLat = Math.min(...latitudes);
    const maxLat = Math.max(...latitudes);
    const minLng = Math.min(...longitudes);
    const maxLng = Math.max(...longitudes);

    const centerLat = (minLat + maxLat) / 2;
    const centerLng = (minLng + maxLng) / 2;

    return (
      <div className="h-96 bg-gradient-to-br from-blue-50 to-green-50 dark:from-blue-950 dark:to-green-950 rounded-lg relative overflow-hidden border">
        {/* Grid Background */}
        <div className="absolute inset-0 opacity-20">
          <div className="grid grid-cols-10 grid-rows-10 h-full w-full">
            {[...Array(100)].map((_, i) => (
              <div key={i} className="border border-gray-300 dark:border-gray-600" />
            ))}
          </div>
        </div>

        {/* Current Location */}
        {currentLocation && (
          <div
            className="absolute w-4 h-4 bg-blue-500 rounded-full border-2 border-white shadow-lg z-10"
            style={{
              left: `${((currentLocation.longitude - minLng) / (maxLng - minLng)) * 100}%`,
              top: `${(1 - (currentLocation.latitude - minLat) / (maxLat - minLat)) * 100}%`,
              transform: 'translate(-50%, -50%)'
            }}
            title="Your Location"
          >
            <div className="absolute inset-0 bg-blue-500 rounded-full animate-ping" />
          </div>
        )}

        {/* Visit Markers */}
        {markers.map((marker) => {
          const StatusIcon = getStatusIcon(marker.status);
          const isSelected = selectedVisit?.id === marker.visit.id;

          return (
            <div
              key={marker.id}
              className={`absolute w-8 h-8 rounded-full border-2 border-white shadow-lg cursor-pointer transition-all z-20 ${
                isSelected ? 'scale-125 ring-2 ring-primary' : 'hover:scale-110'
              }`}
              style={{
                backgroundColor: getStatusColor(marker.status),
                left: `${((marker.longitude - minLng) / (maxLng - minLng)) * 100}%`,
                top: `${(1 - (marker.latitude - minLat) / (maxLat - minLat)) * 100}%`,
                transform: 'translate(-50%, -50%)'
              }}
              onClick={() => setSelectedVisit(marker.visit)}
              title={`${marker.visit.lead?.firstName} ${marker.visit.lead?.lastName} - ${marker.status}`}
              data-testid={`map-marker-${marker.visit.visitNumber}`}
            >
              <StatusIcon className="w-4 h-4 text-white absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2" />
            </div>
          );
        })}

        {/* Map Legend */}
        <div className="absolute bottom-4 left-4 bg-white dark:bg-gray-800 rounded-lg p-3 shadow-lg">
          <h4 className="text-xs font-semibold mb-2">Visit Status</h4>
          <div className="space-y-1">
            {[
              { status: 'scheduled', label: 'Scheduled', color: '#3b82f6' },
              { status: 'in_progress', label: 'In Progress', color: '#f59e0b' },
              { status: 'completed', label: 'Completed', color: '#10b981' },
              { status: 'cancelled', label: 'Cancelled', color: '#ef4444' }
            ].map(({ status, label, color }) => (
              <div key={status} className="flex items-center space-x-2">
                <div 
                  className="w-3 h-3 rounded-full border border-white"
                  style={{ backgroundColor: color }}
                />
                <span className="text-xs">{label}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Current Location Indicator */}
        {currentLocation && (
          <div className="absolute bottom-4 right-4 bg-white dark:bg-gray-800 rounded-lg p-2 shadow-lg">
            <div className="flex items-center space-x-2 text-xs">
              <div className="w-3 h-3 bg-blue-500 rounded-full" />
              <span>Your Location</span>
            </div>
          </div>
        )}
      </div>
    );
  };

  // Loading skeleton
  if (isLoading) {
    return (
      <div className="space-y-4">
        <Skeleton className="h-96 w-full rounded-lg" />
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[...Array(3)].map((_, i) => (
            <Card key={i}>
              <CardContent className="p-4">
                <Skeleton className="h-4 w-3/4 mb-2" />
                <Skeleton className="h-3 w-1/2 mb-1" />
                <Skeleton className="h-3 w-2/3" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Map Container */}
      <Card>
        <CardHeader className="pb-2">
          <CardTitle className="text-sm flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <MapPin className="h-4 w-4" />
              <span>Visit Locations ({visitsWithCoordinates.length} with GPS)</span>
            </div>
            {selectedVisit && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => openInMaps(selectedVisit)}
                data-testid="button-open-in-maps"
              >
                <Route className="h-4 w-4 mr-2" />
                Get Directions
              </Button>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {mapError ? (
            <Alert>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{mapError}</AlertDescription>
            </Alert>
          ) : (
            <div ref={mapContainerRef}>
              {renderSimpleMap()}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Selected Visit Details */}
      {selectedVisit && (
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Target className="h-4 w-4" />
                <span>Selected Visit</span>
              </div>
              <Badge className="capitalize">
                {selectedVisit.status.replace('_', ' ')}
              </Badge>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <h4 className="font-light">
                  {selectedVisit.visitNumber} - {selectedVisit.lead?.firstName} {selectedVisit.lead?.lastName}
                </h4>
                {selectedVisit.lead?.companyName && (
                  <p className="text-sm text-muted-foreground">{selectedVisit.lead.companyName}</p>
                )}
                <p className="text-sm text-muted-foreground mt-1">
                  Purpose: {selectedVisit.purpose.replace('_', ' ')}
                </p>
              </div>

              <div>
                <p className="text-sm text-muted-foreground">Address:</p>
                <p className="text-sm font-light">{selectedVisit.visitAddress}</p>
                {selectedVisit.visitCity && (
                  <p className="text-sm text-muted-foreground">{selectedVisit.visitCity}</p>
                )}
              </div>
            </div>

            <div className="flex space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onVisitSelect(selectedVisit)}
                data-testid="button-view-details"
              >
                View Details
              </Button>

              {selectedVisit.status === 'scheduled' && !selectedVisit.actualStartTime && (
                <Button
                  size="sm"
                  onClick={() => onCheckIn(selectedVisit)}
                  data-testid="button-check-in"
                >
                  <Navigation className="h-4 w-4 mr-2" />
                  Check In
                </Button>
              )}

              {selectedVisit.status === 'in_progress' && selectedVisit.actualStartTime && !selectedVisit.actualEndTime && (
                <Button
                  size="sm"
                  onClick={() => onCheckOut(selectedVisit)}
                  data-testid="button-check-out"
                >
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Check Out
                </Button>
              )}

              <Button
                variant="outline"
                size="sm"
                onClick={() => openInMaps(selectedVisit)}
                data-testid="button-get-directions"
              >
                <Route className="h-4 w-4 mr-2" />
                Directions
              </Button>
            </div>

            {/* Nearby Visits */}
            {(() => {
              const nearby = getNearbyVisits(selectedVisit);
              return nearby.length > 0 && (
                <div>
                  <h5 className="text-sm font-light mb-2">Nearby Visits</h5>
                  <div className="space-y-2">
                    {nearby.map(({ visit, distance }) => (
                      <div 
                        key={visit.id} 
                        className="flex items-center justify-between p-2 bg-muted rounded cursor-pointer hover:bg-muted/80"
                        onClick={() => setSelectedVisit(visit)}
                      >
                        <div>
                          <p className="text-sm font-light">
                            {visit.lead?.firstName} {visit.lead?.lastName}
                          </p>
                          <p className="text-xs text-muted-foreground">
                            {distance.toFixed(1)}km away
                          </p>
                        </div>
                        <Badge variant="outline" className="text-xs">
                          {visit.status.replace('_', ' ')}
                        </Badge>
                      </div>
                    ))}
                  </div>
                </div>
              );
            })()}
          </CardContent>
        </Card>
      )}

      {/* Visit List */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {visitsWithCoordinates.map((visit) => {
          const StatusIcon = getStatusIcon(visit.status);

          return (
            <Card 
              key={visit.id} 
              className={`cursor-pointer transition-all ${
                selectedVisit?.id === visit.id ? 'ring-2 ring-primary' : 'hover:shadow-md'
              }`}
              onClick={() => setSelectedVisit(visit)}
              data-testid={`visit-card-${visit.visitNumber}`}
            >
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-2">
                  <div>
                    <h4 className="font-light text-sm">{visit.visitNumber}</h4>
                    <p className="text-xs text-muted-foreground">
                      {visit.lead?.firstName} {visit.lead?.lastName}
                    </p>
                  </div>
                  <Badge 
                    variant="outline" 
                    className="flex items-center space-x-1"
                    style={{ color: getStatusColor(visit.status) }}
                  >
                    <StatusIcon className="h-3 w-3" />
                    <span className="capitalize text-xs">{visit.status.replace('_', ' ')}</span>
                  </Badge>
                </div>

                <p className="text-xs text-muted-foreground mb-2">
                  {visit.visitAddress}
                  {visit.visitCity && `, ${visit.visitCity}`}
                </p>

                <div className="flex items-center space-x-4 text-xs text-muted-foreground">
                  <div className="flex items-center space-x-1">
                    <MapPin className="h-3 w-3" />
                    <span>GPS</span>
                  </div>
                  {currentLocation && visit.latitude && visit.longitude && (
                    <div className="flex items-center space-x-1">
                      <Route className="h-3 w-3" />
                      <span>
                        {calculateDistance(
                          currentLocation.latitude,
                          currentLocation.longitude,
                          parseFloat(visit.latitude),
                          parseFloat(visit.longitude)
                        ).toFixed(1)}km
                      </span>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* No GPS Data Alert */}
      {visits.length > visitsWithCoordinates.length && (
        <Alert>
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            {visits.length - visitsWithCoordinates.length} visit(s) don't have GPS coordinates and won't appear on the map.
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
}

