import { useState } from "react";
import { format } from "date-fns";
import { 
  MapPin, 
  Calendar, 
  Clock, 
  User, 
  MoreHorizontal, 
  Edit, 
  Trash2, 
  Navigation, 
  CheckCircle, 
  XCircle, 
  Upload,
  Eye,
  Timer,
  AlertCircle
} from "lucide-react";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Skeleton } from "@/components/ui/skeleton";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";

import type { FieldVisit } from "@shared/schema";

interface VisitWithDetails extends FieldVisit {
  lead?: {
    id: string;
    firstName: string;
    lastName: string;
    companyName?: string;
  };
  assignedToUser?: {
    id: string;
    firstName: string;
    lastName: string;
  };
  assignedByUser?: {
    id: string;
    firstName: string;
    lastName: string;
  };
}

type VisitStatus = 'scheduled' | 'in_progress' | 'completed' | 'cancelled';

interface VisitTableProps {
  visits: VisitWithDetails[];
  isLoading: boolean;
  onEdit: (visit: VisitWithDetails) => void;
  onDelete: (visit: VisitWithDetails) => void;
  onCheckIn: (visit: VisitWithDetails) => void;
  onCheckOut: (visit: VisitWithDetails) => void;
  onStatusUpdate: (visit: VisitWithDetails, status: VisitStatus, notes?: string) => void;
  onProofUpload: (visit: VisitWithDetails) => void;
}

export default function VisitTable({ 
  visits, 
  isLoading, 
  onEdit, 
  onDelete, 
  onCheckIn, 
  onCheckOut, 
  onStatusUpdate,
  onProofUpload 
}: VisitTableProps) {
  const [selectedVisit, setSelectedVisit] = useState<VisitWithDetails | null>(null);
  const [statusUpdateOpen, setStatusUpdateOpen] = useState(false);
  const [newStatus, setNewStatus] = useState<VisitStatus>('scheduled');
  const [statusNotes, setStatusNotes] = useState('');

  // Get status badge variant and icon
  const getStatusInfo = (status: VisitStatus) => {
    switch (status) {
      case 'scheduled':
        return {
          variant: 'secondary' as const,
          icon: Calendar,
          color: 'text-blue-600',
          bgColor: 'bg-blue-100 dark:bg-blue-900'
        };
      case 'in_progress':
        return {
          variant: 'default' as const,
          icon: Timer,
          color: 'text-orange-600',
          bgColor: 'bg-orange-100 dark:bg-orange-900'
        };
      case 'completed':
        return {
          variant: 'default' as const,
          icon: CheckCircle,
          color: 'text-green-600',
          bgColor: 'bg-green-100 dark:bg-green-900'
        };
      case 'cancelled':
        return {
          variant: 'destructive' as const,
          icon: XCircle,
          color: 'text-red-600',
          bgColor: 'bg-red-100 dark:bg-red-900'
        };
      default:
        return {
          variant: 'secondary' as const,
          icon: AlertCircle,
          color: 'text-gray-600',
          bgColor: 'bg-gray-100 dark:bg-gray-900'
        };
    }
  };

  // Get purpose display text
  const getPurposeText = (purpose: string) => {
    const purposeMap: Record<string, string> = {
      'initial_meeting': 'Initial Meeting',
      'demo': 'Product Demo',
      'follow_up': 'Follow Up',
      'quotation_discussion': 'Quotation Discussion',
      'negotiation': 'Negotiation',
      'closing': 'Closing',
      'support': 'Support',
      'other': 'Other'
    };
    return purposeMap[purpose] || purpose;
  };

  // Check if visit can be checked in
  const canCheckIn = (visit: VisitWithDetails) => {
    return visit.status === 'scheduled' && !visit.actualStartTime;
  };

  // Check if visit can be checked out
  const canCheckOut = (visit: VisitWithDetails) => {
    return visit.status === 'in_progress' && visit.actualStartTime && !visit.actualEndTime;
  };

  // Handle status update
  const handleStatusUpdate = () => {
    if (selectedVisit) {
      onStatusUpdate(selectedVisit, newStatus, statusNotes);
      setStatusUpdateOpen(false);
      setSelectedVisit(null);
      setStatusNotes('');
    }
  };

  // Open status update dialog
  const openStatusUpdate = (visit: VisitWithDetails) => {
    setSelectedVisit(visit);
    setNewStatus(visit.status);
    setStatusUpdateOpen(true);
  };

  // Calculate visit duration
  const getVisitDuration = (visit: VisitWithDetails) => {
    if (visit.actualStartTime && visit.actualEndTime) {
      const start = new Date(visit.actualStartTime);
      const end = new Date(visit.actualEndTime);
      const durationMs = end.getTime() - start.getTime();
      const hours = Math.floor(durationMs / (1000 * 60 * 60));
      const minutes = Math.floor((durationMs % (1000 * 60 * 60)) / (1000 * 60));
      return `${hours}h ${minutes}m`;
    }
    return null;
  };

  // Loading skeleton
  if (isLoading) {
    return (
      <div className="space-y-4">
        {[...Array(5)].map((_, i) => (
          <Card key={i}>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div className="space-y-2 flex-1">
                  <Skeleton className="h-4 w-1/4" />
                  <Skeleton className="h-3 w-1/2" />
                  <Skeleton className="h-3 w-1/3" />
                </div>
                <div className="space-y-2">
                  <Skeleton className="h-6 w-20" />
                  <Skeleton className="h-8 w-24" />
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  // Empty state
  if (visits.length === 0) {
    return (
      <Card>
        <CardContent className="text-center py-8">
          <MapPin className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold mb-2">No Field Visits Found</h3>
          <p className="text-muted-foreground">
            No visits match your current filters. Try adjusting your search criteria.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      {/* Desktop Table View */}
      <div className="hidden md:block">
        <Card>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Visit Details</TableHead>
                <TableHead>Customer</TableHead>
                <TableHead>Assigned To</TableHead>
                <TableHead>Date & Time</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Location</TableHead>
                <TableHead className="w-[100px]">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {visits.map((visit) => {
                const statusInfo = getStatusInfo(visit.status);
                const StatusIcon = statusInfo.icon;
                const duration = getVisitDuration(visit);

                return (
                  <TableRow key={visit.id} data-testid={`visit-row-${visit.visitNumber}`}>
                    <TableCell>
                      <div className="space-y-1">
                        <div className="font-light" data-testid={`visit-number-${visit.visitNumber}`}>
                          {visit.visitNumber}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {getPurposeText(visit.purpose)}
                        </div>
                        {duration && (
                          <div className="text-xs text-green-600 flex items-center space-x-1">
                            <Clock className="h-3 w-3" />
                            <span>Duration: {duration}</span>
                          </div>
                        )}
                      </div>
                    </TableCell>

                    <TableCell>
                      <div className="space-y-1">
                        <div className="font-light">
                          {visit.lead?.firstName} {visit.lead?.lastName}
                        </div>
                        {visit.lead?.companyName && (
                          <div className="text-sm text-muted-foreground">
                            {visit.lead.companyName}
                          </div>
                        )}
                      </div>
                    </TableCell>

                    <TableCell>
                      <div className="flex items-center space-x-2">
                        <User className="h-4 w-4 text-muted-foreground" />
                        <span className="text-sm">
                          {visit.assignedToUser?.firstName} {visit.assignedToUser?.lastName}
                        </span>
                      </div>
                    </TableCell>

                    <TableCell>
                      <div className="space-y-1">
                        <div className="flex items-center space-x-2">
                          <Calendar className="h-4 w-4 text-muted-foreground" />
                          <span className="text-sm">
                            {format(new Date(visit.plannedDate), 'MMM dd, yyyy')}
                          </span>
                        </div>
                        {visit.plannedStartTime && (
                          <div className="flex items-center space-x-2">
                            <Clock className="h-4 w-4 text-muted-foreground" />
                            <span className="text-sm">
                              {format(new Date(visit.plannedStartTime), 'hh:mm a')}
                            </span>
                          </div>
                        )}
                      </div>
                    </TableCell>

                    <TableCell>
                      <Badge 
                        variant={statusInfo.variant}
                        className={`${statusInfo.bgColor} ${statusInfo.color} capitalize flex items-center space-x-1 w-fit`}
                      >
                        <StatusIcon className="h-3 w-3" />
                        <span>{visit.status.replace('_', ' ')}</span>
                      </Badge>
                    </TableCell>

                    <TableCell>
                      <div className="text-sm text-muted-foreground max-w-[200px] truncate">
                        {visit.visitAddress}
                        {visit.visitCity && `, ${visit.visitCity}`}
                      </div>
                      {(visit.latitude && visit.longitude) && (
                        <div className="text-xs text-green-600 flex items-center space-x-1 mt-1">
                          <MapPin className="h-3 w-3" />
                          <span>GPS Available</span>
                        </div>
                      )}
                    </TableCell>

                    <TableCell>
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button 
                            variant="ghost" 
                            className="h-8 w-8 p-0"
                            data-testid={`visit-actions-${visit.visitNumber}`}
                          >
                            <MoreHorizontal className="h-4 w-4" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                          <DropdownMenuItem onClick={() => onEdit(visit)}>
                            <Edit className="h-4 w-4 mr-2" />
                            Edit
                          </DropdownMenuItem>

                          {canCheckIn(visit) && (
                            <DropdownMenuItem onClick={() => onCheckIn(visit)}>
                              <Navigation className="h-4 w-4 mr-2" />
                              Check In
                            </DropdownMenuItem>
                          )}

                          {canCheckOut(visit) && (
                            <DropdownMenuItem onClick={() => onCheckOut(visit)}>
                              <CheckCircle className="h-4 w-4 mr-2" />
                              Check Out
                            </DropdownMenuItem>
                          )}

                          <DropdownMenuItem onClick={() => openStatusUpdate(visit)}>
                            <Timer className="h-4 w-4 mr-2" />
                            Update Status
                          </DropdownMenuItem>

                          {visit.status === 'completed' && (
                            <DropdownMenuItem onClick={() => onProofUpload(visit)}>
                              <Upload className="h-4 w-4 mr-2" />
                              Upload Proof
                            </DropdownMenuItem>
                          )}

                          <DropdownMenuSeparator />

                          <DropdownMenuItem 
                            onClick={() => onDelete(visit)}
                            className="text-red-600"
                          >
                            <Trash2 className="h-4 w-4 mr-2" />
                            Delete
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </Card>
      </div>

      {/* Mobile Card View */}
      <div className="md:hidden space-y-4">
        {visits.map((visit) => {
          const statusInfo = getStatusInfo(visit.status);
          const StatusIcon = statusInfo.icon;
          const duration = getVisitDuration(visit);

          return (
            <Card key={visit.id} data-testid={`visit-card-${visit.visitNumber}`}>
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-3">
                  <div className="space-y-1">
                    <div className="font-semibold" data-testid={`visit-number-${visit.visitNumber}`}>
                      {visit.visitNumber}
                    </div>
                    <Badge 
                      variant={statusInfo.variant}
                      className={`${statusInfo.bgColor} ${statusInfo.color} capitalize flex items-center space-x-1 w-fit`}
                    >
                      <StatusIcon className="h-3 w-3" />
                      <span>{visit.status.replace('_', ' ')}</span>
                    </Badge>
                  </div>

                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button 
                        variant="ghost" 
                        className="h-8 w-8 p-0"
                        data-testid={`visit-actions-${visit.visitNumber}`}
                      >
                        <MoreHorizontal className="h-4 w-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem onClick={() => onEdit(visit)}>
                        <Edit className="h-4 w-4 mr-2" />
                        Edit
                      </DropdownMenuItem>

                      {canCheckIn(visit) && (
                        <DropdownMenuItem onClick={() => onCheckIn(visit)}>
                          <Navigation className="h-4 w-4 mr-2" />
                          Check In
                        </DropdownMenuItem>
                      )}

                      {canCheckOut(visit) && (
                        <DropdownMenuItem onClick={() => onCheckOut(visit)}>
                          <CheckCircle className="h-4 w-4 mr-2" />
                          Check Out
                        </DropdownMenuItem>
                      )}

                      <DropdownMenuItem onClick={() => openStatusUpdate(visit)}>
                        <Timer className="h-4 w-4 mr-2" />
                        Update Status
                      </DropdownMenuItem>

                      {visit.status === 'completed' && (
                        <DropdownMenuItem onClick={() => onProofUpload(visit)}>
                          <Upload className="h-4 w-4 mr-2" />
                          Upload Proof
                        </DropdownMenuItem>
                      )}

                      <DropdownMenuSeparator />

                      <DropdownMenuItem 
                        onClick={() => onDelete(visit)}
                        className="text-red-600"
                      >
                        <Trash2 className="h-4 w-4 mr-2" />
                        Delete
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>

                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <User className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm font-light">
                      {visit.lead?.firstName} {visit.lead?.lastName}
                    </span>
                    {visit.lead?.companyName && (
                      <span className="text-sm text-muted-foreground">
                        - {visit.lead.companyName}
                      </span>
                    )}
                  </div>

                  <div className="flex items-center space-x-2">
                    <Calendar className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm">
                      {format(new Date(visit.plannedDate), 'MMM dd, yyyy')}
                    </span>
                    {visit.plannedStartTime && (
                      <>
                        <Clock className="h-4 w-4 text-muted-foreground" />
                        <span className="text-sm">
                          {format(new Date(visit.plannedStartTime), 'hh:mm a')}
                        </span>
                      </>
                    )}
                  </div>

                  <div className="flex items-center space-x-2">
                    <MapPin className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm text-muted-foreground">
                      {visit.visitAddress}
                      {visit.visitCity && `, ${visit.visitCity}`}
                    </span>
                  </div>

                  <div className="text-sm text-muted-foreground">
                    Purpose: {getPurposeText(visit.purpose)}
                  </div>

                  {duration && (
                    <div className="text-xs text-green-600 flex items-center space-x-1">
                      <Clock className="h-3 w-3" />
                      <span>Duration: {duration}</span>
                    </div>
                  )}

                  <div className="text-sm text-muted-foreground">
                    Assigned to: {visit.assignedToUser?.firstName} {visit.assignedToUser?.lastName}
                  </div>
                </div>

                <div className="flex space-x-2 mt-3">
                  {canCheckIn(visit) && (
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => onCheckIn(visit)}
                      className="flex-1"
                    >
                      <Navigation className="h-4 w-4 mr-1" />
                      Check In
                    </Button>
                  )}

                  {canCheckOut(visit) && (
                    <Button
                      size="sm"
                      onClick={() => onCheckOut(visit)}
                      className="flex-1"
                    >
                      <CheckCircle className="h-4 w-4 mr-1" />
                      Check Out
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Status Update Dialog */}
      <Dialog open={statusUpdateOpen} onOpenChange={setStatusUpdateOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Update Visit Status</DialogTitle>
            <DialogDescription>
              Change the status of visit {selectedVisit?.visitNumber}
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <label className="text-sm font-light">New Status</label>
              <Select value={newStatus} onValueChange={(value) => setNewStatus(value as VisitStatus)}>
                <SelectTrigger data-testid="select-new-status">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="scheduled">Scheduled</SelectItem>
                  <SelectItem value="in_progress">In Progress</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <label className="text-sm font-light">Notes (Optional)</label>
              <Textarea
                placeholder="Add a note about this status change..."
                value={statusNotes}
                onChange={(e) => setStatusNotes(e.target.value)}
                data-testid="textarea-status-notes"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                onClick={() => setStatusUpdateOpen(false)}
                data-testid="button-cancel-status"
              >
                Cancel
              </Button>
              <Button
                onClick={handleStatusUpdate}
                data-testid="button-update-status"
              >
                Update Status
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/pages/marketing/FieldVisits.t.content:
import { useState, useMemo } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Plus, MapPin, Calendar, Users, Clock, CheckCircle, XCircle, AlertCircle, Map, Table, Filter } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";

import VisitForm from "@/components/marketing/VisitForm";
import VisitTable from "@/components/marketing/VisitTable";
import VisitMap from "@/components/marketing/VisitMap";
import GPSModal from "@/components/marketing/GPSModal";
import ProofUpload from "@/components/marketing/ProofUpload";

import type { FieldVisit, InsertFieldVisit, User, Lead } from "@shared/schema";

interface VisitWithDetails extends FieldVisit {
  lead?: {
    id: string;
    firstName: string;
    lastName: string;
    companyName?: string;
  };
  assignedToUser?: {
    id: string;
    firstName: string;
    lastName: string;
  };
  assignedByUser?: {
    id: string;
    firstName: string;
    lastName: string;
  };
}

interface VisitMetrics {
  totalVisits: number;
  todayVisits: number;
  scheduledVisits: number;
  inProgressVisits: number;
  completedVisits: number;
  cancelledVisits: number;
  completionRate: number;
  averageDuration: number;
}

type VisitStatus = 'scheduled' | 'in_progress' | 'completed' | 'cancelled';
type ViewMode = 'table' | 'map';

export default function FieldVisits() {
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [selectedVisit, setSelectedVisit] = useState<VisitWithDetails | null>(null);
  const [viewMode, setViewMode] = useState<ViewMode>('table');
  const [statusFilter, setStatusFilter] = useState<VisitStatus | 'all'>('all');
  const [assigneeFilter, setAssigneeFilter] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [gpsModalOpen, setGpsModalOpen] = useState(false);
  const [gpsAction, setGpsAction] = useState<'check-in' | 'check-out'>('check-in');
  const [proofModalOpen, setProofModalOpen] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch field visits with error handling
  const { data: visits = [], isLoading: visitsLoading, error: visitsError } = useQuery<VisitWithDetails[]>({
    queryKey: ['/api/field-visits'],
    meta: { errorMessage: "Failed to load field visits" }
  });

  // Fetch today's visits
  const { data: todayVisits = [] } = useQuery<VisitWithDetails[]>({
    queryKey: ['/api/field-visits/today'],
    meta: { errorMessage: "Failed to load today's visits" }
  });

  // Fetch visit metrics
  const { data: metrics, isLoading: metricsLoading } = useQuery<VisitMetrics>({
    queryKey: ['/api/field-visits/metrics'],
    meta: { errorMessage: "Failed to load visit metrics" }
  });

  // Fetch users for assignment dropdown
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['/api/users'],
    meta: { errorMessage: "Failed to load users" }
  });

  // Fetch leads for visit creation
  const { data: leads = [] } = useQuery<Lead[]>({
    queryKey: ['/api/marketing/leads '],
    meta: { errorMessage: "Failed to load leads" }
  });

  // Create visit mutation
  const createVisitMutation = useMutation({
    mutationFn: (data: InsertFieldVisit) => apiRequest('/api/field-visits', { method: 'POST', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/today'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/metrics'] });
      toast({ title: "Visit scheduled successfully!" });
      setIsFormOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error scheduling visit", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Update visit mutation
  const updateVisitMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<InsertFieldVisit> }) => 
      apiRequest(`/api/field-visits/${id}`, { method: 'PUT', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/today'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/metrics'] });
      toast({ title: "Visit updated successfully!" });
      setIsFormOpen(false);
      setSelectedVisit(null);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating visit", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Delete visit mutation
  const deleteVisitMutation = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/field-visits/${id}`, { method: 'DELETE' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/today'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/metrics'] });
      toast({ title: "Visit deleted successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error deleting visit", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Check-in mutation
  const checkInMutation = useMutation({
    mutationFn: ({ id, location }: { id: string; location: { latitude: number; longitude: number; location?: string; photoPath?: string } }) =>
      apiRequest(`/api/field-visits/${id}/check-in`, { method: 'POST', body: JSON.stringify(location) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/today'] });
      toast({ title: "Successfully checked in!" });
      setGpsModalOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error checking in", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Check-out mutation
  const checkOutMutation = useMutation({
    mutationFn: ({ id, data }: { 
      id: string; 
      data: { 
        latitude: number; 
        longitude: number; 
        location?: string; 
        photoPath?: string;
        visitNotes?: string;
        outcome?: string;
        nextAction?: string;
      } 
    }) =>
      apiRequest(`/api/field-visits/${id}/check-out`, { method: 'POST', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/today'] });
      toast({ title: "Successfully checked out!" });
      setGpsModalOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error checking out", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Status update mutation
  const updateStatusMutation = useMutation({
    mutationFn: ({ id, status, notes }: { id: string; status: VisitStatus; notes?: string }) =>
      apiRequest(`/api/field-visits/${id}/status`, { method: 'PUT', body: JSON.stringify({ status, notes }) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
      queryClient.invalidateQueries({ queryKey: ['/api/field-visits/today'] });
      toast({ title: "Visit status updated!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating status", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Filter visits based on status, assignee, and search term
  const filteredVisits = useMemo(() => {
    return visits.filter(visit => {
      const matchesStatus = statusFilter === 'all' || visit.status === statusFilter;
      const matchesAssignee = assigneeFilter === 'all' || visit.assignedTo === assigneeFilter;
      const matchesSearch = !searchTerm || 
        visit.visitNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
        visit.visitAddress.toLowerCase().includes(searchTerm.toLowerCase()) ||
        visit.lead?.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        visit.lead?.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        visit.lead?.companyName?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesStatus && matchesAssignee && matchesSearch;
    });
  }, [visits, statusFilter, assigneeFilter, searchTerm]);

  // Get status counts for tabs
  const statusCounts = useMemo(() => {
    const counts = visits.reduce((acc, visit) => {
      acc[visit.status] = (acc[visit.status] || 0) + 1;
      return acc;
    }, {} as Record<VisitStatus, number>);

    return {
      all: visits.length,
      scheduled: counts.scheduled || 0,
      in_progress: counts.in_progress || 0,
      completed: counts.completed || 0,
      cancelled: counts.cancelled || 0
    };
  }, [visits]);

  // Handle form submission
  const handleFormSubmit = (data: InsertFieldVisit) => {
    if (selectedVisit) {
      updateVisitMutation.mutate({ id: selectedVisit.id, data });
    } else {
      createVisitMutation.mutate(data);
    }
  };

  // Handle visit edit
  const handleEditVisit = (visit: VisitWithDetails) => {
    setSelectedVisit(visit);
    setIsFormOpen(true);
  };

  // Handle visit delete
  const handleDeleteVisit = (visit: VisitWithDetails) => {
    if (confirm(`Are you sure you want to delete visit ${visit.visitNumber}?`)) {
      deleteVisitMutation.mutate(visit.id);
    }
  };

  // Handle GPS actions
  const handleGPSAction = (visit: VisitWithDetails, action: 'check-in' | 'check-out') => {
    setSelectedVisit(visit);
    setGpsAction(action);
    setGpsModalOpen(true);
  };

  // Handle GPS check-in
  const handleCheckIn = (location: { latitude: number; longitude: number; location?: string; photoPath?: string }) => {
    if (selectedVisit) {
      checkInMutation.mutate({ id: selectedVisit.id, location });
    }
  };

  // Handle GPS check-out
  const handleCheckOut = (data: { 
    latitude: number; 
    longitude: number; 
    location?: string; 
    photoPath?: string;
    visitNotes?: string;
    outcome?: string;
    nextAction?: string;
  }) => {
    if (selectedVisit) {
      checkOutMutation.mutate({ id: selectedVisit.id, data });
    }
  };

  // Handle status update
  const handleStatusUpdate = (visit: VisitWithDetails, status: VisitStatus, notes?: string) => {
    updateStatusMutation.mutate({ id: visit.id, status, notes });
  };

  // Handle proof upload
  const handleProofUpload = (visit: VisitWithDetails) => {
    setSelectedVisit(visit);
    setProofModalOpen(true);
  };

  // Default metrics if loading
  const displayMetrics: VisitMetrics = metrics || {
    totalVisits: 0,
    todayVisits: 0,
    scheduledVisits: 0,
    inProgressVisits: 0,
    completedVisits: 0,
    cancelledVisits: 0,
    completionRate: 0,
    averageDuration: 0
  };

  if (visitsError) {
    return (
      <div className="p-8">
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
              <h2 className="text-lg font-semibold mb-2">Error Loading Field Visits</h2>
              <p className="text-muted-foreground">
                Failed to load field visits data. Please try again.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-4 md:p-8 space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-foreground" data-testid="page-title">Field Visits</h1>
          <p className="text-muted-foreground">
            Schedule, track, and manage field visits with GPS verification
          </p>
        </div>

        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setViewMode(viewMode === 'table' ? 'map' : 'table')}
            data-testid="toggle-view-mode"
          >
            {viewMode === 'table' ? <Map className="h-4 w-4 mr-2" /> : <Table className="h-4 w-4 mr-2" />}
            {viewMode === 'table' ? 'Map View' : 'Table View'}
          </Button>

          <Button 
            onClick={() => {
              setSelectedVisit(null);
              setIsFormOpen(true);
            }}
            data-testid="button-schedule-visit"
          >
            <Plus className="h-4 w-4 mr-2" />
            Schedule Visit
          </Button>
        </div>
      </div>

      {/* Metrics Dashboard */}
      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-light flex items-center space-x-2">
              <Calendar className="h-4 w-4 text-blue-500" />
              <span>Today's Visits</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-today-visits">
              {metricsLoading ? '...' : displayMetrics.todayVisits}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-light flex items-center space-x-2">
              <Clock className="h-4 w-4 text-orange-500" />
              <span>Scheduled</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-scheduled-visits">
              {statusCounts.scheduled}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-light flex items-center space-x-2">
              <MapPin className="h-4 w-4 text-blue-500" />
              <span>In Progress</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-in-progress-visits">
              {statusCounts.in_progress}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-light flex items-center space-x-2">
              <CheckCircle className="h-4 w-4 text-green-500" />
              <span>Completed</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-completed-visits">
              {statusCounts.completed}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-light flex items-center space-x-2">
              <XCircle className="h-4 w-4 text-red-500" />
              <span>Cancelled</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-cancelled-visits">
              {statusCounts.cancelled}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-light flex items-center space-x-2">
              <Users className="h-4 w-4 text-emerald-500" />
              <span>Completion Rate</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="metric-completion-rate">
              {metricsLoading ? '...' : `${displayMetrics.completionRate?.toFixed(1) || 0}%`}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="flex-1">
          <Input
            placeholder="Search visits, customers, or addresses..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            data-testid="input-search-visits"
          />
        </div>

        <Select value={assigneeFilter} onValueChange={setAssigneeFilter}>
          <SelectTrigger className="w-full sm:w-48" data-testid="select-assignee-filter">
            <SelectValue placeholder="Filter by assignee" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Assignees</SelectItem>
            {users.map(user => (
              <SelectItem key={user.id} value={user.id}>
                {user.firstName} {user.lastName}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Status Filter Tabs */}
      <Tabs value={statusFilter} onValueChange={(value) => setStatusFilter(value as VisitStatus | 'all')}>
        <TabsList className="grid w-full grid-cols-3 md:grid-cols-5">
          <TabsTrigger value="all" data-testid="tab-all-visits">
            All ({statusCounts.all})
          </TabsTrigger>
          <TabsTrigger value="scheduled" data-testid="tab-scheduled-visits">
            Scheduled ({statusCounts.scheduled})
          </TabsTrigger>
          <TabsTrigger value="in_progress" data-testid="tab-in-progress-visits">
            In Progress ({statusCounts.in_progress})
          </TabsTrigger>
          <TabsTrigger value="completed" data-testid="tab-completed-visits">
            Completed ({statusCounts.completed})
          </TabsTrigger>
          <TabsTrigger value="cancelled" data-testid="tab-cancelled-visits">
            Cancelled ({statusCounts.cancelled})
          </TabsTrigger>
        </TabsList>

        <TabsContent value={statusFilter} className="mt-6">
          {viewMode === 'table' ? (
            <VisitTable
              visits={filteredVisits}
              isLoading={visitsLoading}
              onEdit={handleEditVisit}
              onDelete={handleDeleteVisit}
              onCheckIn={(visit) => handleGPSAction(visit, 'check-in')}
              onCheckOut={(visit) => handleGPSAction(visit, 'check-out')}
              onStatusUpdate={handleStatusUpdate}
              onProofUpload={handleProofUpload}
            />
          ) : (
            <VisitMap
              visits={filteredVisits}
              isLoading={visitsLoading}
              onVisitSelect={setSelectedVisit}
              onCheckIn={(visit) => handleGPSAction(visit, 'check-in')}
              onCheckOut={(visit) => handleGPSAction(visit, 'check-out')}
            />
          )}
        </TabsContent>
      </Tabs>

      {/* Visit Form Modal */}
      <Dialog open={isFormOpen} onOpenChange={setIsFormOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>
              {selectedVisit ? 'Edit Visit' : 'Schedule New Visit'}
            </DialogTitle>
          </DialogHeader>
          <VisitForm
            visit={selectedVisit}
            leads={leads}
            users={users}
            onSubmit={handleFormSubmit}
            onCancel={() => {
              setIsFormOpen(false);
              setSelectedVisit(null);
            }}
            isLoading={createVisitMutation.isPending || updateVisitMutation.isPending}
          />
        </DialogContent>
      </Dialog>

      {/* GPS Check-in/Check-out Modal */}
      <GPSModal
        open={gpsModalOpen}
        onOpenChange={setGpsModalOpen}
        visit={selectedVisit}
        action={gpsAction}
        onCheckIn={handleCheckIn}
        onCheckOut={handleCheckOut}
        isLoading={checkInMutation.isPending || checkOutMutation.isPending}
      />

      {/* Proof Upload Modal */}
      <ProofUpload
        open={proofModalOpen}
        onOpenChange={setProofModalOpen}
        visit={selectedVisit}
        onUploadComplete={() => {
          queryClient.invalidateQueries({ queryKey: ['/api/field-visits'] });
          setProofModalOpen(false);
        }}
      />
    </div>
  );
}

client/src/components/marketing/TaskBoar.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Plus, MoreVertical, Users, CheckCircle, Clock, AlertCircle } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

import TaskCard from "./TaskCard";
import type { MarketingTask, User, Lead, FieldVisit } from "@shared/schema";

interface TaskWithDetails extends MarketingTask {
  assignedToUser?: User;
  assignedByUser?: User;
  lead?: Lead;
  fieldVisit?: FieldVisit;
}

interface TaskBoardProps {
  tasks: TaskWithDetails[];
  onEdit?: (task: TaskWithDetails) => void;
  onDelete?: (taskId: string) => void;
  onCreateTask?: (status?: string) => void;
  loading?: boolean;
}

interface BoardColumn {
  id: string;
  title: string;
  status: string;
  color: string;
  icon: React.ComponentType<{ className?: string }>;
  description: string;
  maxTasks?: number;
}

const boardColumns: BoardColumn[] = [
  {
    id: 'pending',
    title: 'Pending',
    status: 'pending',
    color: 'bg-gray-50 border-gray-200 dark:bg-gray-900 dark:border-gray-700',
    icon: Clock,
    description: 'Tasks waiting to be started',
    maxTasks: 50
  },
  {
    id: 'in_progress',
    title: 'In Progress',
    status: 'in_progress',
    color: 'bg-blue-50 border-blue-200 dark:bg-blue-900 dark:border-blue-700',
    icon: Users,
    description: 'Currently active tasks',
    maxTasks: 20
  },
  {
    id: 'completed',
    title: 'Completed',
    status: 'completed',
    color: 'bg-green-50 border-green-200 dark:bg-green-900 dark:border-green-700',
    icon: CheckCircle,
    description: 'Successfully finished tasks'
  },
  {
    id: 'cancelled',
    title: 'Cancelled',
    status: 'cancelled',
    color: 'bg-red-50 border-red-200 dark:bg-red-900 dark:border-red-700',
    icon: AlertCircle,
    description: 'Tasks that were cancelled or abandoned'
  }
];

export default function TaskBoard({ 
  tasks, 
  onEdit, 
  onDelete, 
  onCreateTask,
  loading = false 
}: TaskBoardProps) {
  const [draggedTask, setDraggedTask] = useState<TaskWithDetails | null>(null);
  const [dragOverColumn, setDragOverColumn] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateStatusMutation = useMutation({
    mutationFn: ({ taskId, status }: { taskId: string; status: string }) => 
      apiRequest(`/api/marketing-tasks/${taskId}/status`, { 
        method: 'PUT', 
        body: JSON.stringify({ status }) 
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task status updated successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating task status", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const completeTaskMutation = useMutation({
    mutationFn: (taskId: string) => 
      apiRequest(`/api/marketing-tasks/${taskId}/complete`, { method: 'POST' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task completed successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error completing task", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Group tasks by status
  const tasksByStatus = tasks.reduce((acc, task) => {
    if (!acc[task.status]) {
      acc[task.status] = [];
    }
    acc[task.status].push(task);
    return acc;
  }, {} as Record<string, TaskWithDetails[]>);

  // Handle drag and drop
  const handleDragStart = (task: TaskWithDetails) => {
    setDraggedTask(task);
  };

  const handleDragOver = (e: React.DragEvent, columnId: string) => {
    e.preventDefault();
    setDragOverColumn(columnId);
  };

  const handleDragLeave = () => {
    setDragOverColumn(null);
  };

  const handleDrop = (e: React.DragEvent, newStatus: string) => {
    e.preventDefault();
    setDragOverColumn(null);

    if (!draggedTask || draggedTask.status === newStatus) {
      setDraggedTask(null);
      return;
    }

    if (newStatus === 'completed') {
      completeTaskMutation.mutate(draggedTask.id);
    } else {
      updateStatusMutation.mutate({ taskId: draggedTask.id, status: newStatus });
    }

    setDraggedTask(null);
  };

  const getColumnStats = (status: string) => {
    const columnTasks = tasksByStatus[status] || [];
    const overdue = columnTasks.filter(task => 
      task.dueDate && new Date(task.dueDate) < new Date() && task.status !== 'completed'
    ).length;

    return {
      total: columnTasks.length,
      overdue,
      highPriority: columnTasks.filter(task => 
        task.priority === 'high' || task.priority === 'urgent'
      ).length
    };
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {boardColumns.map((column) => (
          <Card key={column.id} className={`min-h-[600px] ${column.color}`}>
            <CardHeader>
              <div className="animate-pulse space-y-2">
                <div className="h-4 bg-muted rounded w-3/4"></div>
                <div className="h-3 bg-muted rounded w-1/2"></div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {[1, 2, 3].map((i) => (
                  <div key={i} className="h-32 bg-muted rounded animate-pulse"></div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Board Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold">Task Board</h3>
          <p className="text-sm text-muted-foreground">
            Drag and drop tasks between columns to update their status
          </p>
        </div>
        <Button 
          onClick={() => onCreateTask?.()}
          className="flex items-center space-x-2"
          data-testid="create-task-board"
        >
          <Plus className="h-4 w-4" />
          <span>Add Task</span>
        </Button>
      </div>

      {/* Kanban Board */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {boardColumns.map((column) => {
          const Icon = column.icon;
          const columnTasks = tasksByStatus[column.status] || [];
          const stats = getColumnStats(column.status);
          const isMaxed = column.maxTasks && stats.total >= column.maxTasks;

          return (
            <Card 
              key={column.id}
              className={`min-h-[600px] transition-all duration-200 ${column.color} ${
                dragOverColumn === column.status ? 'ring-2 ring-primary shadow-lg' : ''
              }`}
              onDragOver={(e) => handleDragOver(e, column.status)}
              onDragLeave={handleDragLeave}
              onDrop={(e) => handleDrop(e, column.status)}
              data-testid={`column-${column.status}`}
            >
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <Icon className="h-4 w-4" />
                    <CardTitle className="text-sm font-light">
                      {column.title}
                    </CardTitle>
                    <Badge variant="secondary" data-testid={`column-count-${column.status}`}>
                      {stats.total}
                    </Badge>
                  </div>

                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                        <MoreVertical className="h-3 w-3" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem 
                        onClick={() => onCreateTask?.(column.status)}
                        data-testid={`create-task-${column.status}`}
                      >
                        <Plus className="h-4 w-4 mr-2" />
                        Add Task Here
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </div>

                <p className="text-xs text-muted-foreground">
                  {column.description}
                </p>

                {/* Column Stats */}
                <div className="flex items-center space-x-3 text-xs">
                  {stats.highPriority > 0 && (
                    <div className="flex items-center space-x-1 text-orange-600 dark:text-orange-400">
                      <AlertCircle className="h-3 w-3" />
                      <span>{stats.highPriority} high priority</span>
                    </div>
                  )}

                  {stats.overdue > 0 && (
                    <div className="flex items-center space-x-1 text-red-600 dark:text-red-400">
                      <Clock className="h-3 w-3" />
                      <span>{stats.overdue} overdue</span>
                    </div>
                  )}

                  {isMaxed && (
                    <Badge variant="destructive" className="text-xs">
                      At capacity
                    </Badge>
                  )}
                </div>
              </CardHeader>

              <CardContent className="pt-0">
                <ScrollArea className="h-[500px]">
                  <div className="space-y-3">
                    {columnTasks.map((task) => (
                      <div
                        key={task.id}
                        draggable
                        onDragStart={() => handleDragStart(task)}
                        className="cursor-move transition-transform hover:scale-105"
                        data-testid={`draggable-task-${task.id}`}
                      >
                        <TaskCard
                          task={task}
                          onEdit={onEdit}
                          onDelete={onDelete}
                          compact
                          showAssignee
                          draggable
                        />
                      </div>
                    ))}

                    {/* Empty State */}
                    {columnTasks.length === 0 && (
                      <div className="flex flex-col items-center justify-center py-8 text-center">
                        <Icon className="h-8 w-8 text-muted-foreground/50 mb-2" />
                        <p className="text-sm text-muted-foreground">
                          No {column.title.toLowerCase()} tasks
                        </p>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onCreateTask?.(column.status)}
                          className="mt-2 text-xs"
                          data-testid={`empty-create-${column.status}`}
                        >
                          <Plus className="h-3 w-3 mr-1" />
                          Add Task
                        </Button>
                      </div>
                    )}

                    {/* Drop Zone Indicator */}
                    {dragOverColumn === column.status && draggedTask && (
                      <div className="border-2 border-dashed border-primary rounded-lg p-4 bg-primary/5 text-center">
                        <p className="text-sm text-primary font-light">
                          Drop to move task to {column.title}
                        </p>
                      </div>
                    )}
                  </div>
                </ScrollArea>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Board Footer with Quick Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
        {boardColumns.map((column) => {
          const stats = getColumnStats(column.status);
          return (
            <div key={column.id} className="bg-muted/30 rounded-lg p-3">
              <div className="text-2xl font-bold" data-testid={`stat-total-${column.status}`}>
                {stats.total}
              </div>
              <div className="text-sm text-muted-foreground">
                {column.title}
              </div>
              {stats.overdue > 0 && column.status !== 'completed' && (
                <div className="text-xs text-red-600 dark:text-red-400 mt-1">
                  {stats.overdue} overdue
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

client/src/components/marketing/TaskCard.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  Clock, 
  User as UserIcon, 
  Calendar, 
  Tag, 
  CheckCircle, 
  Play, 
  Pause, 
  MoreVertical,
  Edit,
  Trash2,
  Users,
  MapPin,
  Phone,
  Mail,
  Presentation,
  Search,
  TrendingUp,
  FileText,
  Target,
  AlertTriangle,
  Timer,
  Building
} from "lucide-react";
import { format, isToday, isTomorrow, isPast } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Progress } from "@/components/ui/progress";
import { 
  DropdownMenu, 
  DropdownMenuContent, 
  DropdownMenuItem, 
  DropdownMenuSeparator, 
  DropdownMenuTrigger 
} from "@/components/ui/dropdown-menu";
import { Tooltip, TooltipContent, TooltipTrigger } from "@/components/ui/tooltip";

import type { MarketingTask, User, Lead, FieldVisit } from "@shared/schema";

interface TaskWithDetails extends MarketingTask {
  assignedToUser?: User;
  assignedByUser?: User;
  lead?: Lead;
  fieldVisit?: FieldVisit;
}

interface TaskCardProps {
  task: TaskWithDetails;
  onEdit?: (task: TaskWithDetails) => void;
  onDelete?: (taskId: string) => void;
  compact?: boolean;
  showAssignee?: boolean;
  draggable?: boolean;
}

const taskTypeIcons = {
  visit_client: Users,
  follow_up: Phone,
  demo: Presentation,
  presentation: FileText,
  proposal: Target,
  phone_call: Phone,
  email_campaign: Mail,
  market_research: Search,
  other: Tag
};

const taskTypeColors = {
  visit_client: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
  follow_up: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
  demo: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
  presentation: "bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200",
  proposal: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
  phone_call: "bg-cyan-100 text-cyan-800 dark:bg-cyan-900 dark:text-cyan-200",
  email_campaign: "bg-pink-100 text-pink-800 dark:bg-pink-900 dark:text-pink-200",
  market_research: "bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-200",
  other: "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200"
};

const priorityColors = {
  low: "bg-gray-100 text-gray-800 border-gray-200",
  medium: "bg-yellow-100 text-yellow-800 border-yellow-200", 
  high: "bg-orange-100 text-orange-800 border-orange-200",
  urgent: "bg-red-100 text-red-800 border-red-200"
};

const statusColors = {
  pending: "bg-gray-100 text-gray-800",
  in_progress: "bg-blue-100 text-blue-800",
  completed: "bg-green-100 text-green-800",
  cancelled: "bg-red-100 text-red-800"
};

export default function TaskCard({ 
  task, 
  onEdit, 
  onDelete, 
  compact = false,
  showAssignee = true,
  draggable = false 
}: TaskCardProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateStatusMutation = useMutation({
    mutationFn: ({ status }: { status: string }) => 
      apiRequest(`/api/marketing-tasks/${task.id}/status`, { 
        method: 'PUT', 
        body: JSON.stringify({ status }) 
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task status updated successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating task status", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const completeTaskMutation = useMutation({
    mutationFn: () => apiRequest(`/api/marketing-tasks/${task.id}/complete`, { method: 'POST' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task completed successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error completing task", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const TaskIcon = taskTypeIcons[task.type] || Tag;
  const isOverdue = task.dueDate && isPast(new Date(task.dueDate)) && task.status !== 'completed';
  const isDueToday = task.dueDate && isToday(new Date(task.dueDate));
  const isDueTomorrow = task.dueDate && isTomorrow(new Date(task.dueDate));

  const handleStatusChange = (status: string) => {
    if (status === 'completed') {
      completeTaskMutation.mutate();
    } else {
      updateStatusMutation.mutate({ status });
    }
  };

  const getProgressPercentage = () => {
    switch (task.status) {
      case 'completed':
        return 100;
      case 'in_progress':
        return 60;
      case 'pending':
        return 0;
      case 'cancelled':
        return 0;
      default:
        return 0;
    }
  };

  const getDueDateColor = () => {
    if (isOverdue) return "text-red-600 dark:text-red-400";
    if (isDueToday) return "text-orange-600 dark:text-orange-400";
    if (isDueTomorrow) return "text-yellow-600 dark:text-yellow-400";
    return "text-muted-foreground";
  };

  const getDueDateText = () => {
    if (!task.dueDate) return null;
    const date = new Date(task.dueDate);

    if (isOverdue) return "Overdue";
    if (isDueToday) return "Due today";
    if (isDueTomorrow) return "Due tomorrow";
    return format(date, "MMM d, yyyy");
  };

  return (
    <Card 
      className={`transition-all duration-200 hover:shadow-md ${
        draggable ? 'cursor-move' : ''
      } ${isOverdue ? 'border-l-4 border-l-red-500' : ''} ${
        task.priority === 'urgent' ? 'border-l-4 border-l-red-400' : ''
      }`}
      data-testid={`task-card-${task.id}`}
    >
      <CardHeader className="pb-2">
        <div className="flex items-start justify-between space-x-2">
          <div className="flex items-start space-x-2 flex-1">
            <div className={`p-1.5 rounded-lg ${taskTypeColors[task.type]}`}>
              <TaskIcon className="h-3 w-3" />
            </div>
            <div className="flex-1 min-w-0">
              <h3 
                className="font-semibold text-sm leading-tight truncate cursor-pointer hover:text-primary"
                onClick={() => setIsExpanded(!isExpanded)}
                data-testid={`task-title-${task.id}`}
              >
                {task.title}
              </h3>
              {!compact && task.description && (
                <p 
                  className={`text-xs text-muted-foreground mt-1 ${
                    isExpanded ? '' : 'line-clamp-2'
                  }`}
                  data-testid={`task-description-${task.id}`}
                >
                  {task.description}
                </p>
              )}
            </div>
          </div>

          <div className="flex items-center space-x-1">
            <Badge 
              variant="secondary" 
              className={`text-xs ${priorityColors[task.priority]}`}
              data-testid={`task-priority-${task.id}`}
            >
              {task.priority}
            </Badge>

            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                  <MoreVertical className="h-3 w-3" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                {task.status === 'pending' && (
                  <DropdownMenuItem 
                    onClick={() => handleStatusChange('in_progress')}
                    data-testid={`task-start-${task.id}`}
                  >
                    <Play className="h-4 w-4 mr-2" />
                    Start Task
                  </DropdownMenuItem>
                )}

                {task.status === 'in_progress' && (
                  <>
                    <DropdownMenuItem 
                      onClick={() => handleStatusChange('completed')}
                      data-testid={`task-complete-${task.id}`}
                    >
                      <CheckCircle className="h-4 w-4 mr-2" />
                      Complete Task
                    </DropdownMenuItem>
                    <DropdownMenuItem 
                      onClick={() => handleStatusChange('pending')}
                      data-testid={`task-pause-${task.id}`}
                    >
                      <Pause className="h-4 w-4 mr-2" />
                      Pause Task
                    </DropdownMenuItem>
                  </>
                )}

                {(task.status === 'pending' || task.status === 'in_progress') && (
                  <DropdownMenuItem 
                    onClick={() => handleStatusChange('cancelled')}
                    data-testid={`task-cancel-${task.id}`}
                  >
                    <AlertTriangle className="h-4 w-4 mr-2" />
                    Cancel Task
                  </DropdownMenuItem>
                )}

                <DropdownMenuSeparator />

                {onEdit && (
                  <DropdownMenuItem 
                    onClick={() => onEdit(task)}
                    data-testid={`task-edit-${task.id}`}
                  >
                    <Edit className="h-4 w-4 mr-2" />
                    Edit
                  </DropdownMenuItem>
                )}

                {onDelete && (
                  <DropdownMenuItem 
                    onClick={() => onDelete(task.id)}
                    className="text-red-600 dark:text-red-400"
                    data-testid={`task-delete-${task.id}`}
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete
                  </DropdownMenuItem>
                )}
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        <div className="space-y-3">
          {/* Progress Bar */}
          <div className="space-y-1">
            <div className="flex items-center justify-between text-xs">
              <Badge 
                variant="secondary" 
                className={statusColors[task.status]}
                data-testid={`task-status-${task.id}`}
              >
                {task.status.replace('_', ' ').toUpperCase()}
              </Badge>
              <span className="text-muted-foreground">
                {getProgressPercentage()}%
              </span>
            </div>
            <Progress value={getProgressPercentage()} className="h-1" />
          </div>

          {/* Task Info */}
          <div className="space-y-2">
            {/* Assignee & Due Date */}
            <div className="flex items-center justify-between text-xs">
              {showAssignee && task.assignedToUser && (
                <div className="flex items-center space-x-1">
                  <Avatar className="h-4 w-4">
                    <AvatarFallback className="text-[8px]">
                      {task.assignedToUser.firstName[0]}{task.assignedToUser.lastName[0]}
                    </AvatarFallback>
                  </Avatar>
                  <span 
                    className="text-muted-foreground truncate max-w-[80px]"
                    data-testid={`task-assignee-${task.id}`}
                  >
                    {task.assignedToUser.firstName} {task.assignedToUser.lastName}
                  </span>
                </div>
              )}

              {task.dueDate && (
                <div className={`flex items-center space-x-1 ${getDueDateColor()}`}>
                  <Calendar className="h-3 w-3" />
                  <span data-testid={`task-due-date-${task.id}`}>
                    {getDueDateText()}
                  </span>
                  {isOverdue && <AlertTriangle className="h-3 w-3" />}
                </div>
              )}
            </div>

            {/* Estimated Hours */}
            {task.estimatedHours && (
              <div className="flex items-center space-x-1 text-xs text-muted-foreground">
                <Timer className="h-3 w-3" />
                <span data-testid={`task-estimated-hours-${task.id}`}>
                  {task.estimatedHours}h estimated
                </span>
              </div>
            )}

            {/* Related entities */}
            <div className="flex items-center space-x-2 text-xs">
              {task.lead && (
                <Tooltip>
                  <TooltipTrigger>
                    <Badge variant="outline" className="text-[10px] px-1.5 py-0.5">
                      <UserIcon className="h-2.5 w-2.5 mr-1" />
                      Lead
                    </Badge>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>{task.lead.firstName} {task.lead.lastName}</p>
                  </TooltipContent>
                </Tooltip>
              )}

              {task.fieldVisit && (
                <Tooltip>
                  <TooltipTrigger>
                    <Badge variant="outline" className="text-[10px] px-1.5 py-0.5">
                      <MapPin className="h-2.5 w-2.5 mr-1" />
                      Visit
                    </Badge>
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>{task.fieldVisit.visitNumber}</p>
                  </TooltipContent>
                </Tooltip>
              )}
            </div>

            {/* Tags */}
            {task.tags && task.tags.length > 0 && (
              <div className="flex flex-wrap gap-1">
                {task.tags.slice(0, compact ? 2 : 3).map((tag, index) => (
                  <Badge 
                    key={index} 
                    variant="outline" 
                    className="text-[9px] px-1 py-0"
                    data-testid={`task-tag-${task.id}-${index}`}
                  >
                    {tag}
                  </Badge>
                ))}
                {task.tags.length > (compact ? 2 : 3) && (
                  <Badge variant="outline" className="text-[9px] px-1 py-0">
                    +{task.tags.length - (compact ? 2 : 3)}
                  </Badge>
                )}
              </div>
            )}

            {/* Recurring indicator */}
            {task.isRecurring && (
              <div className="flex items-center space-x-1 text-xs text-blue-600 dark:text-blue-400">
                <TrendingUp className="h-3 w-3" />
                <span>Recurring {task.recurringFrequency}</span>
              </div>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/marketing/TaskForm.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { 
  X, 
  Calendar, 
  User as UserIcon, 
  Clock, 
  Target, 
  FileText, 
  Tag, 
  AlertCircle,
  Users,
  MapPin,
  Phone,
  Mail,
  Presentation,
  Search,
  TrendingUp
} from "lucide-react";
import { format } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar as CalendarComponent } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";

import type { MarketingTask, User, Lead, FieldVisit } from "@shared/schema";

const taskFormSchema = z.object({
  title: z.string().min(1, "Task title is required"),
  description: z.string().optional(),
  type: z.enum(['visit_client', 'follow_up', 'demo', 'presentation', 'proposal', 'phone_call', 'email_campaign', 'market_research', 'other']),
  assignedTo: z.string().min(1, "Assignee is required"),
  priority: z.enum(['low', 'medium', 'high', 'urgent']).default('medium'),
  dueDate: z.date().optional(),
  estimatedHours: z.string().optional(),
  leadId: z.string().optional(),
  fieldVisitId: z.string().optional(),
  tags: z.array(z.string()).optional(),
  isRecurring: z.boolean().default(false),
  recurringFrequency: z.enum(['daily', 'weekly', 'monthly']).optional()
});

type TaskFormData = z.infer<typeof taskFormSchema>;

interface TaskWithDetails extends MarketingTask {
  assignedToUser?: User;
  lead?: Lead;
  fieldVisit?: FieldVisit;
}

interface TaskFormProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  taskId?: string;
  defaultValues?: Partial<TaskFormData>;
  leadId?: string;
  fieldVisitId?: string;
}

const taskTypes = {
  visit_client: { 
    label: 'Client Visit', 
    icon: Users, 
    color: 'bg-blue-100 text-blue-800',
    description: 'Schedule or conduct client meetings'
  },
  follow_up: { 
    label: 'Follow Up', 
    icon: Phone, 
    color: 'bg-green-100 text-green-800',
    description: 'Follow up on leads or existing clients'
  },
  demo: { 
    label: 'Product Demo', 
    icon: Presentation, 
    color: 'bg-purple-100 text-purple-800',
    description: 'Conduct product demonstrations'
  },
  presentation: { 
    label: 'Presentation', 
    icon: FileText, 
    color: 'bg-orange-100 text-orange-800',
    description: 'Prepare or deliver presentations'
  },
  proposal: { 
    label: 'Proposal', 
    icon: Target, 
    color: 'bg-red-100 text-red-800',
    description: 'Create and submit proposals'
  },
  phone_call: { 
    label: 'Phone Call', 
    icon: Phone, 
    color: 'bg-cyan-100 text-cyan-800',
    description: 'Make important phone calls'
  },
  email_campaign: { 
    label: 'Email Campaign', 
    icon: Mail, 
    color: 'bg-pink-100 text-pink-800',
    description: 'Plan and execute email campaigns'
  },
  market_research: { 
    label: 'Market Research', 
    icon: Search, 
    color: 'bg-indigo-100 text-indigo-800',
    description: 'Conduct market analysis and research'
  },
  other: { 
    label: 'Other', 
    icon: Tag, 
    color: 'bg-gray-100 text-gray-800',
    description: 'Custom task type'
  }
};

export default function TaskForm({ 
  open, 
  onOpenChange, 
  taskId, 
  defaultValues, 
  leadId,
  fieldVisitId 
}: TaskFormProps) {
  const [activeTab, setActiveTab] = useState("basic");
  const [tagInput, setTagInput] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch users for assignment dropdown
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['/api/users'],
    enabled: open
  });

  // Fetch leads for linking
  const { data: leads = [] } = useQuery<Lead[]>({
    queryKey: ['/api/marketing/leads '],
    enabled: open
  });

  // Fetch field visits for linking
  const { data: fieldVisits = [] } = useQuery<FieldVisit[]>({
    queryKey: ['/api/field-visits'],
    enabled: open
  });

  // Fetch existing task data if editing
  const { data: existingTask } = useQuery<TaskWithDetails>({
    queryKey: ['/api/marketing-tasks', taskId],
    enabled: !!taskId && open
  });

  const form = useForm<TaskFormData>({
    resolver: zodResolver(taskFormSchema),
    defaultValues: {
      type: "follow_up",
      priority: "medium",
      isRecurring: false,
      tags: [],
      leadId: leadId || "",
      fieldVisitId: fieldVisitId || "",
      ...defaultValues
    }
  });

  useEffect(() => {
    if (existingTask) {
      form.reset({
        title: existingTask.title,
        description: existingTask.description || "",
        type: existingTask.type,
        assignedTo: existingTask.assignedTo,
        priority: existingTask.priority,
        dueDate: existingTask.dueDate ? new Date(existingTask.dueDate) : undefined,
        estimatedHours: existingTask.estimatedHours?.toString() || "",
        leadId: existingTask.leadId || "",
        fieldVisitId: existingTask.fieldVisitId || "",
        tags: existingTask.tags || [],
        isRecurring: existingTask.isRecurring || false,
        recurringFrequency: existingTask.recurringFrequency as any
      });
    }
  }, [existingTask, form]);

  const createMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/marketing-tasks', { method: 'POST', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task created successfully!" });
      onOpenChange(false);
      form.reset();
    },
    onError: (error: any) => {
      toast({ 
        title: "Error creating task", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const updateMutation = useMutation({
    mutationFn: (data: any) => apiRequest(`/api/marketing-tasks/${taskId}`, { method: 'PUT', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks', taskId] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task updated successfully!" });
      onOpenChange(false);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating task", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const onSubmit = (data: TaskFormData) => {
    const submitData = {
      ...data,
      estimatedHours: data.estimatedHours ? parseFloat(data.estimatedHours) : undefined,
      tags: data.tags || [],
      leadId: data.leadId && data.leadId !== 'none' ? data.leadId : undefined,
      fieldVisitId: data.fieldVisitId && data.fieldVisitId !== 'none' ? data.fieldVisitId : undefined,
      recurringFrequency: data.isRecurring ? data.recurringFrequency : undefined
    };

    if (taskId) {
      updateMutation.mutate(submitData);
    } else {
      createMutation.mutate(submitData);
    }
  };

  const isPending = createMutation.isPending || updateMutation.isPending;

  const handleAddTag = () => {
    if (tagInput.trim() && !form.getValues("tags")?.includes(tagInput.trim())) {
      const currentTags = form.getValues("tags") || [];
      form.setValue("tags", [...currentTags, tagInput.trim()]);
      setTagInput("");
    }
  };

  const removeTag = (tagToRemove: string) => {
    const currentTags = form.getValues("tags") || [];
    form.setValue("tags", currentTags.filter(tag => tag !== tagToRemove));
  };

  // Get assignee workload info
  const getAssigneeWorkload = (userId: string) => {
    // This would ideally come from a metrics API
    return {
      currentTasks: Math.floor(Math.random() * 15) + 1,
      capacity: 20,
      utilizationRate: Math.floor(Math.random() * 100)
    };
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Target className="h-5 w-5" />
            <span>{taskId ? 'Edit Task' : 'Create New Task'}</span>
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <Tabs value={activeTab} onValueChange={setActiveTab}>
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="basic" data-testid="tab-basic">Basic Info</TabsTrigger>
                <TabsTrigger value="assignment" data-testid="tab-assignment">Assignment</TabsTrigger>
                <TabsTrigger value="details" data-testid="tab-details">Details</TabsTrigger>
                <TabsTrigger value="advanced" data-testid="tab-advanced">Advanced</TabsTrigger>
              </TabsList>

              <TabsContent value="basic" className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Task Title *</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Enter task title..." 
                            {...field} 
                            data-testid="input-title" 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="type"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Task Type *</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-type">
                              <SelectValue placeholder="Select task type" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {Object.entries(taskTypes).map(([key, type]) => {
                              const Icon = type.icon;
                              return (
                                <SelectItem key={key} value={key}>
                                  <div className="flex items-center space-x-2">
                                    <Icon className="h-4 w-4" />
                                    <span>{type.label}</span>
                                  </div>
                                </SelectItem>
                              );
                            })}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea 
                          placeholder="Describe the task details..."
                          className="min-h-[100px]" 
                          {...field}
                          data-testid="textarea-description"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="priority"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Priority</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-priority">
                              <SelectValue placeholder="Select priority" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="low">
                              <div className="flex items-center space-x-2">
                                <Badge variant="secondary" className="w-2 h-2 p-0 rounded-full bg-gray-400"></Badge>
                                <span>Low Priority</span>
                              </div>
                            </SelectItem>
                            <SelectItem value="medium">
                              <div className="flex items-center space-x-2">
                                <Badge variant="secondary" className="w-2 h-2 p-0 rounded-full bg-yellow-500"></Badge>
                                <span>Medium Priority</span>
                              </div>
                            </SelectItem>
                            <SelectItem value="high">
                              <div className="flex items-center space-x-2">
                                <Badge variant="secondary" className="w-2 h-2 p-0 rounded-full bg-red-500"></Badge>
                                <span>High Priority</span>
                              </div>
                            </SelectItem>
                            <SelectItem value="urgent">
                              <div className="flex items-center space-x-2">
                                <Badge variant="destructive" className="w-2 h-2 p-0 rounded-full"></Badge>
                                <span>Urgent</span>
                              </div>
                            </SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="estimatedHours"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center space-x-2">
                          <Clock className="h-4 w-4" />
                          <span>Estimated Hours</span>
                        </FormLabel>
                        <FormControl>
                          <Input 
                            type="number" 
                            step="0.5" 
                            placeholder="2.5" 
                            {...field}
                            data-testid="input-estimated-hours"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
              </TabsContent>

              <TabsContent value="assignment" className="space-y-4">
                <FormField
                  control={form.control}
                  name="assignedTo"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <UserIcon className="h-4 w-4" />
                        <span>Assign To *</span>
                      </FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-assignee">
                            <SelectValue placeholder="Select team member" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {users.filter(user => user.id && user.id.trim() !== "").map((user) => {
                            const workload = getAssigneeWorkload(user.id);
                            return (
                              <SelectItem key={user.id} value={user.id}>
                                <div className="flex items-center justify-between w-full">
                                  <div className="flex flex-col">
                                    <span>{user.firstName} {user.lastName}</span>
                                    <span className="text-xs text-muted-foreground">
                                      {workload.currentTasks}/{workload.capacity} tasks
                                    </span>
                                  </div>
                                  <Badge 
                                    variant={workload.utilizationRate > 80 ? "destructive" : 
                                           workload.utilizationRate > 60 ? "default" : "secondary"}
                                    className="ml-2"
                                  >
                                    {workload.utilizationRate}%
                                  </Badge>
                                </div>
                              </SelectItem>
                            );
                          })}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="dueDate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <Calendar className="h-4 w-4" />
                        <span>Due Date</span>
                      </FormLabel>
                      <Popover>
                        <PopoverTrigger asChild>
                          <FormControl>
                            <Button
                              variant="outline"
                              className={`w-full pl-3 text-left font-normal ${
                                !field.value && "text-muted-foreground"
                              }`}
                              data-testid="button-due-date"
                            >
                              {field.value ? (
                                format(field.value, "PPP")
                              ) : (
                                <span>Pick a due date</span>
                              )}
                              <Calendar className="ml-auto h-4 w-4 opacity-50" />
                            </Button>
                          </FormControl>
                        </PopoverTrigger>
                        <PopoverContent className="w-auto p-0" align="start">
                          <CalendarComponent
                            mode="single"
                            selected={field.value}
                            onSelect={field.onChange}
                            disabled={(date) =>
                              date < new Date(new Date().setHours(0, 0, 0, 0))
                            }
                            initialFocus
                          />
                        </PopoverContent>
                      </Popover>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              <TabsContent value="details" className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="leadId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Related Lead</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value || "none"}>
                          <FormControl>
                            <SelectTrigger data-testid="select-lead">
                              <SelectValue placeholder="Select lead (optional)" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="none">No Lead Selected</SelectItem>
                            {leads.filter(lead => lead.id && lead.id.trim() !== "").map((lead) => (
                              <SelectItem key={lead.id} value={lead.id}>
                                {lead.firstName} {lead.lastName} 
                                {lead.companyName && ` - ${lead.companyName}`}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="fieldVisitId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Related Field Visit</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value || "none"}>
                          <FormControl>
                            <SelectTrigger data-testid="select-field-visit">
                              <SelectValue placeholder="Select visit (optional)" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="none">No Visit Selected</SelectItem>
                            {fieldVisits.filter(visit => visit.id && visit.id.trim() !== "").map((visit) => (
                              <SelectItem key={visit.id} value={visit.id}>
                                {visit.visitNumber} - {format(new Date(visit.plannedDate), "PPP")}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="space-y-2">
                  <FormLabel className="flex items-center space-x-2">
                    <Tag className="h-4 w-4" />
                    <span>Tags</span>
                  </FormLabel>
                  <div className="flex space-x-2">
                    <Input
                      value={tagInput}
                      onChange={(e) => setTagInput(e.target.value)}
                      placeholder="Add tag..."
                      onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddTag())}
                      data-testid="input-tag"
                    />
                    <Button 
                      type="button" 
                      variant="outline" 
                      onClick={handleAddTag}
                      data-testid="button-add-tag"
                    >
                      Add
                    </Button>
                  </div>
                  {form.watch("tags") && form.watch("tags")!.length > 0 && (
                    <div className="flex flex-wrap gap-2 mt-2">
                      {form.watch("tags")!.map((tag, index) => (
                        <Badge key={index} variant="secondary" className="cursor-pointer">
                          {tag}
                          <X 
                            className="h-3 w-3 ml-1" 
                            onClick={() => removeTag(tag)}
                          />
                        </Badge>
                      ))}
                    </div>
                  )}
                </div>
              </TabsContent>

              <TabsContent value="advanced" className="space-y-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="text-base flex items-center space-x-2">
                      <TrendingUp className="h-4 w-4" />
                      <span>Recurring Task</span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <FormField
                      control={form.control}
                      name="isRecurring"
                      render={({ field }) => (
                        <FormItem className="flex items-center justify-between">
                          <div className="space-y-0.5">
                            <FormLabel>Make this a recurring task</FormLabel>
                            <div className="text-sm text-muted-foreground">
                              Task will be automatically created at specified intervals
                            </div>
                          </div>
                          <FormControl>
                            <Switch
                              checked={field.value}
                              onCheckedChange={field.onChange}
                              data-testid="switch-recurring"
                            />
                          </FormControl>
                        </FormItem>
                      )}
                    />

                    {form.watch("isRecurring") && (
                      <FormField
                        control={form.control}
                        name="recurringFrequency"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Frequency</FormLabel>
                            <Select onValueChange={field.onChange} value={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-frequency">
                                  <SelectValue placeholder="Select frequency" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="daily">Daily</SelectItem>
                                <SelectItem value="weekly">Weekly</SelectItem>
                                <SelectItem value="monthly">Monthly</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    )}
                  </CardContent>
                </Card>

                {/* Task Type Information */}
                {form.watch("type") && (
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-base">Task Type Information</CardTitle>
                    </CardHeader>
                    <CardContent>
                      {(() => {
                        const selectedType = taskTypes[form.watch("type") as keyof typeof taskTypes];
                        const Icon = selectedType?.icon;
                        return (
                          <div className="flex items-start space-x-3">
                            {Icon && (
                              <div className={`p-2 rounded-lg ${selectedType.color}`}>
                                <Icon className="h-4 w-4" />
                              </div>
                            )}
                            <div>
                              <div className="font-light">{selectedType?.label}</div>
                              <div className="text-sm text-muted-foreground">
                                {selectedType?.description}
                              </div>
                            </div>
                          </div>
                        );
                      })()}
                    </CardContent>
                  </Card>
                )}
              </TabsContent>
            </Tabs>

            <Separator />

            <div className="flex justify-end space-x-2">
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={isPending}
                data-testid="button-submit"
              >
                {isPending ? "Saving..." : taskId ? "Update Task" : "Create Task"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

