import { useQuery } from "@tanstack/react-query";
import { Clock, CheckCircle, AlertCircle, TrendingUp, Users, Calendar, Target, Zap } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";

interface TaskMetricsData {
  totalTasks: number;
  pendingTasks: number;
  inProgressTasks: number;
  completedTasks: number;
  overdueTasks: number;
  todaysDue: number;
  completionRate: number;
  averageCompletionTime: number;
  tasksByPriority: {
    high: number;
    medium: number;
    low: number;
  };
  tasksByType: Record<string, number>;
  topPerformers: Array<{
    user: { id: string; firstName: string; lastName: string };
    completedTasks: number;
  }>;
}

export default function TaskMetrics() {
  const { data: metrics, isLoading } = useQuery<TaskMetricsData>({
    queryKey: ['/api/marketing-tasks/metrics']
  });

  if (isLoading || !metrics) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
        {[1, 2, 3, 4].map((i) => (
          <Card key={i} className="animate-pulse">
            <CardHeader className="space-y-0 pb-2">
              <div className="h-4 bg-muted rounded w-3/4"></div>
            </CardHeader>
            <CardContent>
              <div className="h-8 bg-muted rounded w-1/2 mb-2"></div>
              <div className="h-3 bg-muted rounded w-full"></div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  const metricCards = [
    {
      title: "Total Tasks",
      value: metrics.totalTasks,
      icon: Target,
      change: "+12% from last month",
      color: "text-blue-600",
      bgColor: "bg-blue-50 dark:bg-blue-950"
    },
    {
      title: "Pending Tasks",
      value: metrics.pendingTasks,
      icon: Clock,
      change: `${metrics.overdueTasks} overdue`,
      color: "text-orange-600",
      bgColor: "bg-orange-50 dark:bg-orange-950"
    },
    {
      title: "In Progress",
      value: metrics.inProgressTasks,
      icon: Zap,
      change: `${metrics.todaysDue} due today`,
      color: "text-yellow-600",
      bgColor: "bg-yellow-50 dark:bg-yellow-950"
    },
    {
      title: "Completed",
      value: metrics.completedTasks,
      icon: CheckCircle,
      change: `${metrics.completionRate.toFixed(1)}% completion rate`,
      color: "text-green-600",
      bgColor: "bg-green-50 dark:bg-green-950"
    }
  ];

  return (
    <div className="space-y-6 mb-6">
      {/* Main Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {metricCards.map((metric) => {
          const Icon = metric.icon;
          return (
            <Card key={metric.title} className="transition-all hover:shadow-md">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-light text-muted-foreground">
                  {metric.title}
                </CardTitle>
                <div className={`p-2 rounded-full ${metric.bgColor}`}>
                  <Icon className={`h-4 w-4 ${metric.color}`} />
                </div>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid={`metric-${metric.title.toLowerCase().replace(' ', '-')}`}>
                  {metric.value}
                </div>
                <p className="text-xs text-muted-foreground mt-1">
                  {metric.change}
                </p>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Secondary Metrics */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Priority Distribution */}
        <Card>
          <CardHeader>
            <CardTitle className="text-base flex items-center space-x-2">
              <AlertCircle className="h-4 w-4" />
              <span>Priority Distribution</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="flex justify-between items-center">
              <div className="flex items-center space-x-2">
                <Badge variant="destructive" className="w-2 h-2 p-0 rounded-full"></Badge>
                <span className="text-sm">High Priority</span>
              </div>
              <span className="text-sm font-light" data-testid="metric-high-priority">
                {metrics.tasksByPriority.high}
              </span>
            </div>
            <Progress 
              value={(metrics.tasksByPriority.high / metrics.totalTasks) * 100} 
              className="h-2"
            />

            <div className="flex justify-between items-center">
              <div className="flex items-center space-x-2">
                <Badge variant="default" className="w-2 h-2 p-0 rounded-full bg-yellow-500"></Badge>
                <span className="text-sm">Medium Priority</span>
              </div>
              <span className="text-sm font-light" data-testid="metric-medium-priority">
                {metrics.tasksByPriority.medium}
              </span>
            </div>
            <Progress 
              value={(metrics.tasksByPriority.medium / metrics.totalTasks) * 100} 
              className="h-2"
            />

            <div className="flex justify-between items-center">
              <div className="flex items-center space-x-2">
                <Badge variant="secondary" className="w-2 h-2 p-0 rounded-full"></Badge>
                <span className="text-sm">Low Priority</span>
              </div>
              <span className="text-sm font-light" data-testid="metric-low-priority">
                {metrics.tasksByPriority.low}
              </span>
            </div>
            <Progress 
              value={(metrics.tasksByPriority.low / metrics.totalTasks) * 100} 
              className="h-2"
            />
          </CardContent>
        </Card>

        {/* Top Performers */}
        <Card>
          <CardHeader>
            <CardTitle className="text-base flex items-center space-x-2">
              <TrendingUp className="h-4 w-4" />
              <span>Top Performers</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            {metrics.topPerformers.slice(0, 3).map((performer, index) => (
              <div key={performer.user.id} className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <div className={`w-6 h-6 rounded-full flex items-center justify-center text-xs font-semibold ${
                    index === 0 ? 'bg-yellow-500 text-white' : 
                    index === 1 ? 'bg-gray-400 text-white' : 
                    'bg-orange-600 text-white'
                  }`}>
                    {index + 1}
                  </div>
                  <span className="text-sm font-light">
                    {performer.user.firstName} {performer.user.lastName}
                  </span>
                </div>
                <Badge variant="secondary" data-testid={`performer-${index}-tasks`}>
                  {performer.completedTasks} tasks
                </Badge>
              </div>
            ))}
          </CardContent>
        </Card>

        {/* Task Overview */}
        <Card>
          <CardHeader>
            <CardTitle className="text-base flex items-center space-x-2">
              <Calendar className="h-4 w-4" />
              <span>This Week Overview</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Completion Rate</span>
                <span className="font-light" data-testid="completion-rate">
                  {metrics.completionRate.toFixed(1)}%
                </span>
              </div>
              <Progress value={metrics.completionRate} className="h-2" />
            </div>

            <div className="flex justify-between items-center py-1 border-t">
              <span className="text-sm text-muted-foreground">Avg. Completion</span>
              <span className="text-sm font-light" data-testid="avg-completion-time">
                {metrics.averageCompletionTime.toFixed(1)}h
              </span>
            </div>

            <div className="flex justify-between items-center py-1">
              <span className="text-sm text-muted-foreground">Due Today</span>
              <Badge 
                variant={metrics.todaysDue > 0 ? "default" : "secondary"}
                data-testid="tasks-due-today"
              >
                {metrics.todaysDue}
              </Badge>
            </div>

            <div className="flex justify-between items-center py-1">
              <span className="text-sm text-muted-foreground">Overdue</span>
              <Badge 
                variant={metrics.overdueTasks > 0 ? "destructive" : "secondary"}
                data-testid="overdue-tasks"
              >
                {metrics.overdueTasks}
              </Badge>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/marketing/TaskTabl.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  MoreHorizontal, 
  ArrowUpDown, 
  Filter,
  Edit,
  CheckCircle,
  Play,
  Trash2,
  Calendar,
  User as UserIcon,
  Clock,
  AlertTriangle,
  Eye,
  Copy
} from "lucide-react";
import { format, isPast, isToday, isTomorrow } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Progress } from "@/components/ui/progress";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";

import type { MarketingTask, User, Lead, FieldVisit } from "@shared/schema";

interface TaskWithDetails extends MarketingTask {
  assignedToUser?: User;
  assignedByUser?: User;
  lead?: Lead;
  fieldVisit?: FieldVisit;
}

interface TaskTableProps {
  tasks: TaskWithDetails[];
  onEdit?: (task: TaskWithDetails) => void;
  onDelete?: (taskId: string) => void;
  onViewDetails?: (task: TaskWithDetails) => void;
  loading?: boolean;
}

type SortField = 'title' | 'priority' | 'status' | 'dueDate' | 'assignedTo' | 'createdAt';
type SortOrder = 'asc' | 'desc';

const priorityOrder = { low: 0, medium: 1, high: 2, urgent: 3 };
const statusOrder = { pending: 0, in_progress: 1, completed: 2, cancelled: 3 };

const taskTypeLabels = {
  visit_client: 'Client Visit',
  follow_up: 'Follow Up',
  demo: 'Product Demo',
  presentation: 'Presentation',
  proposal: 'Proposal',
  phone_call: 'Phone Call',
  email_campaign: 'Email Campaign',
  market_research: 'Market Research',
  other: 'Other'
};

const priorityColors = {
  low: "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200",
  medium: "bg-yellow-100 text-yellow-800 dark:bg-yellow-800 dark:text-yellow-200",
  high: "bg-orange-100 text-orange-800 dark:bg-orange-800 dark:text-orange-200",
  urgent: "bg-red-100 text-red-800 dark:bg-red-800 dark:text-red-200"
};

const statusColors = {
  pending: "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200",
  in_progress: "bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-200",
  completed: "bg-green-100 text-green-800 dark:bg-green-800 dark:text-green-200",
  cancelled: "bg-red-100 text-red-800 dark:bg-red-800 dark:text-red-200"
};

export default function TaskTable({ 
  tasks, 
  onEdit, 
  onDelete, 
  onViewDetails, 
  loading = false 
}: TaskTableProps) {
  const [selectedTasks, setSelectedTasks] = useState<Set<string>>(new Set());
  const [sortField, setSortField] = useState<SortField>('createdAt');
  const [sortOrder, setSortOrder] = useState<SortOrder>('desc');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateStatusMutation = useMutation({
    mutationFn: ({ taskId, status }: { taskId: string; status: string }) => 
      apiRequest(`/api/marketing-tasks/${taskId}/status`, { 
        method: 'PUT', 
        body: JSON.stringify({ status }) 
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task status updated successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error updating task status", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const completeTaskMutation = useMutation({
    mutationFn: (taskId: string) => 
      apiRequest(`/api/marketing-tasks/${taskId}/complete`, { method: 'POST' }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-tasks/metrics'] });
      toast({ title: "Task completed successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Error completing task", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Sorting logic
  const sortedTasks = [...tasks].sort((a, b) => {
    let comparison = 0;

    switch (sortField) {
      case 'title':
        comparison = a.title.localeCompare(b.title);
        break;
      case 'priority':
        comparison = priorityOrder[a.priority] - priorityOrder[b.priority];
        break;
      case 'status':
        comparison = statusOrder[a.status] - statusOrder[b.status];
        break;
      case 'dueDate':
        const dateA = a.dueDate ? new Date(a.dueDate).getTime() : Infinity;
        const dateB = b.dueDate ? new Date(b.dueDate).getTime() : Infinity;
        comparison = dateA - dateB;
        break;
      case 'assignedTo':
        const nameA = a.assignedToUser ? `${a.assignedToUser.firstName} ${a.assignedToUser.lastName}` : '';
        const nameB = b.assignedToUser ? `${b.assignedToUser.firstName} ${b.assignedToUser.lastName}` : '';
        comparison = nameA.localeCompare(nameB);
        break;
      case 'createdAt':
        comparison = new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime();
        break;
      default:
        comparison = 0;
    }

    return sortOrder === 'asc' ? comparison : -comparison;
  });

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortOrder('asc');
    }
  };

  const handleSelectTask = (taskId: string, checked: boolean) => {
    const newSelection = new Set(selectedTasks);
    if (checked) {
      newSelection.add(taskId);
    } else {
      newSelection.delete(taskId);
    }
    setSelectedTasks(newSelection);
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedTasks(new Set(tasks.map(task => task.id)));
    } else {
      setSelectedTasks(new Set());
    }
  };

  const handleStatusChange = (taskId: string, status: string) => {
    if (status === 'completed') {
      completeTaskMutation.mutate(taskId);
    } else {
      updateStatusMutation.mutate({ taskId, status });
    }
  };

  const getProgressPercentage = (status: string) => {
    switch (status) {
      case 'completed':
        return 100;
      case 'in_progress':
        return 60;
      case 'pending':
        return 0;
      case 'cancelled':
        return 0;
      default:
        return 0;
    }
  };

  const getDueDateInfo = (dueDate: string | null) => {
    if (!dueDate) return null;

    const date = new Date(dueDate);
    const isOverdue = isPast(date);
    const isDueToday = isToday(date);
    const isDueTomorrow = isTomorrow(date);

    let color = "text-muted-foreground";
    let text = format(date, "MMM d");
    let icon = null;

    if (isOverdue) {
      color = "text-red-600 dark:text-red-400";
      text = "Overdue";
      icon = <AlertTriangle className="h-3 w-3" />;
    } else if (isDueToday) {
      color = "text-orange-600 dark:text-orange-400";
      text = "Today";
    } else if (isDueTomorrow) {
      color = "text-yellow-600 dark:text-yellow-400";
      text = "Tomorrow";
    }

    return { color, text, icon, isOverdue };
  };

  const SortableHeader = ({ field, children }: { field: SortField; children: React.ReactNode }) => (
    <Button
      variant="ghost"
      size="sm"
      className="h-8 p-0 font-light hover:bg-transparent"
      onClick={() => handleSort(field)}
      data-testid={`sort-${field}`}
    >
      {children}
      <ArrowUpDown className="ml-2 h-3 w-3" />
    </Button>
  );

  if (loading) {
    return (
      <div className="border">
        <div className="p-4">
          <div className="space-y-3">
            {[1, 2, 3, 4, 5].map((i) => (
              <div key={i} className="flex space-x-4 animate-pulse">
                <div className="w-4 h-4 bg-muted rounded"></div>
                <div className="w-32 h-4 bg-muted rounded"></div>
                <div className="w-20 h-4 bg-muted rounded"></div>
                <div className="w-24 h-4 bg-muted rounded"></div>
                <div className="w-28 h-4 bg-muted rounded"></div>
                <div className="w-20 h-4 bg-muted rounded"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Bulk Actions Bar */}
      {selectedTasks.size > 0 && (
        <div className="flex items-center space-x-2 p-3 bg-muted/50 rounded-lg">
          <span className="text-sm font-light">
            {selectedTasks.size} task(s) selected
          </span>
          <Button
            size="sm"
            variant="outline"
            onClick={() => {
              selectedTasks.forEach(taskId => {
                const task = tasks.find(t => t.id === taskId);
                if (task && task.status === 'pending') {
                  handleStatusChange(taskId, 'in_progress');
                }
              });
            }}
            data-testid="bulk-start"
          >
            <Play className="h-4 w-4 mr-2" />
            Start Selected
          </Button>
          <Button
            size="sm"
            variant="outline"
            onClick={() => {
              selectedTasks.forEach(taskId => {
                const task = tasks.find(t => t.id === taskId);
                if (task && task.status === 'in_progress') {
                  handleStatusChange(taskId, 'completed');
                }
              });
            }}
            data-testid="bulk-complete"
          >
            <CheckCircle className="h-4 w-4 mr-2" />
            Complete Selected
          </Button>
          <Button
            size="sm"
            variant="outline"
            onClick={() => setSelectedTasks(new Set())}
            data-testid="bulk-clear"
          >
            Clear Selection
          </Button>
        </div>
      )}

      {/* Tasks Table */}
      <div className="border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead className="w-12">
                <Checkbox
                  checked={selectedTasks.size === tasks.length && tasks.length > 0}
                  onCheckedChange={handleSelectAll}
                  data-testid="select-all-tasks"
                />
              </TableHead>
              <TableHead>
                <SortableHeader field="title">Task</SortableHeader>
              </TableHead>
              <TableHead className="w-32">
                <SortableHeader field="status">Status</SortableHeader>
              </TableHead>
              <TableHead className="w-28">
                <SortableHeader field="priority">Priority</SortableHeader>
              </TableHead>
              <TableHead className="w-40">
                <SortableHeader field="assignedTo">Assignee</SortableHeader>
              </TableHead>
              <TableHead className="w-32">
                <SortableHeader field="dueDate">Due Date</SortableHeader>
              </TableHead>
              <TableHead className="w-24">Progress</TableHead>
              <TableHead className="w-20">Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {sortedTasks.map((task) => {
              const dueDateInfo = getDueDateInfo(task.dueDate);
              const progress = getProgressPercentage(task.status);

              return (
                <TableRow 
                  key={task.id}
                  className={`${selectedTasks.has(task.id) ? 'bg-muted/50' : ''} ${
                    dueDateInfo?.isOverdue && task.status !== 'completed' ? 'border-l-2 border-l-red-500' : ''
                  }`}
                  data-testid={`task-row-${task.id}`}
                >
                  <TableCell>
                    <Checkbox
                      checked={selectedTasks.has(task.id)}
                      onCheckedChange={(checked) => handleSelectTask(task.id, checked as boolean)}
                      data-testid={`select-task-${task.id}`}
                    />
                  </TableCell>

                  <TableCell>
                    <div className="space-y-1">
                      <div 
                        className="font-light text-sm cursor-pointer hover:text-primary line-clamp-1"
                        onClick={() => onViewDetails?.(task)}
                        data-testid={`task-title-${task.id}`}
                      >
                        {task.title}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {taskTypeLabels[task.type]}
                      </div>
                      {task.estimatedHours && (
                        <div className="flex items-center text-xs text-muted-foreground">
                          <Clock className="h-3 w-3 mr-1" />
                          {task.estimatedHours}h
                        </div>
                      )}
                    </div>
                  </TableCell>

                  <TableCell>
                    <Badge 
                      variant="secondary" 
                      className={statusColors[task.status]}
                      data-testid={`task-status-${task.id}`}
                    >
                      {task.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </TableCell>

                  <TableCell>
                    <Badge 
                      variant="outline" 
                      className={priorityColors[task.priority]}
                      data-testid={`task-priority-${task.id}`}
                    >
                      {task.priority.toUpperCase()}
                    </Badge>
                  </TableCell>

                  <TableCell>
                    {task.assignedToUser ? (
                      <div className="flex items-center space-x-2">
                        <Avatar className="h-6 w-6">
                          <AvatarFallback className="text-xs">
                            {task.assignedToUser.firstName[0]}{task.assignedToUser.lastName[0]}
                          </AvatarFallback>
                        </Avatar>
                        <span 
                          className="text-sm truncate"
                          data-testid={`task-assignee-${task.id}`}
                        >
                          {task.assignedToUser.firstName} {task.assignedToUser.lastName}
                        </span>
                      </div>
                    ) : (
                      <span className="text-sm text-muted-foreground">Unassigned</span>
                    )}
                  </TableCell>

                  <TableCell>
                    {dueDateInfo ? (
                      <div className={`flex items-center space-x-1 text-sm ${dueDateInfo.color}`}>
                        <Calendar className="h-3 w-3" />
                        <span data-testid={`task-due-date-${task.id}`}>
                          {dueDateInfo.text}
                        </span>
                        {dueDateInfo.icon}
                      </div>
                    ) : (
                      <span className="text-sm text-muted-foreground">No due date</span>
                    )}
                  </TableCell>

                  <TableCell>
                    <div className="space-y-1">
                      <div className="flex items-center justify-between text-xs">
                        <span>{progress}%</span>
                      </div>
                      <Progress value={progress} className="h-1 w-16" />
                    </div>
                  </TableCell>

                  <TableCell>
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" className="h-8 w-8 p-0">
                          <MoreHorizontal className="h-4 w-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuLabel>Actions</DropdownMenuLabel>

                        {onViewDetails && (
                          <DropdownMenuItem 
                            onClick={() => onViewDetails(task)}
                            data-testid={`task-view-${task.id}`}
                          >
                            <Eye className="h-4 w-4 mr-2" />
                            View Details
                          </DropdownMenuItem>
                        )}

                        {onEdit && (
                          <DropdownMenuItem 
                            onClick={() => onEdit(task)}
                            data-testid={`task-edit-${task.id}`}
                          >
                            <Edit className="h-4 w-4 mr-2" />
                            Edit Task
                          </DropdownMenuItem>
                        )}

                        <DropdownMenuSeparator />

                        {task.status === 'pending' && (
                          <DropdownMenuItem 
                            onClick={() => handleStatusChange(task.id, 'in_progress')}
                            data-testid={`task-start-${task.id}`}
                          >
                            <Play className="h-4 w-4 mr-2" />
                            Start Task
                          </DropdownMenuItem>
                        )}

                        {task.status === 'in_progress' && (
                          <DropdownMenuItem 
                            onClick={() => handleStatusChange(task.id, 'completed')}
                            data-testid={`task-complete-${task.id}`}
                          >
                            <CheckCircle className="h-4 w-4 mr-2" />
                            Complete Task
                          </DropdownMenuItem>
                        )}

                        <DropdownMenuItem 
                          onClick={() => {
                            navigator.clipboard.writeText(task.id);
                            toast({ title: "Task ID copied to clipboard" });
                          }}
                          data-testid={`task-copy-${task.id}`}
                        >
                          <Copy className="h-4 w-4 mr-2" />
                          Copy Task ID
                        </DropdownMenuItem>

                        <DropdownMenuSeparator />

                        {onDelete && (
                          <DropdownMenuItem 
                            onClick={() => onDelete(task.id)}
                            className="text-red-600 dark:text-red-400"
                            data-testid={`task-delete-${task.id}`}
                          >
                            <Trash2 className="h-4 w-4 mr-2" />
                            Delete Task
                          </DropdownMenuItem>
                        )}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>

        {tasks.length === 0 && (
          <div className="text-center py-8">
            <div className="text-muted-foreground">No tasks found</div>
            <div className="text-sm text-muted-foreground mt-1">
              Try adjusting your filters or create a new task
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

