import { useState } from "react";
import { Calendar as CalendarIcon, ChevronDown } from "lucide-react";
import { format } from "date-fns";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Calendar } from "@/components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";

interface DateRange {
  from: Date | null;
  to: Date | null;
}

interface DateRangeSelectorProps {
  dateRange: DateRange;
  onDateRangeChange: (range: DateRange) => void;
  className?: string;
}

type PresetRange = 'today' | 'yesterday' | 'week' | 'last_week' | 'month' | 'last_month' | 'quarter' | 'last_quarter' | 'year' | 'last_year' | 'custom';

const getPresetRange = (preset: PresetRange): DateRange => {
  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());

  switch (preset) {
    case 'today':
      return { from: today, to: today };

    case 'yesterday':
      const yesterday = new Date(today);
      yesterday.setDate(yesterday.getDate() - 1);
      return { from: yesterday, to: yesterday };

    case 'week':
      const startOfWeek = new Date(today);
      startOfWeek.setDate(today.getDate() - today.getDay());
      return { from: startOfWeek, to: today };

    case 'last_week':
      const lastWeekStart = new Date(today);
      lastWeekStart.setDate(today.getDate() - today.getDay() - 7);
      const lastWeekEnd = new Date(lastWeekStart);
      lastWeekEnd.setDate(lastWeekStart.getDate() + 6);
      return { from: lastWeekStart, to: lastWeekEnd };

    case 'month':
      const startOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
      return { from: startOfMonth, to: today };

    case 'last_month':
      const lastMonthStart = new Date(now.getFullYear(), now.getMonth() - 1, 1);
      const lastMonthEnd = new Date(now.getFullYear(), now.getMonth(), 0);
      return { from: lastMonthStart, to: lastMonthEnd };

    case 'quarter':
      const quarter = Math.floor(now.getMonth() / 3);
      const startOfQuarter = new Date(now.getFullYear(), quarter * 3, 1);
      return { from: startOfQuarter, to: today };

    case 'last_quarter':
      const lastQuarter = Math.floor(now.getMonth() / 3) - 1;
      const lastQuarterYear = lastQuarter < 0 ? now.getFullYear() - 1 : now.getFullYear();
      const lastQuarterMonth = lastQuarter < 0 ? 9 : lastQuarter * 3;
      const lastQuarterStart = new Date(lastQuarterYear, lastQuarterMonth, 1);
      const lastQuarterEnd = new Date(lastQuarterYear, lastQuarterMonth + 3, 0);
      return { from: lastQuarterStart, to: lastQuarterEnd };

    case 'year':
      const startOfYear = new Date(now.getFullYear(), 0, 1);
      return { from: startOfYear, to: today };

    case 'last_year':
      const lastYearStart = new Date(now.getFullYear() - 1, 0, 1);
      const lastYearEnd = new Date(now.getFullYear() - 1, 11, 31);
      return { from: lastYearStart, to: lastYearEnd };

    default:
      return { from: null, to: null };
  }
};

const presetOptions = [
  { value: 'today', label: 'Today' },
  { value: 'yesterday', label: 'Yesterday' },
  { value: 'week', label: 'This Week' },
  { value: 'last_week', label: 'Last Week' },
  { value: 'month', label: 'This Month' },
  { value: 'last_month', label: 'Last Month' },
  { value: 'quarter', label: 'This Quarter' },
  { value: 'last_quarter', label: 'Last Quarter' },
  { value: 'year', label: 'This Year' },
  { value: 'last_year', label: 'Last Year' },
  { value: 'custom', label: 'Custom Range' }
];

export default function DateRangeSelector({ 
  dateRange, 
  onDateRangeChange, 
  className 
}: DateRangeSelectorProps) {
  const [selectedPreset, setSelectedPreset] = useState<PresetRange>('month');
  const [showCustomCalendar, setShowCustomCalendar] = useState(false);

  const handlePresetChange = (value: PresetRange) => {
    setSelectedPreset(value);

    if (value === 'custom') {
      setShowCustomCalendar(true);
    } else {
      setShowCustomCalendar(false);
      const newRange = getPresetRange(value);
      onDateRangeChange(newRange);
    }
  };

  const handleCustomDateSelect = (range: DateRange) => {
    onDateRangeChange(range);
  };

  const formatDateRange = (range: DateRange): string => {
    if (!range.from) return "Select date range";
    if (!range.to) return format(range.from, "MMM dd, yyyy");
    if (range.from.getTime() === range.to.getTime()) {
      return format(range.from, "MMM dd, yyyy");
    }
    return `${format(range.from, "MMM dd")} - ${format(range.to, "MMM dd, yyyy")}`;
  };

  return (
    <div className={cn("flex flex-col sm:flex-row gap-4", className)}>
      {/* Preset Selector */}
      <div className="flex-1">
        <Select value={selectedPreset} onValueChange={handlePresetChange}>
          <SelectTrigger data-testid="date-range-preset-selector">
            <SelectValue placeholder="Select time period" />
          </SelectTrigger>
          <SelectContent>
            {presetOptions.map((option) => (
              <SelectItem key={option.value} value={option.value}>
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Date Range Display */}
      <div className="flex-1">
        <Popover open={showCustomCalendar} onOpenChange={setShowCustomCalendar}>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              className={cn(
                "w-full justify-start text-left font-normal",
                !dateRange.from && "text-muted-foreground"
              )}
              data-testid="custom-date-range-trigger"
            >
              <CalendarIcon className="mr-2 h-4 w-4" />
              {formatDateRange(dateRange)}
              <ChevronDown className="ml-auto h-4 w-4" />
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-auto p-0" align="start">
            <Card>
              <CardContent className="p-4">
                <div className="space-y-4">
                  <div>
                    <h4 className="font-light text-sm mb-2">From Date</h4>
                    <Calendar
                      mode="single"
                      selected={dateRange.from || undefined}
                      onSelect={(date) => handleCustomDateSelect({ 
                        from: date || null, 
                        to: dateRange.to 
                      })}
                      disabled={(date) =>
                        date > new Date() || date < new Date("2020-01-01")
                      }
                      initialFocus
                      data-testid="from-date-calendar"
                    />
                  </div>

                  {dateRange.from && (
                    <div>
                      <h4 className="font-light text-sm mb-2">To Date</h4>
                      <Calendar
                        mode="single"
                        selected={dateRange.to || undefined}
                        onSelect={(date) => handleCustomDateSelect({ 
                          from: dateRange.from, 
                          to: date || null 
                        })}
                        disabled={(date) =>
                          date > new Date() || 
                          date < (dateRange.from || new Date("2020-01-01"))
                        }
                        data-testid="to-date-calendar"
                      />
                    </div>
                  )}

                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      variant="outline" 
                      onClick={() => setShowCustomCalendar(false)}
                      data-testid="cancel-custom-date"
                    >
                      Cancel
                    </Button>
                    <Button 
                      size="sm" 
                      onClick={() => setShowCustomCalendar(false)}
                      disabled={!dateRange.from || !dateRange.to}
                      data-testid="apply-custom-date"
                    >
                      Apply
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          </PopoverContent>
        </Popover>
      </div>

      {/* Quick Info */}
      <div className="text-sm text-muted-foreground self-center">
        {dateRange.from && dateRange.to && (
          <span data-testid="date-range-info">
            {Math.ceil((dateRange.to.getTime() - dateRange.from.getTime()) / (1000 * 60 * 60 * 24)) + 1} days
          </span>
        )}
      </div>
    </div>
  );
}

client/src/components/marketing/ReportCh.content:
import React from "react";
import { useQuery } from "@tanstack/react-query";
import {
  BarChart, Bar, LineChart, Line, PieChart, Pie, 
  FunnelChart, Funnel, AreaChart, Area,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, 
  ResponsiveContainer, Cell
} from "recharts";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { TrendingUp, TrendingDown, Users, MapPin, Target, Clock } from "lucide-react";

interface DateRange {
  from: Date | null;
  to: Date | null;
}

interface ReportChartsProps {
  dateRange: DateRange;
}

interface ConversionFunnelData {
  stage: string;
  count: number;
  percentage: number;
  dropoffRate: number;
}

interface VisitTrendsData {
  date: string;
  scheduled: number;
  completed: number;
  cancelled: number;
  successRate: number;
}

interface TeamPerformanceData {
  userId: string;
  userName: string;
  visitsCompleted: number;
  leadsConverted: number;
  tasksCompleted: number;
  efficiency: number;
}

interface LeadSourceData {
  source: string;
  count: number;
  conversionRate: number;
  value: number;
}

const COLORS = {
  primary: '#3b82f6',
  success: '#10b981',
  warning: '#f59e0b',
  danger: '#ef4444',
  info: '#06b6d4',
  purple: '#8b5cf6',
  pink: '#ec4899',
  orange: '#f97316'
};

const CHART_COLORS = [
  COLORS.primary,
  COLORS.success,
  COLORS.warning,
  COLORS.danger,
  COLORS.info,
  COLORS.purple,
  COLORS.pink,
  COLORS.orange
];

export default function ReportCharts({ dateRange }: ReportChartsProps) {
  const dateRangeParam = React.useMemo(() => {
    if (!dateRange.from || !dateRange.to) return '';
    return `from=${dateRange.from.toISOString()}&to=${dateRange.to.toISOString()}`;
  }, [dateRange.from, dateRange.to]);

  const { data: conversionData, isLoading: loadingConversion } = useQuery({
    queryKey: ['/api/marketing/conversion-rates', dateRangeParam],
    enabled: !!dateRangeParam
  });

  const { data: visitData, isLoading: loadingVisits } = useQuery({
    queryKey: ['/api/marketing/visit-success-rates', dateRangeParam],
    enabled: !!dateRangeParam
  });

  const { data: teamData, isLoading: loadingTeam } = useQuery({
    queryKey: ['/api/marketing/team-performance', dateRangeParam],
    enabled: !!dateRangeParam
  });

  const { data: leadsData, isLoading: loadingLeads } = useQuery({
    queryKey: ['/api/marketing/leads /metrics', dateRangeParam],
    enabled: !!dateRangeParam
  });

  // Transform API data for charts
  const conversionFunnelData: ConversionFunnelData[] = React.useMemo(() => {
    if (!conversionData || typeof conversionData !== 'object') return [];

    // Handle different possible API response formats
    if (Array.isArray(conversionData)) {
      return conversionData.map((item: any) => ({
        stage: item.stage || item.name || 'Unknown',
        count: item.count || item.value || 0,
        percentage: item.percentage || 0,
        dropoffRate: item.dropoffRate || 0
      }));
    }

    // If API returns aggregated metrics, transform to funnel format
    const conversionObj = conversionData as any;
    const stages = [
      { stage: 'New Leads', count: conversionObj?.totalLeads || 0 },
      { stage: 'Contacted', count: conversionObj?.contactedLeads || 0 },
      { stage: 'In Progress', count: conversionObj?.inProgressLeads || 0 },
      { stage: 'Converted', count: conversionObj?.convertedLeads || 0 }
    ];

    const totalLeads = stages[0].count;
    return stages.map((stage, index) => {
      const percentage = totalLeads > 0 ? (stage.count / totalLeads) * 100 : 0;
      const prevCount = index > 0 ? stages[index - 1].count : totalLeads;
      const dropoffRate = prevCount > 0 ? ((prevCount - stage.count) / prevCount) * 100 : 0;

      return {
        stage: stage.stage,
        count: stage.count,
        percentage: Math.round(percentage * 10) / 10,
        dropoffRate: Math.round(dropoffRate * 10) / 10
      };
    });
  }, [conversionData]);

  const visitTrendsData: VisitTrendsData[] = React.useMemo(() => {
    if (!visitData || !Array.isArray(visitData)) return [];

    return visitData.map((item: any) => ({
      date: item.date || item.visitDate || new Date().toISOString().split('T')[0],
      scheduled: item.scheduled || item.totalVisits || 0,
      completed: item.completed || item.completedVisits || 0,
      cancelled: item.cancelled || item.cancelledVisits || 0,
      successRate: item.successRate || (
        item.scheduled > 0 
          ? Math.round((item.completed / item.scheduled) * 1000) / 10
          : 0
      )
    }));
  }, [visitData]);

  const teamPerformanceData: TeamPerformanceData[] = React.useMemo(() => {
    if (!teamData || !Array.isArray(teamData)) return [];

    return teamData.map((item: any) => ({
      userId: item.userId || item.id || 'unknown',
      userName: item.userName || item.name || `${item.firstName || ''} ${item.lastName || ''}`.trim() || 'Unknown User',
      visitsCompleted: item.visitsCompleted || item.completedVisits || 0,
      leadsConverted: item.leadsConverted || item.convertedLeads || 0,
      tasksCompleted: item.tasksCompleted || item.completedTasks || 0,
      efficiency: item.efficiency || item.productivityScore || 0
    }));
  }, [teamData]);

  const leadSourcesData: LeadSourceData[] = React.useMemo(() => {
    if (!leadsData || typeof leadsData !== 'object') return [];

    // Handle source distribution from leads metrics
    const leadsObj = leadsData as any;
    if (leadsObj?.sourceDistribution && Array.isArray(leadsObj.sourceDistribution)) {
      return leadsObj.sourceDistribution.map((item: any) => ({
        source: item.source || 'Unknown',
        count: item.count || item.total || 0,
        conversionRate: item.conversionRate || item.conversion || 0,
        value: item.count || item.total || 0
      }));
    }

    // Fallback: Create from available lead sources data
    const sources = leadsObj?.sources || [];
    return sources.map((source: any) => ({
      source: source.name || 'Unknown',
      count: source.count || 0,
      conversionRate: source.conversionRate || 0,
      value: source.count || 0
    }));
  }, [leadsData]);

  if (!dateRangeParam) {
    return (
      <div className="text-center py-8">
        <div className="text-muted-foreground">
          Please select a date range to view charts and analytics
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Conversion Funnel Chart */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Target className="h-5 w-5 text-primary" />
            <span>Lead Conversion Funnel</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {loadingConversion ? (
            <Skeleton className="h-80 w-full" />
          ) : (
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={conversionFunnelData} layout="horizontal">
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis type="number" />
                <YAxis dataKey="stage" type="category" width={100} />
                <Tooltip 
                  formatter={(value, name) => [
                    name === 'count' ? `${value} leads` : `${value}%`,
                    name === 'count' ? 'Count' : 'Percentage'
                  ]}
                />
                <Legend />
                <Bar dataKey="count" fill={COLORS.primary} name="Lead Count" />
                <Bar dataKey="percentage" fill={COLORS.success} name="Conversion %" />
              </BarChart>
            </ResponsiveContainer>
          )}
        </CardContent>
      </Card>

      {/* Visit Success Timeline */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <MapPin className="h-5 w-5 text-green-600" />
              <span>Visit Success Timeline</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {loadingVisits ? (
              <Skeleton className="h-64 w-full" />
            ) : (
              <ResponsiveContainer width="100%" height={250}>
                <LineChart data={visitTrendsData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" tickFormatter={(value) => new Date(value).toLocaleDateString()} />
                  <YAxis />
                  <Tooltip 
                    labelFormatter={(value) => new Date(value).toLocaleDateString()}
                    formatter={(value, name) => [
                      name === 'successRate' ? `${value}%` : value,
                      name === 'scheduled' ? 'Scheduled' : 
                      name === 'completed' ? 'Completed' : 
                      name === 'cancelled' ? 'Cancelled' : 'Success Rate'
                    ]}
                  />
                  <Legend />
                  <Line type="monotone" dataKey="completed" stroke={COLORS.success} name="Completed" strokeWidth={2} />
                  <Line type="monotone" dataKey="cancelled" stroke={COLORS.danger} name="Cancelled" strokeWidth={2} />
                  <Line type="monotone" dataKey="successRate" stroke={COLORS.info} name="Success Rate %" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            )}
          </CardContent>
        </Card>

        {/* Lead Sources Distribution */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Users className="h-5 w-5 text-purple-600" />
              <span>Lead Sources</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {loadingLeads ? (
              <Skeleton className="h-64 w-full" />
            ) : (
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={leadSourcesData}
                    cx="50%"
                    cy="50%"
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="count"
                    nameKey="source"
                    label={({ source, conversionRate }) => `${source}: ${conversionRate}%`}
                  >
                    {leadSourcesData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={CHART_COLORS[index % CHART_COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip formatter={(value, name) => [`${value} leads`, 'Count']} />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            )}
          </CardContent>
        </Card>
