import { useState, useMemo } from "react";
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon, Users, Clock, MapPin, AlertCircle, CheckCircle, XCircle } from "lucide-react";
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameMonth, isSameDay, isToday, addMonths, subMonths, getDay } from "date-fns";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";

import type { MarketingAttendance, User } from "@shared/schema";

interface AttendanceWithUser extends MarketingAttendance {
  user?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
    role: string;
  };
}

interface AttendanceCalendarProps {
  attendanceData: AttendanceWithUser[];
  users: User[];
  selectedUserId?: string;
  onUserSelect?: (userId: string | undefined) => void;
  onDateSelect?: (date: Date) => void;
  isManager?: boolean;
}

interface DayAttendance {
  date: Date;
  attendance: AttendanceWithUser[];
  presentCount: number;
  absentCount: number;
  lateCount: number;
  onLeaveCount: number;
}

const statusColors = {
  present: 'bg-green-100 text-green-800 border-green-200',
  absent: 'bg-red-100 text-red-800 border-red-200',
  late: 'bg-orange-100 text-orange-800 border-orange-200',
  half_day: 'bg-yellow-100 text-yellow-800 border-yellow-200',
  holiday: 'bg-blue-100 text-blue-800 border-blue-200'
};

const statusIcons = {
  present: <CheckCircle className="h-3 w-3" />,
  absent: <XCircle className="h-3 w-3" />,
  late: <Clock className="h-3 w-3" />,
  half_day: <AlertCircle className="h-3 w-3" />,
  holiday: <CalendarIcon className="h-3 w-3" />
};

export default function AttendanceCalendar({
  attendanceData,
  users,
  selectedUserId,
  onUserSelect,
  onDateSelect,
  isManager = false
}: AttendanceCalendarProps) {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [viewMode, setViewMode] = useState<'team' | 'individual'>('team');

  // Filter attendance data based on selected user
  const filteredAttendance = useMemo(() => {
    if (!selectedUserId) return attendanceData;
    return attendanceData.filter(record => record.userId === selectedUserId);
  }, [attendanceData, selectedUserId]);

  // Group attendance data by date
  const attendanceByDate = useMemo(() => {
    const grouped: Record<string, AttendanceWithUser[]> = {};

    filteredAttendance.forEach(record => {
      const dateKey = format(new Date(record.date), 'yyyy-MM-dd');
      if (!grouped[dateKey]) {
        grouped[dateKey] = [];
      }
      grouped[dateKey].push(record);
    });

    return grouped;
  }, [filteredAttendance]);

  // Get calendar days for current month
  const calendarDays = useMemo(() => {
    const start = startOfMonth(currentMonth);
    const end = endOfMonth(currentMonth);
    const monthDays = eachDayOfInterval({ start, end });

    // Add padding days for proper calendar layout
    const startDay = getDay(start); // 0 = Sunday, 1 = Monday, etc.
    const paddingDays = Array(startDay).fill(null);

    return [...paddingDays, ...monthDays];
  }, [currentMonth]);

  // Get attendance summary for a specific date
  const getDayAttendance = (date: Date): DayAttendance => {
    const dateKey = format(date, 'yyyy-MM-dd');
    const dayAttendance = attendanceByDate[dateKey] || [];

    const summary = dayAttendance.reduce(
      (acc, record) => {
        switch (record.attendanceStatus) {
          case 'present':
            acc.presentCount++;
            break;
          case 'absent':
            acc.absentCount++;
            break;
          case 'late':
            acc.lateCount++;
            break;
          default:
            break;
        }
        if (record.isOnLeave) {
          acc.onLeaveCount++;
        }
        return acc;
      },
      { presentCount: 0, absentCount: 0, lateCount: 0, onLeaveCount: 0 }
    );

    return {
      date,
      attendance: dayAttendance,
      ...summary
    };
  };

  // Get the primary status for a day (used for coloring)
  const getDayPrimaryStatus = (dayData: DayAttendance): string => {
    if (dayData.onLeaveCount > 0) return 'holiday';
    if (dayData.absentCount > 0) return 'absent';
    if (dayData.lateCount > 0) return 'late';
    if (dayData.presentCount > 0) return 'present';
    return 'absent';
  };

  // Navigate to previous month
  const goToPreviousMonth = () => {
    setCurrentMonth(prev => subMonths(prev, 1));
  };

  // Navigate to next month
  const goToNextMonth = () => {
    setCurrentMonth(prev => addMonths(prev, 1));
  };

  // Get selected user details
  const selectedUser = selectedUserId ? users.find(u => u.id === selectedUserId) : null;

  // Get user initials
  const getUserInitials = (user: User) => {
    return `${user.firstName?.[0] || ''}${user.lastName?.[0] || ''}`.toUpperCase();
  };

  return (
    <div className="space-y-6">
      {/* Calendar Header */}
      <Card>
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Button variant="outline" size="sm" onClick={goToPreviousMonth} data-testid="button-prev-month">
                  <ChevronLeft className="h-4 w-4" />
                </Button>
                <CardTitle className="text-lg font-semibold" data-testid="current-month">
                  {format(currentMonth, 'MMMM yyyy')}
                </CardTitle>
                <Button variant="outline" size="sm" onClick={goToNextMonth} data-testid="button-next-month">
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentMonth(new Date())}
                data-testid="button-today"
              >
                Today
              </Button>
            </div>

            <div className="flex items-center space-x-3">
              {/* View Mode Toggle */}
              <Select value={viewMode} onValueChange={(value) => setViewMode(value as 'team' | 'individual')}>
                <SelectTrigger className="w-32" data-testid="select-view-mode">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="team">Team View</SelectItem>
                  <SelectItem value="individual">Individual</SelectItem>
                </SelectContent>
              </Select>

              {/* User Selection (for individual view) */}
              {viewMode === 'individual' && isManager && (
                <Select value={selectedUserId || 'all'} onValueChange={(value) => onUserSelect?.(value === 'all' ? undefined : value)}>
                  <SelectTrigger className="w-48" data-testid="select-user">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Team Members</SelectItem>
                    {users.filter(user => user.id && user.id.trim() !== '').map((user) => (
                      <SelectItem key={user.id} value={user.id}>
                        <div className="flex items-center space-x-2">
                          <Avatar className="h-5 w-5">
                            <AvatarFallback className="text-xs">{getUserInitials(user)}</AvatarFallback>
                          </Avatar>
                          <span>{user.firstName} {user.lastName}</span>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
            </div>
          </div>

          {selectedUser && (
            <div className="flex items-center space-x-2 mt-2">
              <Avatar className="h-6 w-6">
                <AvatarFallback className="text-xs">{getUserInitials(selectedUser)}</AvatarFallback>
              </Avatar>
              <span className="text-sm text-muted-foreground">
                Viewing attendance for {selectedUser.firstName} {selectedUser.lastName}
              </span>
            </div>
          )}
        </CardHeader>

        <CardContent>
          {/* Calendar Grid */}
          <div className="space-y-4">
            {/* Days of Week Header */}
            <div className="grid grid-cols-7 gap-1">
              {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
                <div key={day} className="text-center text-sm font-light text-muted-foreground py-2">
                  {day}
                </div>
              ))}
            </div>

            {/* Calendar Days */}
            <div className="grid grid-cols-7 gap-1">
              {calendarDays.map((day, index) => {
                if (!day) {
                  return <div key={index} className="h-24 p-1"></div>;
                }

                const dayData = getDayAttendance(day);
                const primaryStatus = getDayPrimaryStatus(dayData);
                const isCurrentMonth = isSameMonth(day, currentMonth);
                const isCurrentDay = isToday(day);

                return (
                  <TooltipProvider key={format(day, 'yyyy-MM-dd')}>
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <div
                          className={`h-24 p-1 border rounded-lg cursor-pointer transition-colors ${
                            isCurrentMonth
                              ? 'border-border hover:bg-muted/50'
                              : 'border-muted bg-muted/20'
                          } ${
                            isCurrentDay
                              ? 'ring-2 ring-primary'
                              : ''
                          }`}
                          onClick={() => onDateSelect?.(day)}
                          data-testid={`calendar-day-${format(day, 'yyyy-MM-dd')}`}
                        >
                          {/* Date Number */}
                          <div className="flex items-center justify-between mb-1">
                            <span
                              className={`text-xs font-light ${
                                isCurrentMonth
                                  ? isCurrentDay
                                    ? 'text-primary font-bold'
                                    : 'text-foreground'
                                  : 'text-muted-foreground'
                              }`}
                            >
                              {format(day, 'd')}
                            </span>

                            {/* Status Indicator */}
                            {dayData.attendance.length > 0 && (
                              <div className={`text-xs rounded-full w-4 h-4 flex items-center justify-center ${
                                statusColors[primaryStatus as keyof typeof statusColors] || 'bg-gray-100 text-gray-800'
                              }`}>
                                {statusIcons[primaryStatus as keyof typeof statusIcons]}
                              </div>
                            )}
                          </div>

                          {/* Attendance Summary */}
                          {dayData.attendance.length > 0 && (
                            <div className="space-y-1">
                              {viewMode === 'team' ? (
                                // Team view - show counts
                                <div className="space-y-0.5 text-xs">
                                  {dayData.presentCount > 0 && (
                                    <div className="flex items-center justify-between">
                                      <span className="text-green-600">Present</span>
                                      <span className="font-light">{dayData.presentCount}</span>
                                    </div>
                                  )}
                                  {dayData.absentCount > 0 && (
                                    <div className="flex items-center justify-between">
                                      <span className="text-red-600">Absent</span>
                                      <span className="font-light">{dayData.absentCount}</span>
                                    </div>
                                  )}
                                  {dayData.lateCount > 0 && (
                                    <div className="flex items-center justify-between">
                                      <span className="text-orange-600">Late</span>
                                      <span className="font-light">{dayData.lateCount}</span>
                                    </div>
                                  )}
                                  {dayData.onLeaveCount > 0 && (
                                    <div className="flex items-center justify-between">
                                      <span className="text-blue-600">Leave</span>
                                      <span className="font-light">{dayData.onLeaveCount}</span>
                                    </div>
                                  )}
                                </div>
                              ) : (
                                // Individual view - show specific times
                                <div className="space-y-0.5 text-xs">
                                  {dayData.attendance.map((record, idx) => (
                                    <div key={idx} className="truncate">
                                      {record.checkInTime && (
                                        <div className="flex items-center space-x-1">
                                          <Clock className="h-2 w-2" />
                                          <span>{format(new Date(record.checkInTime), 'HH:mm')}</span>
                                        </div>
                                      )}
                                      {record.isOnLeave && (
                                        <Badge variant="outline" className="text-xs">
                                          {record.leaveType?.toUpperCase() || 'LEAVE'}
                                        </Badge>
                                      )}
                                    </div>
                                  ))}
                                </div>
                              )}
                            </div>
                          )}
                        </div>
                      </TooltipTrigger>
                      <TooltipContent>
                        <div className="space-y-2">
                          <p className="font-light">{format(day, 'EEEE, MMMM d, yyyy')}</p>
                          {dayData.attendance.length > 0 ? (
                            <div className="space-y-1 text-xs">
                              {dayData.attendance.map((record, idx) => (
                                <div key={idx} className="border-b border-muted last:border-0 pb-1 last:pb-0">
                                  <div className="flex items-center space-x-2">
                                    <span className="font-light">
                                      {record.user?.firstName} {record.user?.lastName}
                                    </span>
                                    <Badge variant="outline" className={`text-xs ${
                                      statusColors[record.attendanceStatus as keyof typeof statusColors]
                                    }`}>
                                      {record.attendanceStatus.replace('_', ' ').toUpperCase()}
                                    </Badge>
                                  </div>
                                  {record.checkInTime && (
                                    <p>Check-in: {format(new Date(record.checkInTime), 'HH:mm')}</p>
                                  )}
                                  {record.checkOutTime && (
                                    <p>Check-out: {format(new Date(record.checkOutTime), 'HH:mm')}</p>
                                  )}
                                  {record.isOnLeave && (
                                    <p className="text-blue-600">
                                      On {record.leaveType?.replace('_', ' ')} leave
                                    </p>
                                  )}
                                </div>
                              ))}
                            </div>
                          ) : (
                            <p className="text-xs text-muted-foreground">No attendance records</p>
                          )}
                        </div>
                      </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                );
              })}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Legend */}
      <Card>
        <CardHeader className="pb-2">
          <CardTitle className="text-sm">Legend</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-4 text-xs">
            {Object.entries(statusColors).map(([status, colorClass]) => (
              <div key={status} className="flex items-center space-x-2">
                <div className={`w-3 h-3 rounded-full border ${colorClass}`}>
                  {statusIcons[status as keyof typeof statusIcons]}
                </div>
                <span className="capitalize">{status.replace('_', ' ')}</span>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useState } from "react";
import { Clock, MapPin, User, Calendar, AlertCircle, CheckCircle, XCircle, Pause, Play } from "lucide-react";
import { format, formatDistanceToNow } from "date-fns";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

import type { MarketingAttendance, User as UserType } from "@shared/schema";

interface AttendanceWithUser extends MarketingAttendance {
  user?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
    role: string;
  };
}

interface AttendanceCardProps {
  attendance: AttendanceWithUser;
  onCheckIn?: (userId: string) => void;
  onCheckOut?: (userId: string) => void;
  onStartBreak?: (attendanceId: string) => void;
  onEndBreak?: (attendanceId: string) => void;
  isManager?: boolean;
}

const getStatusColor = (status: string) => {
  switch (status) {
    case 'present':
      return 'text-green-600 bg-green-50 border-green-200';
    case 'absent':
      return 'text-red-600 bg-red-50 border-red-200';
    case 'late':
      return 'text-orange-600 bg-orange-50 border-orange-200';
    case 'half_day':
      return 'text-yellow-600 bg-yellow-50 border-yellow-200';
    case 'holiday':
      return 'text-blue-600 bg-blue-50 border-blue-200';
    default:
      return 'text-gray-600 bg-gray-50 border-gray-200';
  }
};

const getStatusIcon = (status: string) => {
  switch (status) {
    case 'present':
      return <CheckCircle className="h-4 w-4" />;
    case 'absent':
      return <XCircle className="h-4 w-4" />;
    case 'late':
      return <Clock className="h-4 w-4" />;
    case 'half_day':
      return <AlertCircle className="h-4 w-4" />;
    case 'holiday':
      return <Calendar className="h-4 w-4" />;
    default:
      return <Clock className="h-4 w-4" />;
  }
};

export default function AttendanceCard({ 
  attendance, 
  onCheckIn, 
  onCheckOut, 
  onStartBreak, 
  onEndBreak,
  isManager = false 
}: AttendanceCardProps) {
  const [isExpanded, setIsExpanded] = useState(false);

  const user = attendance.user;
  const isCheckedIn = !!attendance.checkInTime;
  const isCheckedOut = !!attendance.checkOutTime;
  const isOnBreak = !!attendance.breakStartTime && !attendance.breakEndTime;
  const canCheckOut = isCheckedIn && !isCheckedOut;
  const canStartBreak = isCheckedIn && !isCheckedOut && !isOnBreak;
  const canEndBreak = isOnBreak;

  // Calculate work duration
  const getWorkDuration = () => {
    if (!attendance.checkInTime) return "Not checked in";

    const startTime = new Date(attendance.checkInTime);
    const endTime = attendance.checkOutTime ? new Date(attendance.checkOutTime) : new Date();

    const diffMs = endTime.getTime() - startTime.getTime();
    const hours = Math.floor(diffMs / (1000 * 60 * 60));
    const minutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));

    return `${hours}h ${minutes}m`;
  };

  // Calculate break duration
  const getBreakDuration = () => {
    if (!attendance.breakStartTime) return "0m";

    const startTime = new Date(attendance.breakStartTime);
    const endTime = attendance.breakEndTime ? new Date(attendance.breakEndTime) : new Date();

    const diffMs = endTime.getTime() - startTime.getTime();
    const minutes = Math.floor(diffMs / (1000 * 60));

    return `${minutes}m`;
  };

  const getUserInitials = () => {
    if (!user) return "NA";
    return `${user.firstName?.[0] || ''}${user.lastName?.[0] || ''}`.toUpperCase();
  };

  const getLocationText = (latitude: string | null, longitude: string | null, location: string | null) => {
    if (location) return location;
    if (latitude && longitude) return `${parseFloat(latitude).toFixed(4)}, ${parseFloat(longitude).toFixed(4)}`;
    return "No location";
  };

  return (
    <Card className="w-full transition-all duration-200 hover:shadow-md">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Avatar className="h-10 w-10">
              <AvatarFallback>{getUserInitials()}</AvatarFallback>
            </Avatar>
            <div>
              <CardTitle className="text-sm font-light" data-testid={`attendance-name-${user?.id}`}>
                {user ? `${user.firstName} ${user.lastName}` : 'Unknown User'}
              </CardTitle>
              <p className="text-xs text-muted-foreground" data-testid={`attendance-role-${user?.id}`}>
                {user?.role?.charAt(0).toUpperCase() + user?.role?.slice(1) || 'Employee'}
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <Badge 
              className={`text-xs border ${getStatusColor(attendance.attendanceStatus)}`}
              data-testid={`attendance-status-${user?.id}`}
            >
              {getStatusIcon(attendance.attendanceStatus)}
              <span className="ml-1 capitalize">{attendance.attendanceStatus.replace('_', ' ')}</span>
            </Badge>

            {attendance.isOnLeave && (
              <Badge variant="outline" className="text-xs" data-testid={`leave-badge-${user?.id}`}>
                On Leave
              </Badge>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Time Information */}
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div className="space-y-1">
            <div className="flex items-center space-x-1 text-muted-foreground">
              <Clock className="h-3 w-3" />
              <span>Check-in</span>
            </div>
            <p className="font-light" data-testid={`checkin-time-${user?.id}`}>
              {attendance.checkInTime 
                ? format(new Date(attendance.checkInTime), 'HH:mm')
                : '--:--'
              }
            </p>
          </div>

          <div className="space-y-1">
            <div className="flex items-center space-x-1 text-muted-foreground">
              <Clock className="h-3 w-3" />
              <span>Check-out</span>
            </div>
            <p className="font-light" data-testid={`checkout-time-${user?.id}`}>
              {attendance.checkOutTime 
                ? format(new Date(attendance.checkOutTime), 'HH:mm')
                : '--:--'
              }
            </p>
          </div>
        </div>

        {/* Work Duration and Status */}
        <div className="grid grid-cols-3 gap-2 text-xs">
          <div className="text-center p-2 bg-muted/50 rounded">
            <p className="text-muted-foreground">Duration</p>
            <p className="font-light" data-testid={`work-duration-${user?.id}`}>{getWorkDuration()}</p>
          </div>
          <div className="text-center p-2 bg-muted/50 rounded">
            <p className="text-muted-foreground">Break</p>
            <p className="font-light" data-testid={`break-duration-${user?.id}`}>{getBreakDuration()}</p>
          </div>
          <div className="text-center p-2 bg-muted/50 rounded">
            <p className="text-muted-foreground">Total Hours</p>
            <p className="font-light" data-testid={`total-hours-${user?.id}`}>
              {attendance.totalHours ? `${attendance.totalHours}h` : '0h'}
            </p>
          </div>
        </div>

        {/* Action Buttons */}
        {!isManager && (
          <div className="flex flex-wrap gap-2">
            {!isCheckedIn && (
              <Button 
                size="sm" 
                onClick={() => onCheckIn?.(user?.id || attendance.userId)}
                className="flex-1"
                data-testid={`button-checkin-${user?.id}`}
              >
                <Play className="h-3 w-3 mr-1" />
                Check In
              </Button>
            )}

            {canCheckOut && (
              <Button 
                size="sm" 
                variant="outline"
                onClick={() => onCheckOut?.(user?.id || attendance.userId)}
                className="flex-1"
                data-testid={`button-checkout-${user?.id}`}
              >
                <Pause className="h-3 w-3 mr-1" />
                Check Out
              </Button>
            )}

            {canStartBreak && (
              <Button 
                size="sm" 
                variant="secondary"
                onClick={() => onStartBreak?.(attendance.id)}
                data-testid={`button-start-break-${user?.id}`}
              >
                <Pause className="h-3 w-3 mr-1" />
                Start Break
              </Button>
            )}

            {canEndBreak && (
              <Button 
                size="sm" 
                variant="secondary"
                onClick={() => onEndBreak?.(attendance.id)}
                data-testid={`button-end-break-${user?.id}`}
              >
                <Play className="h-3 w-3 mr-1" />
                End Break
              </Button>
            )}
          </div>
        )}

        {/* Expandable Details */}
        <div className="border-t pt-3">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsExpanded(!isExpanded)}
            className="w-full text-xs"
            data-testid={`button-toggle-details-${user?.id}`}
          >
            {isExpanded ? 'Hide Details' : 'Show Details'}
          </Button>

          {isExpanded && (
            <div className="mt-3 space-y-3 text-xs">
              {/* Location Details */}
              <div className="space-y-2">
                <h4 className="font-light text-muted-foreground">Location Information</h4>
                <div className="grid grid-cols-1 gap-2">
                  {attendance.checkInLatitude && (
                    <div className="flex items-start space-x-2">
                      <MapPin className="h-3 w-3 mt-0.5 text-green-500" />
                      <div>
                        <p className="font-light">Check-in Location</p>
                        <p className="text-muted-foreground" data-testid={`checkin-location-${user?.id}`}>
                          {getLocationText(
                            attendance.checkInLatitude, 
                            attendance.checkInLongitude, 
                            attendance.checkInLocation
                          )}
                        </p>
                      </div>
                    </div>
                  )}

                  {attendance.checkOutLatitude && (
                    <div className="flex items-start space-x-2">
                      <MapPin className="h-3 w-3 mt-0.5 text-red-500" />
                      <div>
                        <p className="font-light">Check-out Location</p>
                        <p className="text-muted-foreground" data-testid={`checkout-location-${user?.id}`}>
                          {getLocationText(
                            attendance.checkOutLatitude, 
                            attendance.checkOutLongitude, 
                            attendance.checkOutLocation
                          )}
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Work Description */}
              {attendance.workDescription && (
                <div>
                  <h4 className="font-light text-muted-foreground mb-1">Work Description</h4>
                  <p className="text-muted-foreground" data-testid={`work-description-${user?.id}`}>
                    {attendance.workDescription}
                  </p>
                </div>
              )}

              {/* Statistics */}
              <div className="grid grid-cols-2 gap-2 pt-2 border-t">
                <div>
                  <p className="text-muted-foreground">Visits</p>
                  <p className="font-light" data-testid={`visit-count-${user?.id}`}>{attendance.visitCount || 0}</p>
                </div>
                <div>
                  <p className="text-muted-foreground">Tasks</p>
                  <p className="font-light" data-testid={`tasks-completed-${user?.id}`}>{attendance.tasksCompleted || 0}</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

