import { useState, useEffect, useMemo } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  Users, 
  Clock, 
  MapPin, 
  Calendar, 
  UserCheck, 
  UserX, 
  Timer, 
  TrendingUp,
  Plus,
  Filter,
  Download,
  RefreshCw,
  BarChart3,
  AlertCircle,
  CheckCircle,
  XCircle,
  Pause,
  Play
} from "lucide-react";
import { format, isToday, startOfMonth, endOfMonth } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Separator } from "@/components/ui/separator";

// Import our custom components
import AttendanceCard from "@/components/marketing/AttendanceCard";
import CheckInModal from "@/components/marketing/CheckInModal";
import CheckOutModal from "@/components/marketing/CheckOutModal";
import LeaveRequestForm from "@/components/marketing/LeaveRequestForm";
import AttendanceCalendar from "@/components/marketing/AttendanceCalendar";

import type { MarketingAttendance, User, InsertMarketingAttendance } from "@shared/schema";

interface AttendanceWithUser extends MarketingAttendance {
  user?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
    role: string;
  };
}

interface AttendanceMetrics {
  totalEmployees: number;
  presentToday: number;
  absentToday: number;
  lateToday: number;
  onLeaveToday: number;
  averageWorkHours: number;
  attendanceRate: number;
  monthlyStats: {
    totalDays: number;
    presentDays: number;
    absentDays: number;
    leaveDays: number;
  };
}

interface LeaveBalance {
  totalLeave: number;
  usedLeave: number;
  remainingLeave: number;
  sickLeave: number;
  vacationLeave: number;
  personalLeave: number;
}

interface LeaveRequest {
  id?: string;
  leaveType: 'sick' | 'vacation' | 'personal' | 'emergency' | 'training' | 'other';
  startDate: Date;
  endDate: Date;
  reason: string;
  status?: 'pending' | 'approved' | 'rejected' | 'cancelled';
  totalDays?: number;
}

type ViewMode = 'dashboard' | 'team' | 'calendar' | 'reports';
type StatusFilter = 'all' | 'present' | 'absent' | 'late' | 'on_leave';

export default function MarketingAttendance() {
  const [viewMode, setViewMode] = useState<ViewMode>('dashboard');
  const [statusFilter, setStatusFilter] = useState<StatusFilter>('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedUserId, setSelectedUserId] = useState<string | undefined>(undefined);

  // Modal states
  const [checkInModalOpen, setCheckInModalOpen] = useState(false);
  const [checkOutModalOpen, setCheckOutModalOpen] = useState(false);
  const [leaveRequestModalOpen, setLeaveRequestModalOpen] = useState(false);
  const [selectedAttendanceUser, setSelectedAttendanceUser] = useState<string | undefined>(undefined);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Auto-refresh interval (every 30 seconds)
  useEffect(() => {
    const interval = setInterval(() => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/today'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/metrics'] });
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [queryClient]);

  // Fetch today's attendance
  const { data: todayAttendance = [], isLoading: attendanceLoading, error: attendanceError } = useQuery<AttendanceWithUser[]>({
    queryKey: ['/api/marketing-attendance/today'],
    meta: { errorMessage: "Failed to load today's attendance" }
  });

  // Fetch all attendance records for calendar view
  const { data: allAttendance = [] } = useQuery<AttendanceWithUser[]>({
    queryKey: ['/api/marketing-attendance'],
    meta: { errorMessage: "Failed to load attendance records" }
  });

  // Fetch attendance metrics
  const { data: metrics, isLoading: metricsLoading } = useQuery<AttendanceMetrics>({
    queryKey: ['/api/marketing-attendance/metrics'],
    meta: { errorMessage: "Failed to load attendance metrics" }
  });

  // Fetch users for team management
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['/api/users'],
    meta: { errorMessage: "Failed to load users" }
  });

  // Fetch leave balance (mock data for now)
  const leaveBalance: LeaveBalance = {
    totalLeave: 30,
    usedLeave: 12,
    remainingLeave: 18,
    sickLeave: 8,
    vacationLeave: 15,
    personalLeave: 7
  };

  // Check-in mutation - FIXED: Remove automatic modal handling, let modals control flow
  const checkInMutation = useMutation({
    mutationFn: (data: { userId?: string; latitude: number; longitude: number; location?: string; photoPath?: string; workDescription?: string }) =>
      apiRequest('/api/marketing-attendance/check-in', { method: 'POST', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/today'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/metrics'] });
      toast({ title: "Successfully checked in!" });
      // Modal closing is now handled by the modal itself
    },
    onError: (error: any) => {
      // Error handling is now done in handleCheckInSubmit
      console.error('Check-in mutation error:', error);
    }
  });

  // Check-out mutation - FIXED: Remove automatic modal handling, let modals control flow
  const checkOutMutation = useMutation({
    mutationFn: (data: { 
      userId?: string; 
      latitude: number; 
      longitude: number; 
      location?: string; 
      photoPath?: string;
      workDescription?: string;
      visitCount?: number;
      tasksCompleted?: number;
      outcome?: string;
      nextAction?: string;
    }) =>
      apiRequest('/api/marketing-attendance/check-out', { method: 'POST', body: JSON.stringify(data) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/today'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/metrics'] });
      toast({ title: "Successfully checked out!" });
      // Modal closing is now handled by the modal itself
    },
    onError: (error: any) => {
      // Error handling is now done in handleCheckOutSubmit
      console.error('Check-out mutation error:', error);
    }
  });

  // Leave request submission mutation
  const leaveRequestMutation = useMutation({
    mutationFn: (leaveRequest: LeaveRequest) =>
      apiRequest('/api/marketing-attendance/leave-request', { method: 'POST', body: JSON.stringify(leaveRequest) }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance'] });
      queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/metrics'] });
      toast({ title: "Leave request submitted successfully!" });
      setLeaveRequestModalOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: "Error submitting leave request", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  // Filter attendance based on search and status
  const filteredAttendance = useMemo(() => {
    return todayAttendance.filter(record => {
      const matchesSearch = !searchTerm || 
        record.user?.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        record.user?.lastName.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesStatus = statusFilter === 'all' || 
        (statusFilter === 'present' && record.attendanceStatus === 'present') ||
        (statusFilter === 'absent' && record.attendanceStatus === 'absent') ||
        (statusFilter === 'late' && record.attendanceStatus === 'late') ||
        (statusFilter === 'on_leave' && record.isOnLeave);

      return matchesSearch && matchesStatus;
    });
  }, [todayAttendance, searchTerm, statusFilter]);

  // Handle check-in
  const handleCheckIn = (userId: string) => {
    setSelectedAttendanceUser(userId);
    setCheckInModalOpen(true);
  };

  // Handle check-out  
  const handleCheckOut = (userId: string) => {
    setSelectedAttendanceUser(userId);
    setCheckOutModalOpen(true);
  };

  // Handle GPS check-in submission - FIXED: Return Promise for new modal interface
  const handleCheckInSubmit = async (data: {
    latitude: number;
    longitude: number;
    location?: string;
    workDescription?: string;
  }): Promise<{ attendanceId: string; success: boolean; error?: string }> => {
    try {
      const result = await checkInMutation.mutateAsync({
        userId: selectedAttendanceUser,
        ...data
      });

      return {
        attendanceId: result.id,
        success: true
      };
    } catch (error: any) {
      return {
        attendanceId: '',
        success: false,
        error: error.message || 'Check-in failed'
      };
    }
  };

  // Handle GPS check-out submission - FIXED: Return Promise for new modal interface
  const handleCheckOutSubmit = async (data: {
    latitude: number;
    longitude: number;
    location?: string;
    workDescription?: string;
    visitCount?: number;
    tasksCompleted?: number;
    outcome?: string;
    nextAction?: string;
  }): Promise<{ attendanceId: string; success: boolean; error?: string }> => {
    try {
      const result = await checkOutMutation.mutateAsync({
        userId: selectedAttendanceUser,
        ...data
      });

      return {
        attendanceId: result.id,
        success: true
      };
    } catch (error: any) {
      return {
        attendanceId: '',
        success: false,
        error: error.message || 'Check-out failed'
      };
    }
  };

  // Handle leave request submission
  const handleLeaveRequestSubmit = (leaveRequest: LeaveRequest) => {
    leaveRequestMutation.mutate(leaveRequest);
  };

  // Get attendance status counts
  const statusCounts = useMemo(() => {
    const counts = todayAttendance.reduce((acc, record) => {
      if (record.isOnLeave) {
        acc.on_leave++;
      } else {
        acc[record.attendanceStatus] = (acc[record.attendanceStatus] || 0) + 1;
      }
      return acc;
    }, { present: 0, absent: 0, late: 0, on_leave: 0 } as Record<string, number>);

    return counts;
  }, [todayAttendance]);

  // Default metrics if loading
  const displayMetrics: AttendanceMetrics = metrics || {
    totalEmployees: users.length,
    presentToday: statusCounts.present,
    absentToday: statusCounts.absent,
    lateToday: statusCounts.late,
    onLeaveToday: statusCounts.on_leave,
    averageWorkHours: 8.5,
    attendanceRate: users.length > 0 ? (statusCounts.present / users.length) * 100 : 0,
    monthlyStats: {
      totalDays: 22,
      presentDays: 18,
      absentDays: 2,
      leaveDays: 2
    }
  };

  const selectedUser = selectedAttendanceUser ? users.find(u => u.id === selectedAttendanceUser) : null;

  if (attendanceError) {
    return (
      <div className="p-8">
        <Alert>
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            Failed to load attendance data. Please try refreshing the page.
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold" data-testid="page-title">Marketing Attendance</h1>
          <p className="text-muted-foreground">
            Track team attendance with live location and leave management
          </p>
        </div>

        <div className="flex items-center space-x-3">
          <Button
            variant="outline"
            onClick={() => {
              queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/today'] });
              queryClient.invalidateQueries({ queryKey: ['/api/marketing-attendance/metrics'] });
            }}
            data-testid="button-refresh"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>

          <Button
            onClick={() => setLeaveRequestModalOpen(true)}
            data-testid="button-request-leave"
          >
            <Plus className="h-4 w-4 mr-2" />
            Request Leave
          </Button>
        </div>
      </div>

      {/* View Tabs */}
      <Tabs value={viewMode} onValueChange={(value) => setViewMode(value as ViewMode)}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="dashboard" data-testid="tab-dashboard">
            <BarChart3 className="h-4 w-4 mr-2" />
            Dashboard
          </TabsTrigger>
          <TabsTrigger value="team" data-testid="tab-team">
            <Users className="h-4 w-4 mr-2" />
            Team Status
          </TabsTrigger>
          <TabsTrigger value="calendar" data-testid="tab-calendar">
            <Calendar className="h-4 w-4 mr-2" />
            Calendar View
          </TabsTrigger>
          <TabsTrigger value="reports" data-testid="tab-reports">
            <TrendingUp className="h-4 w-4 mr-2" />
            Reports
          </TabsTrigger>
        </TabsList>

        {/* Dashboard Tab */}
        <TabsContent value="dashboard" className="space-y-6">
          {/* Metrics Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between pb-2">
                <CardTitle className="text-sm font-light">Present Today</CardTitle>
                <UserCheck className="h-4 w-4 text-green-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600" data-testid="metric-present">
                  {displayMetrics.presentToday}
                </div>
                <p className="text-xs text-muted-foreground">
                  {users.length > 0 ? `${((displayMetrics.presentToday / users.length) * 100).toFixed(1)}%` : '0%'} of team
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between pb-2">
                <CardTitle className="text-sm font-light">Absent Today</CardTitle>
                <UserX className="h-4 w-4 text-red-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-red-600" data-testid="metric-absent">
                  {displayMetrics.absentToday}
                </div>
                <p className="text-xs text-muted-foreground">
                  {users.length > 0 ? `${((displayMetrics.absentToday / users.length) * 100).toFixed(1)}%` : '0%'} of team
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between pb-2">
                <CardTitle className="text-sm font-light">Late Arrivals</CardTitle>
                <Timer className="h-4 w-4 text-orange-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-orange-600" data-testid="metric-late">
                  {displayMetrics.lateToday}
                </div>
                <p className="text-xs text-muted-foreground">
                  {users.length > 0 ? `${((displayMetrics.lateToday / users.length) * 100).toFixed(1)}%` : '0%'} of team
                </p>
              </CardContent>
            </Card>

            <Card>
