    description: 'Status updates and POD management'
  },
  {
    id: 'reports',
    label: 'Reports',
    icon: FileText,
    path: '/logistics/reports',
    description: 'Delivery analytics and performance'
  },
  {
    id: 'tasks',
    label: 'Logistics Tasks',
    icon: ClipboardList,
    path: '/logistics/tasks',
    description: 'Employee task assignment'
  },
  {
