import { useState } from "react";
import { Edit, MoreHorizontal, Eye, MapPin, Clock, History, Trash2 } from "lucide-react";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { DataTable } from "@/components/ui/data-table";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

// Use shared types from schema
import type { LogisticsShipment, LogisticsShipmentStatus } from "@shared/schema";

interface ShipmentTableProps {
  shipments: LogisticsShipment[];
  onEdit: (shipment: LogisticsShipment) => void;
  onViewTimeline: (shipment: LogisticsShipment) => void;
  onDelete: (shipment: LogisticsShipment) => void;
}

export default function ShipmentTable({ shipments, onEdit, onViewTimeline, onDelete }: ShipmentTableProps) {
  const getStatusBadge = (status: string) => {
    const statusConfig = {
      created: { label: "Created", className: "bg-gray-100 text-gray-800" },
      packed: { label: "Packed", className: "bg-blue-100 text-blue-800" },
      dispatched: { label: "Dispatched", className: "bg-indigo-100 text-indigo-800" },
      in_transit: { label: "In Transit", className: "bg-purple-100 text-purple-800" },
      out_for_delivery: { label: "Out for Delivery", className: "bg-orange-100 text-orange-800" },
      delivered: { label: "Delivered", className: "bg-green-100 text-green-800" },
      closed: { label: "Closed", className: "bg-slate-100 text-slate-800" },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || {
      label: status,
      className: "bg-gray-100 text-gray-800"
    };

    return (
      <Badge className={config.className}>
        {config.label}
      </Badge>
    );
  };

  const columns = [
    {
      key: "consignmentNumber",
      header: "Consignment #",
      cell: (shipment: LogisticsShipment) => (
        <div className="font-light" data-testid={`text-consignment-${shipment.id}`}>
          {shipment.consignmentNumber}
        </div>
      ),
    },
    {
      key: "client",
      header: "Client/Vendor",
      cell: (shipment: LogisticsShipment) => (
        <div className="text-sm">
          {shipment.clientId ? (
            <div className="font-light text-foreground">Client Shipment</div>
          ) : shipment.vendorId ? (
            <div className="text-muted-foreground">Vendor Shipment</div>
          ) : (
            <span className="text-muted-foreground">Direct</span>
          )}
        </div>
      ),
    },
    {
      key: "route",
      header: "Route",
      cell: (shipment: LogisticsShipment) => (
        <div className="flex items-center space-x-2 text-sm">
          <div className="flex flex-col">
            <div className="font-light">{shipment.source}</div>
            <div className="flex items-center text-muted-foreground">
              <MapPin className="h-3 w-3 mr-1" />
              {shipment.destination}
            </div>
          </div>
        </div>
      ),
    },
    {
      key: "currentStatus",
      header: "Status",
      cell: (shipment: LogisticsShipment) => getStatusBadge(shipment.currentStatus),
    },
    {
      key: "dates",
      header: "Dates",
      cell: (shipment: LogisticsShipment) => (
        <div className="text-sm">
          {shipment.dispatchDate && (
            <div className="flex items-center">
              <Clock className="h-3 w-3 mr-1 text-muted-foreground" />
              Dispatch: {new Date(shipment.dispatchDate).toLocaleDateString()}
            </div>
          )}
          {shipment.expectedDeliveryDate && (
            <div className="text-muted-foreground">
              Expected: {new Date(shipment.expectedDeliveryDate).toLocaleDateString()}
            </div>
          )}
          {shipment.deliveredAt && (
            <div className="text-green-600">
              Delivered: {new Date(shipment.deliveredAt).toLocaleDateString()}
            </div>
          )}
        </div>
      ),
    },
    {
      key: "actions",
      header: "Actions",
      cell: (shipment: LogisticsShipment) => (
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button
              variant="ghost"
              className="h-8 w-8 p-0"
              data-testid={`button-actions-${shipment.id}`}
            >
              <span className="sr-only">Open menu</span>
              <MoreHorizontal className="h-4 w-4" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end">
            <DropdownMenuItem
              onClick={() => onEdit(shipment)}
              data-testid={`action-edit-${shipment.id}`}
            >
              <Edit className="mr-2 h-4 w-4" />
              Edit Shipment
            </DropdownMenuItem>
            <DropdownMenuItem
              onClick={() => onViewTimeline(shipment)}
              data-testid={`action-timeline-${shipment.id}`}
            >
              <History className="mr-2 h-4 w-4" />
              View Timeline
            </DropdownMenuItem>
            <DropdownMenuItem
              onClick={() => onDelete(shipment)}
              className="text-destructive focus:text-destructive"
              data-testid={`action-delete-${shipment.id}`}
            >
              <Trash2 className="mr-2 h-4 w-4" />
              Delete Shipment
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      ),
    },
  ];

  return (
    <DataTable
      data={shipments}
      columns={columns}
      searchable={true}
      searchKey="consignmentNumber"
      data-testid="table-shipments"
    />
  );
}

client/src/components/logistics/StatusWo.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { 
  Package, 
  Truck, 
  MapPin, 
  CheckCircle, 
  Clock,
  FileUp,
  ArrowRight,
  AlertCircle
} from "lucide-react";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { queryClient, apiRequest } from "@/lib/queryClient";

// Use shared types and validation helpers
import type { 
  LogisticsShipment, 
  LogisticsShipmentStatus 
} from "@shared/schema";
import { 
  LOGISTICS_SHIPMENT_STATUSES, 
  getNextStatus, 
  isValidStatusTransition 
} from "@shared/schema";

// Import POD upload component
import PodUploadComponent from "./PodUploadComponent";

interface StatusWorkflowPanelProps {
  shipments: LogisticsShipment[];
}

// Status update form schema with proper enum validation
const statusUpdateSchema = z.object({
  status: z.enum(LOGISTICS_SHIPMENT_STATUSES, {
    errorMap: () => ({ message: "Invalid status value" })
  }),
  notes: z.string().optional(),
  location: z.string().optional(),
  podFile: z.string().optional(),
});

type StatusUpdateForm = z.infer<typeof statusUpdateSchema>;

export default function StatusWorkflowPanel({ shipments }: StatusWorkflowPanelProps) {
  const [selectedShipment, setSelectedShipment] = useState<LogisticsShipment | null>(null);
  const [showStatusUpdate, setShowStatusUpdate] = useState(false);
  const [showPodUpload, setShowPodUpload] = useState(false);
  const { toast } = useToast();

  const form = useForm<StatusUpdateForm>({
    resolver: zodResolver(statusUpdateSchema),
    defaultValues: {
      status: undefined,
      notes: "",
      location: "",
      podFile: "",
    },
  });

  // Status workflow configuration
  const statusWorkflow = [
    { 
      key: 'created', 
      label: 'Created', 
      icon: Package, 
      color: 'bg-gray-100 text-gray-800',
      description: 'Shipment created and ready for packing'
    },
    { 
      key: 'packed', 
      label: 'Packed', 
      icon: Package, 
      color: 'bg-blue-100 text-blue-800',
      description: 'Items packed and ready for dispatch'
    },
    { 
      key: 'dispatched', 
      label: 'Dispatched', 
      icon: Truck, 
      color: 'bg-indigo-100 text-indigo-800',
      description: 'Shipment dispatched from warehouse'
    },
    { 
      key: 'in_transit', 
      label: 'In Transit', 
      icon: MapPin, 
      color: 'bg-purple-100 text-purple-800',
      description: 'Shipment in transit to destination'
    },
    { 
      key: 'out_for_delivery', 
      label: 'Out for Delivery', 
      icon: Truck, 
      color: 'bg-orange-100 text-orange-800',
      description: 'Out for final delivery'
    },
    { 
      key: 'delivered', 
      label: 'Delivered', 
      icon: CheckCircle, 
      color: 'bg-green-100 text-green-800',
      description: 'Successfully delivered to customer'
    },
    { 
      key: 'closed', 
      label: 'Closed', 
      icon: CheckCircle, 
      color: 'bg-slate-100 text-slate-800',
      description: 'Shipment completed and closed'
    },
  ];

  // Update status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async ({ shipmentId, data }: { shipmentId: string; data: StatusUpdateForm }) => {
      return await apiRequest(`/api/logistics/shipments/${shipmentId}/status`, {
        method: "PUT",
        body: JSON.stringify(data),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/logistics/shipments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/logistics/dashboard"] });
      setShowStatusUpdate(false);
      setSelectedShipment(null);
      form.reset();
      toast({
        title: "Success",
        description: "Shipment status updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update status",
        variant: "destructive",
      });
    },
  });

  // Close shipment mutation (for POD upload)
  const closeShipmentMutation = useMutation({
    mutationFn: async ({ shipmentId, data }: { shipmentId: string; data: any }) => {
      return await apiRequest(`/api/logistics/shipments/${shipmentId}/close`, {
        method: "POST",
        body: JSON.stringify(data),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/logistics/shipments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/logistics/dashboard"] });
      setShowPodUpload(false);
      setSelectedShipment(null);
      toast({
        title: "Success",
        description: "Shipment closed with POD uploaded successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to close shipment",
        variant: "destructive",
      });
    },
  });

  const handleStatusUpdate = (shipment: LogisticsShipment, newStatus: LogisticsShipmentStatus) => {
    // Validate status transition
    if (!isValidStatusTransition(shipment.currentStatus as LogisticsShipmentStatus, newStatus)) {
      toast({
        title: "Invalid Status Transition",
        description: `Cannot move from ${shipment.currentStatus} to ${newStatus}. Please follow the proper workflow sequence.`,
        variant: "destructive",
      });
      return;
    }

    setSelectedShipment(shipment);
    form.setValue('status', newStatus);
    setShowStatusUpdate(true);
  };

  const handlePodUpload = (shipment: LogisticsShipment) => {
    setSelectedShipment(shipment);
    setShowPodUpload(true);
  };

  const onSubmitStatusUpdate = (data: StatusUpdateForm) => {
    if (selectedShipment) {
      updateStatusMutation.mutate({ shipmentId: selectedShipment.id, data });
    }
  };

  const onSubmitPodUpload = (data: any) => {
    if (selectedShipment) {
      closeShipmentMutation.mutate({ shipmentId: selectedShipment.id, data });
    }
  };

  // Group shipments by status
  const shipmentsByStatus = statusWorkflow.reduce((acc, status) => {
    acc[status.key] = shipments.filter(s => s.currentStatus === status.key);
    return acc;
  }, {} as Record<string, LogisticsShipment[]>);

  // Use the shared getNextStatus function from schema instead of local one

  return (
    <div className="space-y-6">
      {/* Workflow Overview */}
      <div className="flex items-center justify-between overflow-x-auto pb-4">
        {statusWorkflow.map((status, index) => {
          const Icon = status.icon;
          const count = shipmentsByStatus[status.key]?.length || 0;

          return (
            <div key={status.key} className="flex items-center">
              <div className="flex flex-col items-center space-y-2 min-w-[120px]">
                <div className={`w-12 h-12 rounded-full flex items-center justify-center ${status.color}`}>
                  <Icon className="h-6 w-6" />
                </div>
                <div className="text-center">
                  <div className="text-sm font-light">{status.label}</div>
                  <div className="text-2xl font-bold">{count}</div>
                  <div className="text-xs text-muted-foreground">{status.description}</div>
                </div>
              </div>

              {index < statusWorkflow.length - 1 && (
                <ArrowRight className="h-4 w-4 text-muted-foreground mx-4" />
              )}
            </div>
          );
        })}
      </div>

      {/* Status Columns */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {statusWorkflow.map((status) => {
          const statusShipments = shipmentsByStatus[status.key] || [];
          const Icon = status.icon;

          return (
            <Card key={status.key} className="h-fit">
              <CardHeader className="pb-3">
                <CardTitle className="flex items-center space-x-2 text-sm">
                  <Icon className="h-4 w-4" />
                  <span>{status.label}</span>
                  <Badge variant="secondary">{statusShipments.length}</Badge>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3 max-h-96 overflow-y-auto">
                {statusShipments.length === 0 ? (
                  <div className="text-center text-muted-foreground py-4">
                    No shipments in this status
                  </div>
                ) : (
                  statusShipments.map((shipment) => {
                    const nextStatus = getNextStatus(shipment.currentStatus as LogisticsShipmentStatus);
                    const nextStatusConfig = nextStatus ? statusWorkflow.find(s => s.key === nextStatus) : null;

                    return (
                      <Card key={shipment.id} className="p-3">
                        <div className="space-y-2">
                          <div className="font-light text-sm" data-testid={`workflow-shipment-${shipment.id}`}>
                            {shipment.consignmentNumber}
                          </div>
                          <div className="text-xs text-muted-foreground">
                            {shipment.clientId ? 'Client Shipment' : shipment.vendorId ? 'Vendor Shipment' : 'Direct Shipment'}
                          </div>
                          <div className="text-xs">
                            {shipment.source} ‚Üí {shipment.destination}
                          </div>

                          <div className="flex space-x-1 pt-2">
                            {nextStatusConfig && (
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => handleStatusUpdate(shipment, nextStatus!)}
                                className="text-xs"
                                data-testid={`button-advance-${shipment.id}`}
                              >
                                ‚Üí {nextStatusConfig.label}
                              </Button>
                            )}

                            {shipment.currentStatus === 'delivered' && (
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => handlePodUpload(shipment)}
                                className="text-xs"
                                data-testid={`button-pod-${shipment.id}`}
                              >
                                <FileUp className="h-3 w-3 mr-1" />
                                POD
                              </Button>
                            )}
                          </div>
                        </div>
                      </Card>
                    );
                  })
                )}
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Status Update Dialog */}
      <Dialog open={showStatusUpdate} onOpenChange={setShowStatusUpdate}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Update Shipment Status</DialogTitle>
            <DialogDescription>
              Update status for shipment {selectedShipment?.consignmentNumber}
            </DialogDescription>
          </DialogHeader>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmitStatusUpdate)} className="space-y-4">
              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>New Status</FormLabel>
                    <FormControl>
                      <Input {...field} readOnly className="bg-muted" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="location"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Current Location (Optional)</FormLabel>
                    <FormControl>
                      <Input {...field} data-testid="input-location" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="notes"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Notes (Optional)</FormLabel>
                    <FormControl>
                      <Textarea {...field} data-testid="input-status-notes" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="flex justify-end space-x-2">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setShowStatusUpdate(false)}
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={updateStatusMutation.isPending}
                  data-testid="button-update-status"
                >
                  Update Status
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* POD Upload Dialog */}
      <Dialog open={showPodUpload} onOpenChange={setShowPodUpload}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Close Shipment with POD</DialogTitle>
            <DialogDescription>
              Upload Proof of Delivery and close shipment {selectedShipment?.consignmentNumber}
            </DialogDescription>
          </DialogHeader>

          <PodUploadComponent 
            shipment={selectedShipment}
            onClose={() => setShowPodUpload(false)}
            onComplete={(podData) => {
              onSubmitPodUpload(podData);
              setShowPodUpload(false);
            }}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/logistics/Timeline.content:
import { useQuery } from "@tanstack/react-query";
import { Clock, MapPin, User, FileText, Package, Truck, CheckCircle } from "lucide-react";

import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";

interface LogisticsShipment {
  id: string;
  consignmentNumber: string;
  source: string;
  destination: string;
  currentStatus: string;
}

interface StatusUpdate {
  id: string;
  status: string;
  notes?: string;
  location?: string;
  updatedBy: string;
  createdAt: string;
  user?: { username: string; };
}

interface Checkpoint {
  id: string;
  location: string;
  latitude?: number;
  longitude?: number;
  notes?: string;
  recordedBy: string;
  createdAt: string;
  user?: { username: string; };
}

interface TimelineHistoryProps {
  shipment: LogisticsShipment;
}

export default function TimelineHistory({ shipment }: TimelineHistoryProps) {
  // Fetch status updates for this shipment
  const { data: statusUpdates = [], isLoading: updatesLoading } = useQuery<StatusUpdate[]>({
    queryKey: ['/api/logistics/status-updates', shipment.id],
    queryFn: async () => {
      const response = await fetch(`/api/logistics/status-updates?shipmentId=${shipment.id}`);
      if (!response.ok) throw new Error('Failed to fetch status updates');
      return response.json();
    }
  });

  // Fetch GPS checkpoints for this shipment
  const { data: checkpoints = [], isLoading: checkpointsLoading } = useQuery<Checkpoint[]>({
    queryKey: ['/api/logistics/checkpoints', shipment.id],
    queryFn: async () => {
      const response = await fetch(`/api/logistics/checkpoints?shipmentId=${shipment.id}`);
      if (!response.ok) throw new Error('Failed to fetch checkpoints');
      return response.json();
    }
  });

  // Combine and sort timeline events
  const timelineEvents = [
    ...statusUpdates.map(update => ({
      id: `status-${update.id}`,
      type: 'status' as const,
      timestamp: update.createdAt,
      status: update.status,
      location: update.location,
      notes: update.notes,
      user: update.user?.username || 'System',
      data: update,
    })),
    ...checkpoints.map(checkpoint => ({
      id: `checkpoint-${checkpoint.id}`,
      type: 'checkpoint' as const,
      timestamp: checkpoint.createdAt,
      location: checkpoint.location,
      notes: checkpoint.notes,
      user: checkpoint.user?.username || 'Driver',
      data: checkpoint,
    }))
  ].sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime());

  const getStatusIcon = (status: string) => {
    const icons = {
      created: Package,
      packed: Package,
      dispatched: Truck,
      in_transit: MapPin,
      out_for_delivery: Truck,
      delivered: CheckCircle,
      closed: CheckCircle,
    };
    return icons[status as keyof typeof icons] || Package;
  };

  const getStatusColor = (status: string) => {
    const colors = {
      created: 'text-gray-500',
      packed: 'text-blue-500',
      dispatched: 'text-indigo-500',
      in_transit: 'text-purple-500',
      out_for_delivery: 'text-orange-500',
      delivered: 'text-green-500',
      closed: 'text-slate-500',
    };
    return colors[status as keyof typeof colors] || 'text-gray-500';
  };

  const getStatusLabel = (status: string) => {
    const labels = {
      created: 'Created',
      packed: 'Packed',
      dispatched: 'Dispatched',
      in_transit: 'In Transit',
      out_for_delivery: 'Out for Delivery',
      delivered: 'Delivered',
      closed: 'Closed',
    };
    return labels[status as keyof typeof labels] || status;
  };

  if (updatesLoading || checkpointsLoading) {
    return (
      <div className="space-y-4">
        {[...Array(5)].map((_, i) => (
          <div key={i} className="flex space-x-4">
            <Skeleton className="h-8 w-8 rounded-full" />
            <div className="flex-1">
              <Skeleton className="h-4 w-24 mb-2" />
              <Skeleton className="h-16 w-full" />
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (timelineEvents.length === 0) {
    return (
      <div className="text-center py-8">
        <Package className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
        <h3 className="text-lg font-light text-foreground mb-2">No Timeline Data</h3>
        <p className="text-muted-foreground">
          No status updates or checkpoints have been recorded for this shipment yet.
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Shipment Header */}
      <Card className="border-l-4 border-l-primary">
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-semibold text-lg">{shipment.consignmentNumber}</h3>
              <p className="text-muted-foreground">
                {shipment.source} ‚Üí {shipment.destination}
              </p>
            </div>
            <Badge className={getStatusColor(shipment.currentStatus)}>
              {getStatusLabel(shipment.currentStatus)}
            </Badge>
          </div>
        </CardContent>
      </Card>

      {/* Timeline */}
      <div className="relative">
        {/* Vertical line */}
        <div className="absolute left-4 top-0 bottom-0 w-0.5 bg-border"></div>

        <div className="space-y-6">
          {timelineEvents.map((event, index) => {
            const isLast = index === timelineEvents.length - 1;

            return (
              <div key={event.id} className="relative flex items-start space-x-4">
                {/* Timeline dot */}
                <div className={`relative z-10 flex h-8 w-8 items-center justify-center rounded-full border-2 border-background ${
                  event.type === 'status' ? 'bg-primary text-primary-foreground' : 'bg-secondary text-secondary-foreground'
                }`}>
                  {event.type === 'status' ? (
                    (() => {
                      const StatusIcon = getStatusIcon(event.status || '');
                      return <StatusIcon className="h-4 w-4" />;
                    })()
                  ) : (
                    <MapPin className="h-4 w-4" />
                  )}
                </div>

                {/* Event content */}
                <div className="flex-1 pb-8">
                  <Card className={event.type === 'status' ? 'border-primary/20' : 'border-secondary/20'}>
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          {event.type === 'status' ? (
                            <Badge variant="outline" className={getStatusColor(event.status || '')}>
                              {getStatusLabel(event.status || '')}
                            </Badge>
                          ) : (
                            <Badge variant="outline">
                              GPS Checkpoint
                            </Badge>
                          )}
                          <div className="flex items-center text-sm text-muted-foreground">
                            <User className="h-3 w-3 mr-1" />
                            {event.user}
                          </div>
                        </div>
                        <div className="flex items-center text-sm text-muted-foreground">
                          <Clock className="h-3 w-3 mr-1" />
                          {new Date(event.timestamp).toLocaleString()}
                        </div>
                      </div>

                      {event.location && (
                        <div className="flex items-center text-sm mb-2">
                          <MapPin className="h-3 w-3 mr-1 text-muted-foreground" />
                          <span className="font-light">{event.location}</span>
                        </div>
                      )}

                      {event.notes && (
                        <div className="flex items-start text-sm">
                          <FileText className="h-3 w-3 mr-1 text-muted-foreground mt-0.5" />
                          <span className="text-muted-foreground">{event.notes}</span>
                        </div>
                      )}

                      {/* GPS coordinates for checkpoints */}
                      {event.type === 'checkpoint' && event.data && 'latitude' in event.data && event.data.latitude && event.data.longitude && (
                        <div className="mt-2 text-xs text-muted-foreground font-mono">
                          GPS: {event.data.latitude.toFixed(6)}, {event.data.longitude.toFixed(6)}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Summary */}
      <Card className="bg-muted/30">
        <CardContent className="p-4">
          <h4 className="font-light mb-2">Timeline Summary</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-muted-foreground">Total Events:</span>
              <span className="ml-2 font-light">{timelineEvents.length}</span>
            </div>
            <div>
              <span className="text-muted-foreground">Status Updates:</span>
              <span className="ml-2 font-light">{statusUpdates.length}</span>
            </div>
            <div>
              <span className="text-muted-foreground">GPS Checkpoints:</span>
              <span className="ml-2 font-light">{checkpoints.length}</span>
            </div>
            <div>
              <span className="text-muted-foreground">Current Status:</span>
              <span className="ml-2 font-light">{getStatusLabel(shipment.currentStatus)}</span>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

